    "contracts/prize-pots/player-pot/v3/PixelSweeperPlayerPotV3CrossChainSenderKreepyKritters.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

import {Client} from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";
import {IRouterClient} from "@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";

import {PixelSweeperPlayerPotV3CrossChainSender, IRareboardSweeper, Address} from "./PixelSweeperPlayerPotV3CrossChainSender.sol";
import {KreepyKrittersPortalEncoder, BytesLib} from "../../../ccip/KreepyKrittersPortalEncoder.sol";

interface IKreepyKrittersPortal {
    function getTransferFee(
        uint64 destinationChainSelector,
        bytes memory data,
        bytes calldata extraArgs
    ) external view returns (uint256 fee);

    function encodeTransfer(
        address sender,
        address recipient,
        uint256[] memory tokenIds
    ) external pure returns (bytes memory data);

    function transfer(
        uint64 destinationChainSelector,
        bytes memory data,
        bytes calldata extraArgs
    ) external payable;

    function token() external view returns (address);
}

contract PixelSweeperPlayerPotV3CrossChainSenderKreepyKritters is
    PixelSweeperPlayerPotV3CrossChainSender,
    KreepyKrittersPortalEncoder
{
    using BytesLib for bytes;

    error OnlyOperator();
    error UnsufficientFunds(uint256 fee, uint256 balance);
    error InvalidRecipient();

    event TransferFundsDeposited(address sender, uint256 value);

    IKreepyKrittersPortal public immutable portal;

    bytes public transferPrizeExtraArgs;
    mapping(address => bool) public isOperator;

    modifier onlyOperator() {
        if (!isOperator[msg.sender]) revert OnlyOperator();
        _;
    }

    constructor(
        address _owner,
        IRareboardSweeper _sweeper,
        uint256 _ticketPrice,
        FundConfig memory _fundConfig,
        address _router,
        address _crossChainTarget,
        uint64 _crossChainSelector,
        IKreepyKrittersPortal _portal
    )
        PixelSweeperPlayerPotV3CrossChainSender(
            _owner,
            _sweeper,
            _portal.token(),
            _ticketPrice,
            _fundConfig,
            _router,
            _crossChainTarget,
            _crossChainSelector
        )
    {
        portal = _portal;
        IERC721(collection).setApprovalForAll(address(_portal), true);
    }

    /* -----------------------*/
    /* --- User interface --- */
    /* -----------------------*/

    function depositTransferFunds() external payable {
        emit TransferFundsDeposited(msg.sender, msg.value);
    }

    /* ---------------------------*/
    /* --- Executor interface --- */
    /* ---------------------------*/

    function transferPrize(
        bytes calldata data,
        bytes calldata extraData
    ) external payable onlyOperator {
        address recipient = data.readAddress(OFFSET_UNLOCK_RECIPIENT);
        if (recipient != crossChainTarget) revert InvalidRecipient();

        (uint256 fee, bool success) = _transferPrize(data, extraData);
        if (!success || fee > msg.value)
            revert UnsufficientFunds(fee, msg.value);

        if (msg.value > fee) {
            Address.sendValue(payable(msg.sender), msg.value - fee);
        }
    }

    /* ------------------------*/
    /* --- Owner interface --- */
    /* ------------------------*/

    /**
     * @notice Configure parameters for the prize transfer.
     * Only callable by owner.
     * @dev If set to empty array, prizes are not transfered automatically and `transferPrize` needs to be called.
     * @param _transferPrizeExtraArgs CCIP extraArgs used to send the burn notification.
     */
    function setPrizeTransferExtraArgs(
        bytes calldata _transferPrizeExtraArgs
    ) external onlyOwner {
        transferPrizeExtraArgs = _transferPrizeExtraArgs;
    }

    /**
     * @notice Set operator role.
     * @param _operator address
     * @param _isOperator new role
     */
    function setOperator(
        address _operator,
        bool _isOperator
    ) external onlyOwner {
        isOperator[_operator] = _isOperator;
    }

    function _addPrize(
        uint256 tokenId,
        uint256 price
    ) internal virtual override {
        super._addPrize(tokenId, price);

        bytes memory extraData = transferPrizeExtraArgs;
        if (extraData.length > 0) {
            uint256[] memory tokenIds = new uint256[](1);
            tokenIds[0] = tokenId;
            bytes memory data = _encodeTransfer(
                address(this),
                crossChainTarget,
                tokenIds
            );
            _transferPrize(data, extraData);
        }
    }

    function _transferPrize(
        bytes memory data,
        bytes memory extraData
    ) internal returns (uint256, bool) {
        uint256 fee = portal.getTransferFee(
            crossChainSelector,
            data,
            extraData
        );

        if (address(this).balance >= fee) {
            portal.transfer{value: fee}(crossChainSelector, data, extraData);
            return (fee, true);
        }
        return (fee, false);
    }
}
"
    },
    "contracts/prize-pots/player-pot/v3/PriceConversion.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

import {AggregatorV3Interface} from "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract PriceConversion {
    error ZeroAddressPriceOracle();

    struct PriceFeed {
        address feed;
        uint8 decimals;
    }

    event PriceFeedUpdated(address feed, uint8 decimals);

    uint256 public constant usdPriceDecimals = 8;
    PriceFeed public ethUsdFeed;

    function usdFromNative(uint256 value) external view returns (uint256) {
        return _usdFromNative(value);
    }

    function nativeFromUsd(uint256 value) external view returns (uint256) {
        return _nativeFromUsd(value);
    }

    function _setEthUsdFeed(address feed) internal {
        if (feed == address(0)) revert ZeroAddressPriceOracle();
        uint8 decimals = AggregatorV3Interface(feed).decimals();
        ethUsdFeed = PriceFeed({feed: feed, decimals: decimals});
        emit PriceFeedUpdated(feed, decimals);
    }

    function _usdFromNative(uint256 value) internal view returns (uint256) {
        PriceFeed memory _ethUsdFeed = ethUsdFeed;
        (address feed, uint8 decimals) = (
            _ethUsdFeed.feed,
            _ethUsdFeed.decimals
        );
        (, int256 price, , , ) = AggregatorV3Interface(feed).latestRoundData();
        return
            _scaled(
                value * uint256(price),
                uint256(decimals),
                usdPriceDecimals
            );
    }

    function _nativeFromUsd(uint256 value) internal view returns (uint256) {
        PriceFeed memory _ethUsdFeed = ethUsdFeed;
        (address feed, uint8 decimals) = (
            _ethUsdFeed.feed,
            _ethUsdFeed.decimals
        );
        (, int256 price, , , ) = AggregatorV3Interface(feed).latestRoundData();
        return
            _scaled(
                value / uint256(price),
                usdPriceDecimals,
                uint256(decimals)
            );
    }

    function _scaled(
        uint256 value,
        uint256 fromDecimals,
        uint256 toDecimals
    ) private pure returns (uint256) {
        return
            fromDecimals == toDecimals
                ? value
                : (
                    fromDecimals > toDecimals
                        ? value / (10 ** uint256(fromDecimals - toDecimals))
                        : value * (10 ** uint256(toDecimals - fromDecimals))
                );
    }
}
"
