interface IFailSafeOrchestrator {
    function authzCheck(
        bytes32 root,
        address caller,
        bytes32[] memory proof
    ) external pure returns (bool authorized);

    function blockSkewDelta() external pure returns (uint);
    function gasToken() external view returns (address);

