      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IUniswapV2Router {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);    
    function WPLS() external pure returns (address);    
    function addLiquidity(address tokenA, address tokenB, uint amountADesired, uint amountBDesired, uint amountAMin, uint amountBMin, address to, uint deadline) external returns (uint amountA, uint amountB, uint liquidity);
    function addLiquidityETH(address token, uint amountTokenDesired, uint amountTokenMin, uint amountETHMin, address to, uint deadline) external payable returns (uint amountToken, uint amountETH, uint liquidity);    
    function removeLiquidity(address tokenA, address tokenB, uint liquidity, uint amountAMin, uint amountBMin, address to, uint deadline) external returns (uint amountA, uint amountB);
    function swapExactTokensForTokens(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline) external returns (uint[] memory amounts);
    function swapExactTokensForTokensSupportingFeeOnTransferTokens(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline) external;
    function swapTokensForExactTokens(uint amountOut, uint amountInMax, address[] calldata path, address to, uint deadline) external returns (uint[] memory amounts);
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);
    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) external pure returns (uint amountIn);
}

interface IUniswapV2Factory {
    function getPair(address token0, address token1) external view returns (address);
    function createPair(address token0, address token1) external returns (address pair);
}

interface IUniswapV2Pair is IERC20 {
    function factory() external view returns (address);
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function price0CumulativeLast() external view returns (uint);
    function price1CumulativeLast() external view returns (uint);
    function kLast() external view returns (uint);
}

library UniswapV2 {
    function addLiquidity(address _router, address _tokenA, address _tokenB, uint _amountA, uint _amountB) external returns(uint, uint, uint) {
        SafeERC20.safeTransferFrom(IERC20(_tokenA), msg.sender, address(this), _amountA);
        SafeERC20.safeTransferFrom(IERC20(_tokenB), msg.sender, address(this), _amountB);

        SafeERC20.safeApprove(IERC20(_tokenA), _router, _amountA);
        SafeERC20.safeApprove(IERC20(_tokenB), _router, _amountB);

        return IUniswapV2Router(_router).addLiquidity(_tokenA, _tokenB, _amountA, _amountB, 1, 1, address(this), block.timestamp);
    }

    function swap(address _router, address _tokenIn, address _tokenOut, uint _amountIn, address _to, uint _minTokenOut) internal returns (uint) {
        IERC20(_tokenIn).approve(_router, _amountIn);

        uint[] memory _amounts;
        address[] memory _swapPath = getSwapPath(_router, _tokenIn, _tokenOut);

        _amounts = IUniswapV2Router(_router).swapExactTokensForTokens(
            _amountIn, 
            _minTokenOut, 
            _swapPath, 
            _to, 
            block.timestamp
        );
        require(_amounts.length > 0, "invalid token amount returned");

        return _amounts[1];
    }

    function getSwapPath(address _router, address _tokenIn, address _tokenOut) internal view returns(address[] memory _path) {
        address _weth;
        if (block.chainid == 369) _weth = IUniswapV2Router(_router).WPLS();
        else _weth = IUniswapV2Router(_router).WETH();

        if (_tokenIn == _weth || _tokenOut == _weth) {
            _path = new address[](2);
            _path[0] = _tokenIn;
            _path[1] = _tokenOut;
        } else {
            _path = new address[](3);
            _path[0] = _tokenIn;
            _path[1] = _weth;
            _path[2] = _tokenOut;
        }
    }
}"
