    "contracts/SoulPresale.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./libraries/UniswapV2.sol";
import "./interfaces/IWETH.sol";
import "./interfaces/ISoulToken.sol";
import "./interfaces/ISoulAccountant.sol";
import "./interfaces/ISoulStake.sol";

/*
 * The Soul Collection
 * https://thesoulcol.com
 * Twitter @TheSoulCol
 */
contract SoulPresale is ReentrancyGuard {
    address private constant DEAD_WALLET = 0x000000000000000000000000000000000000dEaD;
    uint private constant MAX_APPROVE = 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;

    ISoulAccountant  private immutable ACCOUNTANT;
    IUniswapV2Router private immutable ROUTER;
    IUniswapV2Pair   private immutable SOULPOOL;
    IWETH            private immutable WETH;
    IERC20           private immutable STABLE;
    ISoulToken       private immutable TSOUL;
    ISoulStake       private immutable SOULSTAKE;

    // times in UTC
    uint private constant PRESALE_LEVEL_1 = 1698534000; // October 28, 2023 23:00:00 (0.0600)
    uint private constant PRESALE_LEVEL_2 = 1698602400; // October 29, 2023 18:00:00 (0.0660)
    uint private constant PRESALE_LEVEL_3 = 1698681600; // October 30, 2023 16:00:00 (0.0666)
    uint private constant PRESALE_LEVEL_4 = 1698760800; // October 31, 2023 14:00:00 (0.0777)
    uint private constant PRESALE_ENDED   = 1698796799; // October 31, 2023 23:59:59 (launch)

    // // times in UTC
    // //test
    // // 2023-10-28 00:00:01
    // // 2023-10-28 01:00:00
    // // 2023-10-28 02:00:00
    // // 2023-10-28 03:00:00
    // // 2023-10-28 04:00:00
    // uint private constant PRESALE_LEVEL_1 = 1698451201;
    // uint private constant PRESALE_LEVEL_2 = 1698454800;
    // uint private constant PRESALE_LEVEL_3 = 1698458400;
    // uint private constant PRESALE_LEVEL_4 = 1698462000;
    // uint private constant PRESALE_ENDED   = 1698523200; // Sat Oct 28 2023 17:45:00 GMT+0200 (Central European Summer Time)

    uint public PRESALE_REMAINING;
    uint public LIQUIDITY_AMOUNT;
    bool public COMPLETED = false;

    constructor(ISoulToken _tsoul, ISoulStake _stakesoul) {
        require(address(_tsoul) != address(0), "invalid tsoul address");

        TSOUL       = _tsoul;
        ACCOUNTANT  = ISoulAccountant(_tsoul.ACCOUNTANT());
        SOULPOOL    = IUniswapV2Pair(_tsoul.SOULPOOL());
        require(address(ACCOUNTANT) != address(0), "invalid accountant address");
        require(address(SOULPOOL)   != address(0), "invalid tsoul liquidity pool address");

        STABLE = IERC20(ACCOUNTANT.STABLE());
        WETH   = IWETH(ACCOUNTANT.WETH());
        ROUTER = IUniswapV2Router(ACCOUNTANT.ROUTER());
        SOULSTAKE = _stakesoul;
        require(address(WETH)   != address(0), "invalid weth address");
        require(address(ROUTER) != address(0), "invalid router address");
    }

    function init(uint _presaleAmount, uint _liquidityAmount) external nonReentrant {
        require(msg.sender == address(TSOUL), "only token contract can init presale");
        require(TSOUL.balanceOf(address(this)) == _presaleAmount + _liquidityAmount, "token amount mismatch");

        PRESALE_REMAINING = _presaleAmount;
        LIQUIDITY_AMOUNT  = _liquidityAmount;
    }

    function buy(address _token, uint _amount) external payable nonReentrant {
        uint _cost = getCurrentCost(); // 1 ether means not started, 0 ether means finished
        require(_cost != 1 ether, "presale not started");
        require(_cost != 0 ether, "presale ended");

        address _me = address(this);
        uint _stableStartAmount = STABLE.balanceOf(_me);

        if(_token == address(0)) {
            IWETH(WETH).deposit{value: msg.value}();
            UniswapV2.swap(address(ROUTER), address(WETH), address(STABLE), _amount, _me, 0);
        } else if(_token != address(STABLE)) {
            SafeERC20.safeTransferFrom(IERC20(_token), msg.sender, _me, _amount);
            UniswapV2.swap(address(ROUTER), _token, address(STABLE), _amount, _me, 0);
        } else {
            SafeERC20.safeTransferFrom(STABLE, msg.sender, _me, _amount);
        }

        // calculate amount of STABLE sent this transaction
        uint _stableAmount = STABLE.balanceOf(_me) - _stableStartAmount;

        uint _tsoulAmount = (_stableAmount * 1e18) / getCurrentCost();
        require(_tsoulAmount <= PRESALE_REMAINING, "presale max reached");

        // send 50% to accountant, save 50% for liquidity
        SafeERC20.safeTransfer(STABLE, address(ACCOUNTANT), _stableAmount * 50 / 100);

        // send TSOUL tokens to buyer
        SafeERC20.safeTransfer(TSOUL, msg.sender, _tsoulAmount);
    }

    function complete() external nonReentrant {
        require(block.timestamp > PRESALE_ENDED, "presale not ended");
        require(COMPLETED == false, "presale already completed");
        COMPLETED = true; // allows liquidity to be created

        address _me = address(this);

        uint _amountSTABLE = STABLE.balanceOf(_me);
        // convert STABLE to WETH for liquidity
        UniswapV2.swap(address(ROUTER), address(STABLE), address(WETH), _amountSTABLE, _me, 0);

        // @note Assumes 18 decs
        uint _padding = 1e8;
        address _factoryAddr = ROUTER.factory();
        address _stablePair = IUniswapV2Factory(_factoryAddr).getPair(address(STABLE), address(WETH));
        (uint256 _reserve0, uint256 _reserve1,) = IUniswapV2Pair(_stablePair).getReserves();
        address _token0 = IUniswapV2Pair(_stablePair).token0();

        uint _paddedWethPrice = (_token0 == address(STABLE)) ? (_reserve0 * _padding) / _reserve1 : (_reserve1 * _padding) / _reserve0;
        // @note Still padded by "_padding" to account for low value WETHs like PLS
        uint _amountWETH = WETH.balanceOf(_me);
        uint _dollarValueSwapped = _paddedWethPrice * _amountWETH;

        // @note Now finally we can remove the padding JIT
        uint _amountTSOUL = ((_dollarValueSwapped * 1e18) / 0.0777 ether) / _padding;

        // @note We said that the WETH here shold already be 1/2 the collected WETH, L#86
        // @note We can assume that the amount of WETH is equivalent to the amount of STABLE we had. It will only be off by slippage (massive pool, very low slippage) and fees (always <1%)

        // @note accounts for $values of weth as low as 0.00000001$, assumes both 18 decs
        // @note 0 indexed and the last one has a 0 value, so -2 instead of -1
        // @note this will revert when adding liquidity if we sold LESS than 0.154$, but it is slightly more accurate, assumes 18 decs
        // @note Must be approved to take the tokens
        WETH.approve(address(ROUTER), MAX_APPROVE);
        TSOUL.approve(address(ROUTER), MAX_APPROVE);

        ROUTER.addLiquidity(
            address(WETH),
            address(TSOUL),
            _amountWETH,
            _amountTSOUL,
            0,
            0,
            address(this),
            block.timestamp
        );

        // we need to stake a tiny tiny amount to SoulStake
        // we will then get stSOUL tokens which we
        // need to send to the SoulAccountant address
        // so that the initial trading/purchase/etc rewards
        // do not go to the SoulStake contract without
        // any stakers, otherwise the rewards get lost

        SOULPOOL.approve(address(SOULSTAKE), MAX_APPROVE);
        SOULSTAKE.deposit(1 ether);
        SafeERC20.safeTransfer(SOULSTAKE, address(ACCOUNTANT), SOULSTAKE.balanceOf(address(this)));

        // burn liquidity
        SafeERC20.safeTransfer(SOULPOOL, DEAD_WALLET, SOULPOOL.balanceOf(_me));

        // burn any remaining TSOUL
        if(TSOUL.balanceOf(address(this)) > 0) {
            SafeERC20.safeTransfer(TSOUL, DEAD_WALLET, TSOUL.balanceOf(_me));
        }

        // send any dust to accountant
        if(WETH.balanceOf(address(this)) > 0) {
            SafeERC20.safeTransfer(WETH, address(ACCOUNTANT), WETH.balanceOf(_me));
        }
    }

    function getCurrentCost() public view returns (uint _cost) {
        if     (block.timestamp < PRESALE_LEVEL_1) _cost = 1.0000 ether;
        else if(block.timestamp < PRESALE_LEVEL_2) _cost = 0.0600 ether;
        else if(block.timestamp < PRESALE_LEVEL_3) _cost = 0.0660 ether;
        else if(block.timestamp < PRESALE_LEVEL_4) _cost = 0.0666 ether;
        else if(block.timestamp < PRESALE_ENDED)   _cost = 0.0777 ether;
        else return 0 ether;
    }

    function getPresaleLevels() public pure returns (uint[5] memory _start, uint[5] memory _cost) {
        _start[0] = PRESALE_LEVEL_1;
        _cost [0] = 0.0600 ether;
        _start[1] = PRESALE_LEVEL_2;
        _cost [1] = 0.0660 ether;
        _start[2] = PRESALE_LEVEL_3;
        _cost [2] = 0.0666 ether;
        _start[3] = PRESALE_LEVEL_4;
        _cost [3] = 0.0777 ether;
        _start[4] = PRESALE_ENDED;
        _cost [4] = 0 ether;
    }

    receive() external payable {}
    fallback() external payable {}
}"
    }
