pragma solidity 0.8.16;



 
 
interface IFactory{
        function createPair(address tokenA, address tokenB) external returns (address pair);
}
 
interface IRouter {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
 
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline) external;
}
 
contract Maska is ERC20, Ownable{
    using Address for address payable;
 
    IRouter public router;
    address public pair;
 
    bool private swapping;
    bool public swapEnabled;
    bool public lpFilled;
 
    uint256 public swapThreshold = 50_000 * 10e18;
 
    address public marketingWallet;
    address public devFunds;
 
    struct Taxes {
        uint256 marketing;
        uint256 dev;
        uint256 liquidity; 
    }
 
    Taxes public buyTaxes = Taxes(1,1,1);
    Taxes public sellTaxes = Taxes(1,1,1);
    uint256 totalBuyTax = 3;
    uint256 totalSellTax = 3;
 
    mapping (address => bool) public excludedFromFees;
 
    modifier inSwap() {
        if (!swapping) {
            swapping = true;
            _;
            swapping = false;
        }
    }
 
    constructor() ERC20("Maska", "MSK") {
        _mint(msg.sender, 1e9 * 10 ** decimals());
        excludedFromFees[msg.sender] = true;
 
        IRouter _router = IRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        address _pair = IFactory(_router.factory())
            .createPair(address(this), _router.WETH());
 
        router = _router;
        pair = _pair;
        excludedFromFees[address(this)] = true;
        excludedFromFees[marketingWallet] = true;
        excludedFromFees[devFunds] = true;
    }
 
    function decimals() public pure override returns(uint8){
        return 9;
    }
 
    function _transfer(address sender, address recipient, uint256 amount) internal override {
        require(amount > 0, "Transfer amount must be greater than zero");
        require(lpFilled || excludedFromFees[sender] || excludedFromFees[recipient], "Trading disabled");
        uint256 fee;
 
        //set fee to zero if fees in contract are handled or exempted
        if (swapping || excludedFromFees[sender] || excludedFromFees[recipient]) fee = 0;
 
        else if(recipient == pair) fee = amount * totalSellTax / 100;
        else if(sender == pair){
            fee = amount * totalBuyTax / 100;
        }
 
 
        //send fees if threshold has been reached
        //don't do this on buys, breaks swap
        if (swapEnabled && !swapping && sender != pair && fee > 0) swapForFees();
 
        super._transfer(sender, recipient, amount - fee);
        if(fee > 0) super._transfer(sender, address(this) ,fee);
 
    }
 
    function swapForFees() private inSwap {
        uint256 contractBalance = balanceOf(address(this));
        if (contractBalance >= swapThreshold) {
 
            // Split the contract balance into halves
            uint256 denominator = totalSellTax * 2;
            uint256 tokensToAddLiquidityWith = contractBalance * sellTaxes.liquidity / denominator;
            uint256 toSwap = contractBalance - tokensToAddLiquidityWith;
 
            uint256 initialBalance = address(this).balance;
 
            swapTokensForBnb(toSwap);
 
            uint256 deltaBalance = address(this).balance - initialBalance;
            uint256 unitBalance= deltaBalance / (denominator - sellTaxes.liquidity);
            uint256 bnbToAddLiquidityWith = unitBalance * sellTaxes.liquidity;
 
            if(bnbToAddLiquidityWith > 0){
                // Add liquidity to pancake
                addLiquidity(tokensToAddLiquidityWith, bnbToAddLiquidityWith);
            }
 
            uint256 marketingAmt = unitBalance * 2 * sellTaxes.marketing;
            if(marketingAmt > 0){
                payable(marketingWallet).sendValue(marketingAmt);
            }
 
            uint256 devAmt = unitBalance * 2 * sellTaxes.dev;
            if(devAmt > 0){
                payable(devFunds).sendValue(devAmt);
            }
 
        }
    }
 
 
    function swapTokensForBnb(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();
 
        _approve(address(this), address(router), tokenAmount);
 
        // make the swap
        router.swapExactTokensForETHSupportingFeeOnTransferTokens(tokenAmount, 0, path, address(this), block.timestamp);
 
    }
 
    function addLiquidity(uint256 tokenAmount, uint256 bnbAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(router), tokenAmount);
 
        // add the liquidity
        router.addLiquidityETH{value: bnbAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            owner(),
            block.timestamp
        );
    }
 
    function setSwapEnabled(bool state) external onlyOwner {
        swapEnabled = state;
    }
 
    function setSwapThreshold(uint256 new_amount) external onlyOwner {
        swapThreshold = new_amount * 10**decimals();
    }
 
    function setBuyTaxes(uint256 _marketing, uint256 _dev, uint256 _liquidity) external onlyOwner{
        require(
            _marketing + _dev + _liquidity <= 10,
            "Total buy fee is over 10%"
        );
        buyTaxes = Taxes(_marketing, _dev, _liquidity);
        totalBuyTax = _marketing + _dev + _liquidity;
    }
 
    function setSellTaxes(uint256 _marketing, uint256 _dev, uint256 _liquidity) external onlyOwner{
        require(
            _marketing + _dev + _liquidity <= 10,
            "Total sell fee is over 10%"
        );
        sellTaxes = Taxes(_marketing, _dev, _liquidity);
        totalSellTax = _marketing + _dev + _liquidity;
    }
 
 
    function updateMarketingWallet(address newWallet) external onlyOwner{
        require(newWallet != address(0), "Marketing wallet cannot be zero");
        marketingWallet = newWallet;
    }
 
    function updateDevFunds(address newWallet) external onlyOwner{
        require(newWallet != address(0), "Dev wallet cannot be zero");
        devFunds = newWallet;
    }
 
 
    function updateRouterAndPair(IRouter _router, address _pair) external onlyOwner{
        require(address(_router) != address(0), "Router cannot be zero");
        require(pair != address(0), "Pair cannot be zero");
        router = _router;
        pair = _pair;
    }
 
    function updateExcludedFromFees(address _address, bool state) external onlyOwner {
        excludedFromFees[_address] = state;
    }
 
    function confirmLpFilled() external onlyOwner{
        lpFilled = true;
        swapEnabled = true;
    }
 
    function rescueBEP20(address tokenAddress, uint256 amount) external onlyOwner{
        require(tokenAddress != address(this), "Can't take self token");
        IERC20(tokenAddress).transfer(owner(), amount);
    }
 
    function rescueBNB(uint256 weiAmount) external onlyOwner{
        payable(owner()).sendValue(weiAmount);
    }
 
    // fallbacks
    receive() external payable {}
 
}
