contract AirDrop is Ownable {
    IERC20 public tokenERC20;
    IBEP20 public tokenBEP20;
    bool public status = false;
    uint256 public amount = 8000000 ether;

    event DistributeAirDrop(address indexed account, address[] _listWallet);
    event ForwardFunds(address indexed account, address _to);
    event SetStatus(address indexed account, bool _status);
    event SetTokenERC20(address indexed account, address _token);
    event SetTokenBEP20(address indexed account, address _token);
    event SetAirDropAmount(address indexed account, uint256 _amount);

    function balanceTokenERC20() public view returns (uint256) {
        return tokenERC20.balanceOf(address(this));
    }

    function balanceTokenBEP20() public view returns (uint256) {
        return tokenBEP20.balanceOf(address(this));
    }

    function setStatus(bool _status) external onlyOwner {
        status = _status;
    }

    function setTokenERC20(address _token) external onlyOwner {
        tokenERC20 = IERC20(_token);

        emit SetTokenERC20(msg.sender, _token);
    }

    function setTokenBEP20(address _token) external onlyOwner {
        tokenBEP20 = IBEP20(_token);

        emit SetTokenBEP20(msg.sender, _token);
    }

    function setAirDropAmount(uint256 _amount) external onlyOwner {
        amount = _amount;

        emit SetAirDropAmount(msg.sender, _amount);
    }

    function distributeAirDrop(address[] calldata _listWallet) external onlyOwner {
        require(_listWallet.length > 0,"no wallet");
        require(amount > 0,"zero amount");
        for(uint256 i = 0; i < _listWallet.length; i++) {
            _transfer(_listWallet[i], amount);
        }

        emit DistributeAirDrop(msg.sender, _listWallet);
    }

    function _transfer(address _to, uint256 _value) internal {
        if(status) {
            tokenERC20.transfer(_to, _value);
        } else {
            tokenBEP20.transfer(_to, _value);
        }
    }

    function forwardFunds(address _to) external onlyOwner {
        if(status) {
            tokenERC20.transfer(_to, balanceTokenERC20());
        } else {
            tokenBEP20.transfer(_to, balanceTokenBEP20());
        }
        emit ForwardFunds(msg.sender, _to);
    }
}
