        uint8 dev;
        uint8 cellBurn;
        uint8 cellRewards;
        uint8 liquidity;
    }
    Taxes public transferTaxes = Taxes(0,0, 0, 0, 0);
    Taxes public buyTaxes = Taxes(2, 1, 1, 1, 1);
    Taxes public sellTaxes = Taxes(3,1, 1, 2, 3);

    struct TotFeesPaidStruct {
        uint256 marketing;
        uint256 dev;
        uint256 cellBurn;
