        uint256 liquidity;
    }
    TotFeesPaidStruct public totFeesPaid;
    enum ETransferType {
        Sell,
        Buy,
        Transfer
    }
  
    address public constant DEAD_ADDRESS =
        0x000000000000000000000000000000000000dEaD;
