    function excludeAccountRewards(address account) external onlyOwner {
        require(!_isExcluded[account], "Account is already excluded");
        if (_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeAccountRewards(address account) external onlyOwner {
        require(_isExcluded[account], "Account is already excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }


    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    function _getRate() private view returns (uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply / tSupply;
    }

    function _getCurrentSupply() private view returns (uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (
                _rOwned[_excluded[i]] > rSupply ||
                _tOwned[_excluded[i]] > tSupply
            ) return (_rTotal, _tTotal);
            rSupply = rSupply - _rOwned[_excluded[i]];
            tSupply = tSupply - _tOwned[_excluded[i]];
        }
        if (rSupply < (_rTotal / _tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override    {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
       if (from != owner() && to != owner()) 
        require(
        amount <= _maxTxAmount,
        "Transfer amount exceeds the maxTxAmount."
    );




        // is the token balance of this contract address over the min number of
        // tokens that we need to initiate a swap + liquidity lock?
        // also, don't get caught in a circular liquidity event.
        // also, don't swap & liquify if sender is uniswap pair.
      handleLiquidityAndSwaps(from);  // This is a new helper function, see below
       

        //indicates if fee should be deducted from transfer
        ETransferType transferType = ETransferType.Transfer;
        Taxes memory usedTaxes = transferTaxes;
        bool traderExcludedFromFee = false;
        if (to == uniswapV2Pair) {
            transferType = ETransferType.Sell;
            usedTaxes = sellTaxes;
            traderExcludedFromFee = _isExcludedFromFee[from];
        } else if (from == uniswapV2Pair) {
            transferType = ETransferType.Buy;
            usedTaxes = buyTaxes;
            traderExcludedFromFee = _isExcludedFromFee[to];
        }

        if (
            !tradeTaxEnabled ||
            transferType == ETransferType.Transfer ||
            traderExcludedFromFee ||
            usedTaxes.cellRewards +
                usedTaxes.marketing +
                usedTaxes.dev +
                usedTaxes.liquidity +
                usedTaxes.cellBurn ==
            0
        ) {
            taxFreeTransfer(from, to, amount);
        } else {
            //transfer amount, it will take tax, burn, liquidity fee
            _tokenTransfer(from, to, amount, usedTaxes);
        }
    }

    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        // split the contract balance into halves
        uint256 half = contractTokenBalance / 2;
        uint256 otherHalf = contractTokenBalance - half;
        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance - initialBalance;

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            DEAD_ADDRESS,
            block.timestamp
        );
    }

    /// @dev minimum rate is T_TOTAL, Make sure rSupply / tSupply >= T_TOTAL

    function taxFreeTransfer(
        address sender,
        address recipient,
        uint256 tAmount
    ) internal {
        uint256 rate = _getRate();
        _reduceBalance(sender, tAmount, rate);
        _addBalance(recipient, tAmount, rate);

        emit Transfer(sender, recipient, tAmount);
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 tAmount,
        Taxes memory usedTaxes
    ) private {
        uint256 tTransferAmount = tAmount;
        uint256 rate = _getRate();

        if (usedTaxes.liquidity != 0) {
            uint256 tLiquidity = (tAmount * usedTaxes.liquidity) / 100;
            if (tLiquidity != 0) {
                tTransferAmount -= tLiquidity;
                totFeesPaid.liquidity += tLiquidity;
                _addBalance(address(this), tLiquidity, rate);
                emit Transfer(sender, address(this), tLiquidity);
            }
        }
        

        if (usedTaxes.cellBurn != 0) {
            uint256 tBurn = (tAmount * usedTaxes.cellBurn) / 100;
            if (tBurn != 0) {
                tTransferAmount -= tBurn;
                totFeesPaid.cellBurn += tBurn;
                _addBalance(DEAD_ADDRESS, tBurn, rate);
                emit Transfer(sender, DEAD_ADDRESS, tBurn);
            }
        }

        if (usedTaxes.marketing != 0) {
            uint256 tsMarketing = (tAmount * usedTaxes.marketing) / 100;
            if (tsMarketing != 0) {
                tTransferAmount -= tsMarketing;
                totFeesPaid.marketing += tsMarketing;
                swapToMarketing += tsMarketing;
                _addBalance(address(this), tsMarketing, rate);
                emit Transfer(sender, address(this), tsMarketing);
            }
        }

         if (usedTaxes.dev != 0) {
            uint256 tsDev = (tAmount * usedTaxes.dev) / 100;
            if (tsDev != 0) {
                tTransferAmount -= tsDev;
                totFeesPaid.dev += tsDev;
                swapToDev += tsDev;
                _addBalance(address(this), tsDev, rate);
                emit Transfer(sender, address(this), tsDev);
            }
        }

        if (usedTaxes.cellRewards != 0) {
            uint256 tRfi = (tAmount * usedTaxes.cellRewards) / 100;
            if (tRfi != 0) {
                tTransferAmount -= tRfi;
                _rTotal -= tRfi * _getRate();
                totFeesPaid.cellRewards += tRfi;
            }
        }

        _reduceBalance(sender, tAmount, rate);
        if (tTransferAmount != 0) {
            _addBalance(recipient, tTransferAmount, rate);
            emit Transfer(sender, recipient, tTransferAmount);
        }
    }

   

    function swapCellForWETH() internal   {
    uint256 contractTokenBalanceToSwapForWETH = swapToMarketing;

    // Approve the Uniswap V2 Router to spend Cell tokens
    _approve(
        address(this),
        address(uniswapV2Router),
        contractTokenBalanceToSwapForWETH
    );

    swapToMarketing = 0;

    // Define the swap path (Cell -> WETH)
    address[] memory path = new address[](2);
    path[0] = address(this);             // Cell token address
    path[1] = uniswapV2Router.WETH();    // WETH address

    // Perform the token swap on Uniswap
    IUniswapV2Router02(uniswapV2Router)
        .swapExactTokensForTokensSupportingFeeOnTransferTokens(
            contractTokenBalanceToSwapForWETH,
            0,
            path,
            marketingAddress,
            block.timestamp
        );

    emit SwapCellForMarketing(contractTokenBalanceToSwapForWETH);
}


function swapCellForWETH1() internal  {
    uint256 contractTokenBalanceToSwapForWETH1 = swapToDev;

    // Approve the Uniswap V2 Router to spend Cell tokens
    _approve(
