    function _addBalance(
        address account,
        uint256 tAmount,
        uint256 rate
    ) private {
        if (_isExcluded[account]) {
            _tOwned[account] += tAmount;
        } else { 	
            _rOwned[account] += tAmount * rate;
        }
    }

    function _reduceBalance(
        address account,
        uint256 tAmount,
        uint256 rate
    ) private {
        if (_isExcluded[account]) {
            _tOwned[account] -= tAmount;
        } else {
            _rOwned[account] -= tAmount * rate;
        }
    }


    function getNumTokensSellMarketing() public view returns (uint256) {
    return numTokensSellMarketing;
    }

    function getNumTokensSellDev() public view returns (uint256) {
    return numTokensSellDev;
    }


    function handleLiquidityAndSwaps(address from) internal {
    uint256 contractTokenBalance = balanceOf(address(this)) - (swapToMarketing + swapToDev);

        if (contractTokenBalance >= _maxTxAmount) {
            contractTokenBalance = _maxTxAmount;
        }

        bool overMinTokenBalance = contractTokenBalance >=
            numTokensSellToAddToLiquidity;
        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            from != uniswapV2Pair &&
            swapAndLiquifyEnabled
        ) {
            contractTokenBalance = numTokensSellToAddToLiquidity;
            tradeTaxEnabled = false;
            //add liquidity
            swapAndLiquify(contractTokenBalance);
            tradeTaxEnabled = true;
        }
       
        uint256 contractTokenBalanceWETH1 = swapToDev;
        bool overMinTokenBalanceWETH1 = contractTokenBalanceWETH1 >=
            numTokensSellDev;
        if (
            overMinTokenBalanceWETH1 &&
            !inSwapAndLiquify &&
            from != uniswapV2Pair &&
            swapAndLiquifyEnabled
       ) {
            tradeTaxEnabled = false;
            swapCellForWETH1();
            tradeTaxEnabled = true;

    }

     uint256 contractTokenBalanceWETH = swapToMarketing;
        bool overMinTokenBalanceWETH = contractTokenBalanceWETH >=
            numTokensSellMarketing;
        if (
            overMinTokenBalanceWETH &&
            !inSwapAndLiquify &&
            from != uniswapV2Pair &&
            swapAndLiquifyEnabled
       ) {
            tradeTaxEnabled = false;
            swapCellForWETH();
            tradeTaxEnabled = true;

    }
}


