// File: contracts/test/MulTransferNFT.sol

pragma solidity ^0.6.0;




contract MulTransferNFT {

    event ERC1155Received(address operator, address from, uint256 tokenId, uint256 value, bytes data);
    event ERC1155BatchReceived(address operator, address from, uint256[] ids, uint256[] amounts, bytes data);
    event ERC721Received(address operator, address from, uint256 tokenId, bytes data);

    function transferType1155(address nft, address[] memory tos, uint256[] memory ids, uint256[] memory amounts)
        public
    {
        require(tos.length > 0, "array error");
        require(ids.length > 0, "array error");
        require(tos.length == ids.length, "array error");
        require(ids.length == amounts.length, "array error");
        for(uint256 i = 0 ; i < tos.length; i++){
            IERC1155(nft).safeTransferFrom(msg.sender, tos[i], ids[i], amounts[i], '0x0');

        }
    }

    function transferType721(IERC721 nft, address[] memory tos,  uint256[] memory ids)
        public
    {
        require(tos.length > 0, "array error");
        require(ids.length > 0, "array error");
        require(tos.length == ids.length, "array error");
        for(uint256 i = 0; i < tos.length; i++){
            nft.safeTransferFrom(msg.sender, tos[i], ids[i]);
        }
    }

    function onERC1155Received(address operator, address from, uint256 id, uint256 value, bytes calldata data) external returns (bytes4) {
        if(address(this) != operator) {
            return 0;
        }

        emit ERC1155Received(operator, from, id, value, data);
        return bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"));
    }

    function onERC721Received(address operator, address from, uint256 tokenId, bytes memory data) public returns (bytes4) {
        if(address(this) != operator) {
            return 0;
        }

        emit ERC721Received(operator, from, tokenId, data);
        return bytes4(keccak256("onERC721Received(address,address,uint256,bytes)"));
    }
}
