    uint256 public saFee1Rate=50; //2%
    uint256 public saFee2Rate=50; //2%
    uint256 public saBurnRate=100; //1%
    uint256 public saBonusRate=300; //3%

    address public uToken=0x55d398326f99059fF775485246999027B3197955;
    IUniswapV2Router02 public uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    address public LpPair=IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), uToken);
    address public lpCrCreate=0x3d407Fab039feb8fB4fe85eB95Dac946dA00a755;
    address public burnAddr =0x000000000000000000000000000000000000dEaD;
    address public feeAddr1 =0x6f7e9811682dC2245aFb935b3AD026F299517C48;
    address public feeAddr2 =0x121693d265cf6D5C17601Faf8E4DD6A68fa265a0;
    address public lpBonusAddr =0x300E11E4B4167A8E2cF9c04602658b11d7503F7D;
    

    function setSwapAtAmount(uint256 amount) public onlyOwner {
        swapAtAmount = amount;
    }

    function setWhiteList(address[] memory arr, bool state) public onlyOwner {
        for(uint i = 0; i < arr.length; i++){
             whiteList[arr[i]] = state;
        }
    }

    function setFeeAddr(address _feeAddr1,address _feeAddr2,address _lpBonusAddr) public onlyOwner {
        feeAddr1 = _feeAddr1;
        feeAddr2 = _feeAddr2;
        lpBonusAddr=_lpBonusAddr;
    }

    function setBRFeeRate(uint256 _brFee1Rate,uint256 _brFee2Rate,uint256 _brBurnRate,uint256 _brBonusRate) public onlyOwner {
        brFee1Rate = _brFee1Rate;
        brFee2Rate = _brFee2Rate;
        brBurnRate = _brBurnRate;
        brBonusRate = _brBonusRate;
    }

    function setSAFeeRate(uint256 _saFee1Rate,uint256 _saFee2Rate,uint256 _saBurnRate,uint256 _saBonusRate) public onlyOwner {
        saFee1Rate = _saFee1Rate;
        saFee2Rate = _saFee2Rate;
        saBurnRate = _saBurnRate;
        saBonusRate = _saBonusRate;
    }


}
contract L6 is ERC20, Setter {
    using SafeMath for uint256;
    event SwapAndLiquify(uint256 tokensSwapped,uint256 ethReceived,uint256 tokensIntoLiqudity);
    event Claim(address opt,address to, uint256 amount);
    
    constructor() payable ERC20("L6", "L6")  {
        uint256 totalSupply = 3000000*1e18;
        // Fee Exempt
        whiteList[owner()]=true;
        whiteList[feeAddr1] =true;
        whiteList[feeAddr2] =true;
        whiteList[address(this)] =true;
        _cast(lpCrCreate, totalSupply);
    }

    receive() external payable {}

    function _getTypeFee(address _sender, address _recipient) private view returns (string memory, bool) {
        bool takeFee = false;
        string memory tradeType = "";
        if (!Address.isContract(_sender) && !Address.isContract(_recipient)){
            tradeType="transfer";
            takeFee = false;
        } else if (_sender == LpPair && !_isRemoveLiquidity()) {
            tradeType="buy";
            takeFee=true;
        } else if (_recipient == LpPair && !_isAddLiquidity()) {
            tradeType="sale";
            takeFee=true;
        } else if (Address.isContract(_sender) && Address.isContract(_recipient)){
            tradeType="contract";
            takeFee = true;
        }
        if(whiteList[_sender] || whiteList[_recipient]) {
            takeFee = false; //excluded fee
        }
        return(tradeType,takeFee);
    }

    function _calculateSaleFee(uint256 _amount) private view returns (uint256) {
        uint256 saleFee=saFee1Rate+saFee2Rate+saBurnRate+saBonusRate;
        return _amount.mul(saleFee).div(totalRate);
    }

    function _calculateBuyFee(uint256 _amount) private view returns (uint256) {
        uint256 buyFee=brFee1Rate+brFee2Rate+brBurnRate+brBonusRate;
        return _amount.mul(buyFee).div(totalRate);
    }

    function _tokenTransfer(address _sender, address _recipient, uint256 _amount,string memory _tradeType,bool _takeFee) private {
        uint256 feeAmount=0;
        uint256 toAmount=_amount;
        if(isEqual(_tradeType,"buy") && _takeFee){
            feeAmount=_calculateBuyFee(_amount);
            if(feeAmount>0){
                _takeBuyFee(_sender, feeAmount);
            }
        }else if((isEqual(_tradeType,"sale") || isEqual(_tradeType,"contract")) && _takeFee){
            feeAmount=_calculateSaleFee(_amount);
            if(feeAmount>0){
                _takeSaleFee(_sender, feeAmount);
            }
        }
        toAmount=toAmount.sub(feeAmount);
        super._transfer(_sender, _recipient, toAmount);
    }

    function _takeBuyFee(address _sender,uint256 _feeAmount) private {
        uint256 buyTotalRate=brFee1Rate+brFee2Rate+brBurnRate+brBonusRate;
        uint256 fee1Amount= _feeAmount.mul(brFee1Rate).div(buyTotalRate);
        uint256 fee2Amount= _feeAmount.mul(brFee2Rate).div(buyTotalRate);
        uint256 burnAmount= _feeAmount.mul(brBurnRate).div(buyTotalRate);
        if (fee1Amount>0){
            super._transfer(_sender, feeAddr1, fee1Amount);
        }
        if (fee2Amount>0){
            super._transfer(_sender, feeAddr2, fee2Amount);
        }
        if (burnAmount>0){
            super._transfer(_sender, burnAddr, burnAmount);
        }
        uint256 retAmount= _feeAmount.mul(brBonusRate).div(buyTotalRate);
        if (retAmount>0){
            lpBonusAmount+=retAmount;
            super._transfer(_sender, address(this), retAmount);
        }
    }

    function _takeSaleFee(address _sender,uint256 _feeAmount) private {
        uint256 sellTotalRate=saFee1Rate+saFee2Rate+saBurnRate+saBonusRate;
        uint256 fee1Amount= _feeAmount.mul(saFee1Rate).div(sellTotalRate);
        uint256 fee2Amount= _feeAmount.mul(saFee2Rate).div(sellTotalRate);
        uint256 burnAmount= _feeAmount.mul(saBurnRate).div(sellTotalRate);
        if (fee1Amount>0){
            super._transfer(_sender, feeAddr1, fee1Amount);
        }
         if (fee2Amount>0){
            super._transfer(_sender, feeAddr2, fee2Amount);
        }
        if (burnAmount>0){
            super._transfer(_sender, burnAddr, burnAmount);
        }
        uint256 retAmount= _feeAmount.mul(saBonusRate).div(sellTotalRate);
        if (retAmount>0){
            lpBonusAmount+=retAmount;
            super._transfer(_sender, address(this), retAmount);
        }
    }

    function isEqual(string memory a, string memory b) private pure returns (bool) {
        bytes memory aa = bytes(a);
        bytes memory bb = bytes(b);
        if (aa.length != bb.length) return false;
        for(uint i = 0; i < aa.length; i ++) {
            if(aa[i] != bb[i]) return false;
        }
        return true;
    }

    function swapToken(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uToken;
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            lpBonusAddr,
            block.timestamp
        );

    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if(amount == 0) { super._transfer(from, to, 0); return;}
        (string memory tradeType,bool takeFee)=_getTypeFee(from,to);
        if (step == 0 && !isEqual(tradeType, "transfer")) {
            require(from == lpCrCreate, "ERC20: you can't add liquidity");
            step = 1; //add finish
        }

        if (from == LpPair && _isRemoveLiquidity() ) {
            takeFee=false;
        }
        if (to == LpPair && _isAddLiquidity()){
             takeFee=false;
        }
        uint256 ctBalance = balanceOf(address(this));
        bool canSwap = ctBalance >= swapAtAmount;
        if( canSwap &&
            !swapping &&
            from != LpPair &&
            from != owner() &&
            to != owner()
        ) {
            swapping = true;
            if(lpBonusAmount > 0){
                swapToken(lpBonusAmount);
                lpBonusAmount = 0;
            }
            swapping = false;
        }

        if(swapping){
            takeFee=false;
        }
        _tokenTransfer(from, to, amount,tradeType,takeFee);
    }

    function claim(IERC20 token,uint256 amount,address to) public onlyOwner
    {
        require(amount > 0, 'VGT: Can not claim 0');
        IERC20(token).transfer(to, amount);
        emit Claim(_msgSender(),to, amount);
    }

    function _isRemoveLiquidity() internal view returns (bool isRemove) {
        IUniswapV2Pair mainPair = IUniswapV2Pair(LpPair);
        (uint256 r0, uint256 r1, ) = mainPair.getReserves();

        uint256 r;
        if (uToken < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint256 bal = IERC20(uToken).balanceOf(address(mainPair));
        isRemove = r >= bal;
    }

      function _isAddLiquidity() internal view returns (bool isAdd) {
        IUniswapV2Pair mainPair = IUniswapV2Pair(LpPair);
        (uint256 r0, uint256 r1, ) = mainPair.getReserves();

        uint256 r;
        if (uToken < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint256 bal = IERC20(uToken).balanceOf(address(mainPair));
        isAdd = bal > r;
    }

}
