abstract contract Context {
    function _msgSender() internal view virtual returns (address payable) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes memory) {
        this; // silence state mutability warning without generating bytecode - see https://github.com/ethereum/solidity/issues/2691
        return msg.data;
    }
}

contract Ownable is Context {
    address private _owner;
    address private _previousOwner;
    uint256 private _lockTime;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor () internal {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }

    function geUnlockTime() public view returns (uint256) {
        return _lockTime;
    }

    //Locks the contract for owner for the amount of time provided
    function lock(uint256 time) public virtual onlyOwner {
        _previousOwner = _owner;
        _owner = address(0);
        _lockTime = now + time;
        emit OwnershipTransferred(_owner, address(0));
    }
    
    //Unlocks the contract for owner when _lockTime is exceeds
    function unlock() public virtual {
        require(_previousOwner == msg.sender, "You don't have permission to unlock");
        require(now > _lockTime , "Contract is locked until 7 days");
        emit OwnershipTransferred(_owner, _previousOwner);
        _owner = _previousOwner;
    }
}

contract WorldofWaves is Context, IERC20, Ownable {
    //LIBRARY
    using SafeMath for uint256;
    using Address for address;
    
    // INSTANCES
    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;
    
    // EVENT
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify( uint256 tokensSwapped, uint256 ethReceived, uint256 tokensIntoLiquidity);
    event SetExcludeFromReward(address caller, address account);
    event SetIncludeInReward(address caller, address account);
    event SetExcludeFromFee(address caller, address account);
    event SetIncludeInFee(address caller, address account);
    event SetTaxFeePercent(address caller, uint256 newTaxFee);
    event SetLiquidityFeePercent(address caller, uint256 newLiquidityFee);
    event SetMaxTxPercent(address caller, uint256 newMaxTxAmount);
    event SafeWithdrwaBnb(address caller, address receiver, uint256 amount);
    event SetCharityWallet(address caller, address _newCharityWallet);
    event Claim(address receiver, uint256 amount);
    event SetBNBLimit(address caller, uint256 newLimit);

    //MAPPING
    mapping (address => uint256) private _rOwned;
    mapping (address => uint256) private _tOwned;
    mapping (address => mapping (address => uint256)) private _allowances;
    mapping (address => bool) private _isExcludedFromFee;
    mapping (address => bool) private _isExcluded;
    mapping(uint256 => bnbRewardInfo) public cycleInfo; // cycleId => info
    mapping(address => user) public userInfo; // user address => cycleId
    
    address payable public charityWallet;
    address[] private _excluded;
   
    // LIMITATION ON TRANSFER   
    uint256 public _maxTxAmount = 1000000 * 10**9;
    uint256 private numTokensSellToAddToLiquidity = 1000000 * 10**9;
    
    //TOTALSUPPLY INFO
    uint256 private constant MAX = ~uint256(0);
    uint256 private constant _tTotal = 2000000000 * 10**9;
    uint256 private _rTotal = (MAX - (MAX % _tTotal));
    uint256 private _tFeeTotal;

    //TOKEN INFO
    string private constant  _name = "World of Waves";
    string private constant _symbol = "WOW";
    uint8 private constant _decimals = 9;
    
    //LIQUIDITY INFO
    uint256 public _taxFee = 165; // upto two decimal
    uint256 private _previousTaxFee = _taxFee;
    uint256 public _liquidityFee = 935;
    uint256 private _previousLiquidityFee = _liquidityFee;
    uint256 public constant MAX_FEE = 10000;
    
    //CYCLE INFO
    uint256 public liquidityCycle = 1;
    uint256 public initialliquidityCycle = 0;
    uint public _maxLoopEstimation = 10;
    uint256 public limit = 50000 * 10**9;

    // STATUS
    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = true;
    
    // CYCLE STRUCT INFO
    struct bnbRewardInfo{
        uint256 amount;
        uint256 time;
        uint256 totalRewardHolder;
    }
    
    //USER STRCUT INFO
    struct user{
        uint256 claimPoolId;
        bool eligible;
        uint256 time;
        uint lastActiveCycle;
        uint rewardInfoID;
        uint prevClaimCycle;
        uint lastClaimedCycle;
        mapping(uint => UserRewardInfo) rewardInfo;
    }
    
    //USER CYCLE REWARD INFO
    struct UserRewardInfo {
        uint _startCycle;
        uint _endCycle;
    }
    
    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }
    
    constructor ( address router) public {
        _rOwned[_msgSender()] = _rTotal;
        
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(router);
        
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        
        userInfo[owner()].eligible = true; 
        userInfo[owner()].time = block.timestamp;
        userInfo[owner()].lastActiveCycle = liquidityCycle;
        userInfo[owner()].rewardInfoID++;
        userInfo[owner()].rewardInfo[userInfo[owner()].rewardInfoID]._startCycle = userInfo[owner()].lastActiveCycle;
        cycleInfo[liquidityCycle].totalRewardHolder = cycleInfo[liquidityCycle].totalRewardHolder.add(1);
        
        emit Transfer(address(0), _msgSender(), _tTotal);
    }
    
    //to receive ETH from uniswapV2Router when swapping
    receive() external payable {}

    function name() public pure returns (string memory) { return _name; }

    function symbol() public pure returns (string memory) { return _symbol; }

    function decimals() public pure returns (uint8) { return _decimals; }

