    function isExcludedFromReward(address account) public view returns (bool) { return _isExcluded[account]; }

    function totalFees() public view returns (uint256) { return _tFeeTotal; }

    function deliver(uint256 tAmount) public {
        address sender = _msgSender();
        require(!_isExcluded[sender], "Excluded addresses cannot call this function");
        (uint256 rAmount,,,,,) = _getValues(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rTotal = _rTotal.sub(rAmount);
        _tFeeTotal = _tFeeTotal.add(tAmount);
    }

    function reflectionFromToken(uint256 tAmount, bool deductTransferFee) public view returns(uint256) {
        require(tAmount <= _tTotal, "Amount must be less than supply");
        if (!deductTransferFee) {
            (uint256 rAmount,,,,,) = _getValues(tAmount);
            return rAmount;
        } else {
            (,uint256 rTransferAmount,,,,) = _getValues(tAmount);
            return rTransferAmount;
        }
    }

    function tokenFromReflection(uint256 rAmount) public view returns(uint256) {
        require(rAmount <= _rTotal, "Amount must be less than total reflections");
        uint256 currentRate =  _getRate();
        return rAmount.div(currentRate);
    }

    function excludeFromReward(address account) public onlyOwner() {
        require(!_isExcluded[account], "Account is already excluded");
        if(_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
        emit SetExcludeFromReward(msg.sender ,account);
    }

    function includeInReward(address account) external onlyOwner() {
        require(_isExcluded[account], "Account is not excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
        emit SetIncludeInReward(msg.sender ,account);
    }
    
    // user cycle info track on every transfer
    function onEachTransfer(address from, address to ) private {
        
        if((!userInfo[from].eligible) && (balanceOf(from) > limit)){
            userInfo[from].eligible = true;
            userInfo[from].time = block.timestamp;
            cycleInfo[liquidityCycle].totalRewardHolder = cycleInfo[liquidityCycle].totalRewardHolder.add(1);
            userInfo[from].lastActiveCycle = liquidityCycle;
            userInfo[from].rewardInfoID++;
            userInfo[from].rewardInfo[userInfo[from].rewardInfoID]._startCycle = userInfo[from].lastActiveCycle;
        }
        else if((userInfo[from].eligible) && (balanceOf(from) < limit)) {
            if((userInfo[from].rewardInfo[userInfo[from].rewardInfoID]._endCycle == 0)){
                userInfo[from].rewardInfo[userInfo[from].rewardInfoID]._endCycle = liquidityCycle;
                cycleInfo[liquidityCycle].totalRewardHolder = cycleInfo[liquidityCycle].totalRewardHolder.sub(1);
            }
        }
        else if((userInfo[from].eligible) && (balanceOf(from) > limit) && (userInfo[from].rewardInfo[userInfo[from].rewardInfoID]._endCycle != 0)) {
            userInfo[from].lastActiveCycle = liquidityCycle;
            userInfo[from].rewardInfoID++;
            userInfo[from].rewardInfo[userInfo[from].rewardInfoID]._startCycle = userInfo[from].lastActiveCycle;
            cycleInfo[liquidityCycle].totalRewardHolder = cycleInfo[liquidityCycle].totalRewardHolder.add(1);
        }
        
        if((!userInfo[to].eligible) && (balanceOf(to) > limit)){
            userInfo[to].eligible = true;
            userInfo[to].time = block.timestamp;
            cycleInfo[liquidityCycle].totalRewardHolder = cycleInfo[liquidityCycle].totalRewardHolder.add(1);
            userInfo[to].lastActiveCycle = liquidityCycle;
            userInfo[to].rewardInfoID++;
            userInfo[to].rewardInfo[userInfo[to].rewardInfoID]._startCycle = userInfo[to].lastActiveCycle;
        }
        else if((userInfo[to].eligible) && (balanceOf(to) < limit)) {
            if((userInfo[to].rewardInfo[userInfo[to].rewardInfoID]._endCycle == 0)){
                userInfo[to].rewardInfo[userInfo[to].rewardInfoID]._endCycle = liquidityCycle;
                cycleInfo[liquidityCycle].totalRewardHolder = cycleInfo[liquidityCycle].totalRewardHolder.sub(1);
            }
        }
        else if((userInfo[to].eligible) && (balanceOf(to) > limit) && (userInfo[to].rewardInfo[userInfo[to].rewardInfoID]._endCycle != 0)) {
            userInfo[to].lastActiveCycle = liquidityCycle;
            userInfo[to].rewardInfoID++;
            userInfo[to].rewardInfo[userInfo[to].rewardInfoID]._startCycle = userInfo[to].lastActiveCycle;
            cycleInfo[liquidityCycle].totalRewardHolder = cycleInfo[liquidityCycle].totalRewardHolder.add(1);
        }
    }
    
    // view BNB reward of users
    function viewReward( address _user) public view returns (uint) {
        require(userInfo[_user].rewardInfoID > 0, "claim :: user is not eligible to claim reward");
        uint[6] memory _calculateRewards; // 0 - loop count , 1 - _amountOut, 2 -  prevClaimCycle, 3 - rewardInfoID, 4 - lastClaimedCycle, 5 - init
        _calculateRewards[2] = userInfo[_user].prevClaimCycle;
        _calculateRewards[3] = userInfo[_user].rewardInfoID;
        _calculateRewards[4] = userInfo[_user].lastClaimedCycle;
        
        
        if(_calculateRewards[2] < _calculateRewards[3]){
        
            for(uint i = (_calculateRewards[2] == 0) ? 1 : _calculateRewards[2]; i <= _calculateRewards[3]; i++){
                if(userInfo[_user].rewardInfo[i]._startCycle == userInfo[_user].rewardInfo[i]._endCycle) {
                    _calculateRewards[2] = i;
                     _calculateRewards[0]++;
                    if(_calculateRewards[0] < _maxLoopEstimation) 
                        continue;
                    else    
                        break;
                }
                _calculateRewards[5] = userInfo[_user].rewardInfo[i]._startCycle;
                for(uint j = userInfo[_user].rewardInfo[i]._startCycle; j< ((userInfo[_user].rewardInfo[i]._endCycle == 0) ? liquidityCycle : userInfo[_user].rewardInfo[i]._endCycle); j++){
                    _calculateRewards[1] =_calculateRewards[1].add(cycleInfo[j].amount.div(cycleInfo[j].totalRewardHolder));
                    _calculateRewards[4] = j;
                    _calculateRewards[0]++;
                    if(_calculateRewards[0] >= _maxLoopEstimation){
                        break;
                    }
                }
                
                 if(_calculateRewards[0] < _maxLoopEstimation){
                    _calculateRewards[2] = i;
                }
                else if(_calculateRewards[0] >= _maxLoopEstimation){
                    if(_calculateRewards[5] == userInfo[_user].rewardInfo[i]._startCycle){
                        _calculateRewards[2] = i;
                    }
                    break;
                }
            }
        
        }
        
        if((_calculateRewards[2] == _calculateRewards[3])
        && (_calculateRewards[4] < liquidityCycle)
        && (userInfo[_user].rewardInfo[_calculateRewards[3]]._endCycle == 0)
        && (_calculateRewards[4] != 0) && (_calculateRewards[0] < _maxLoopEstimation)){
            uint init = _calculateRewards[4].add(1);
            for(uint k = init; k< liquidityCycle; k++){
                _calculateRewards[1] = _calculateRewards[1].add(cycleInfo[k].amount.div(cycleInfo[k].totalRewardHolder));
                _calculateRewards[4] = k;
                _calculateRewards[0]++;
                if(_calculateRewards[0] > _maxLoopEstimation){
                    break;
                }
            }
        }
        return _calculateRewards[1];
    }
    
    // claim bnb reward of users
    function claim() public {
        require(userInfo[msg.sender].rewardInfoID > 0, "claim :: user is not eligible to claim reward");
        uint _loopCount = 0;
        uint _amountOut;
        uint _init;
        
        if(userInfo[msg.sender].prevClaimCycle < userInfo[msg.sender].rewardInfoID){
        
            for(uint i = (userInfo[msg.sender].prevClaimCycle == 0) ? 1 : userInfo[msg.sender].prevClaimCycle; i <= userInfo[msg.sender].rewardInfoID; i++){
                if(userInfo[msg.sender].rewardInfo[i]._startCycle == userInfo[msg.sender].rewardInfo[i]._endCycle) {
                     userInfo[msg.sender].prevClaimCycle = i;
                     _loopCount++;
                    if(_loopCount < _maxLoopEstimation) 
                        continue;
                    else    
                        break;
                }
                _init = userInfo[msg.sender].rewardInfo[i]._startCycle;
                for(uint j = userInfo[msg.sender].rewardInfo[i]._startCycle; j< ((userInfo[msg.sender].rewardInfo[i]._endCycle == 0) ? liquidityCycle : userInfo[msg.sender].rewardInfo[i]._endCycle); j++){
                    _amountOut = _amountOut.add(cycleInfo[j].amount.div(cycleInfo[j].totalRewardHolder));
                    userInfo[msg.sender].lastClaimedCycle = j;
                    _loopCount++;
                    if(_loopCount >= _maxLoopEstimation){
                        userInfo[msg.sender].rewardInfo[i]._startCycle = userInfo[msg.sender].lastClaimedCycle;
                        break;
                    }
                }
                
                if(_loopCount < _maxLoopEstimation){
                    userInfo[msg.sender].prevClaimCycle = i;
                }
                else if(_loopCount >= _maxLoopEstimation){
                    if(_init == userInfo[msg.sender].rewardInfo[i]._startCycle){
                        userInfo[msg.sender].prevClaimCycle = i;
                    }
                    break;
                }
                
            }
        
        }
        
        if((userInfo[msg.sender].prevClaimCycle == userInfo[msg.sender].rewardInfoID)
        && (userInfo[msg.sender].lastClaimedCycle.add(1) < liquidityCycle)
        && (userInfo[msg.sender].rewardInfo[userInfo[msg.sender].rewardInfoID]._endCycle == 0)
        && (userInfo[msg.sender].lastClaimedCycle != 0) && (_loopCount < _maxLoopEstimation)){
            uint init = userInfo[msg.sender].lastClaimedCycle.add(1);
            for(uint k = init; k< liquidityCycle; k++){
                _amountOut = _amountOut.add(cycleInfo[k].amount.div(cycleInfo[k].totalRewardHolder));
                userInfo[msg.sender].lastClaimedCycle = k;
                _loopCount++;
                if(_loopCount > _maxLoopEstimation){
                    break;
                }
            }
        }
        
        if(_amountOut > 0){
            msg.sender.transfer(_amountOut);
        }
    }
    
