contract FlokiPlay is ERC20, Ownable {

  mapping(address => bool) private liquidityPool;
  mapping(address => bool) private whitelistTax;
  mapping(address => bool) private excludedTax;
  mapping(address => bool) private excludedMaxBalance;
  mapping(address => bool) private excludedMaxBuy;
  mapping(address => bool) private blacklist;

  uint256 public tSupply;
  uint256 maxBalancePermille;
  uint256 maxBuyPermille;

  uint8 private sellTax;
  uint8 private buyTax;
  uint8 private transferTax;
  address private rewardsPool;

  bool private limitMode;
  bool private whitelistMode;

  event changeTax(uint8 _sellTax, uint8 _buyTax, uint8 _transferTax);
  event changeLiquidityPool(address lpAddress, bool status);
  event changeWhitelistTax(address _address, bool status);
  event changeExcludedTax(address _address, bool status);
  event changeRewardsPool(address rewardsPool);
  event changeLimitMode(bool _status);
  event changeWhitelistMode(bool _status);
  event changeMaxBalance(uint256 _maxPermille);
  event changeMaxBuy(uint256 _maxPermille);
  event changeExcludedMaxBalance(address _address, bool status);
  event changeExcludedMaxBuy(address _address, bool status);
  event changeBlacklist(address _address, bool status);

  constructor() ERC20("FlokiPlay", "FLOKIPLAY") {
  	tSupply = 10 * 10**9 * 10**18;
    maxBalancePermille = 5;
   	maxBuyPermille = 5;

    _mint(msg.sender, tSupply);

    sellTax = 30;
    buyTax = 101;
    transferTax = 0;

    limitMode = false;
    whitelistMode = true;

    excludedTax[msg.sender] = true;
    excludedMaxBalance[msg.sender] = true;
  }

  function setTaxes(uint8 _sellTax, uint8 _buyTax, uint8 _transferTax) external onlyOwner {
    sellTax = _sellTax;
    buyTax = _buyTax;
    transferTax = _transferTax;
    emit changeTax(_sellTax,_buyTax,_transferTax);
  }

  function getTaxes() external pure returns (uint8 _sellTax, uint8 _buyTax, uint8 _transferTax) {
    return (_sellTax, _buyTax, _transferTax);
  }

  function setLiquidityPool(address _lpAddress, bool _status) external onlyOwner {
    liquidityPool[_lpAddress] = _status;
    excludedMaxBalance[_lpAddress] = true;
    emit changeLiquidityPool(_lpAddress, _status);
  }

  function setWhitelist(address _address, bool _status) external onlyOwner {
  	require(!_isContract(_address), "Cannot whitelist a contract address.");
    whitelistTax[_address] = _status;
    emit changeWhitelistTax(_address, _status);
  }

  function setMultipleWhitelist(address[] memory _addresses) external onlyOwner {   
    for(uint256 i = 0; i < _addresses.length; i++) {
      if (!_isContract(_addresses[i])) {
        whitelistTax[_addresses[i]] = true;
      }
    }
  }

  function setExcludedTax(address _address, bool _status) external onlyOwner {
    excludedTax[_address] = _status;
    emit changeExcludedTax(_address, _status);
  }

  function setRewardsPool(address _rewardsPool) external onlyOwner {
    rewardsPool = _rewardsPool;
    excludedTax[_rewardsPool] = true;
    excludedMaxBalance[_rewardsPool] = true;
    emit changeRewardsPool(_rewardsPool);
  }

  function setLimitMode(bool _status) external onlyOwner {
    limitMode = _status;
    emit changeLimitMode(_status);
  }

  function setWhitelistMode(bool _status) external onlyOwner {
  	whitelistMode = _status;
    emit changeWhitelistMode(_status);
  }

  function setMaxBalance(uint256 _maxPermille) external onlyOwner {
  	maxBalancePermille = _maxPermille;
    emit changeMaxBalance(_maxPermille);
  }

  function setMaxBuy(uint256 _maxPermille) external onlyOwner {
  	maxBuyPermille = _maxPermille;
    emit changeMaxBuy(_maxPermille);
  }

  function setExcludedMaxBalance(address _address, bool _status) external onlyOwner {
    excludedMaxBalance[_address] = _status;
    emit changeExcludedMaxBalance(_address, _status);
  }

  function setExcludedMaxBuy(address _address, bool _status) external onlyOwner {
    excludedMaxBuy[_address] = _status;
    emit changeExcludedMaxBuy(_address, _status);
  }

  function setBlacklist(address _address, bool _status) external onlyOwner {
    blacklist[_address] = _status;
    emit changeBlacklist(_address, _status);
  }

  function rescueToken(address token, address to) external onlyOwner {
    IERC20(token).transfer(to, IERC20(token).balanceOf(address(this)));
  }

  function isWhitelisted(address _address) external view returns(bool) {
    return whitelistTax[_address];
  }

  function isExcludedFromTax(address _address) external view returns(bool) {
    return excludedTax[_address];
  }

  function isExcludedFromMaxBalance(address _address) external view returns(bool) {
    return excludedMaxBalance[_address];
  }

  function isExcludedFromMaxBuy(address _address) external view returns(bool) {
    return excludedMaxBuy[_address];
  }

  function isBlacklisted(address _address) external view returns(bool) {
    return blacklist[_address];
  }

  function _transfer(address sender, address receiver, uint256 amount) internal virtual override {
  	require(!blacklist[sender], "Sender is backlisted.");
  	require(!blacklist[receiver], "Receiver is backlisted.");

  	if ((liquidityPool[receiver] != true) && (receiver != rewardsPool)) {
  		require(balanceOf(receiver) + amount <= _getMaxBalance(receiver), "Max balance reached for the receiving wallet.");
  	}

    uint256 taxAmount;

    if (excludedTax[sender] || excludedTax[receiver]) {
      // Address is excluded from tax
      taxAmount = 0;
    } else if(liquidityPool[sender] == true) {
      // It's an LP Pair and it's a buy
      require(amount <= _getMaxBuy(receiver), "Max buy reached for the receiving wallet.");

      if((whitelistMode == true) && (whitelistTax[receiver] == true)) {
        taxAmount = 0;
	  } else {
	    taxAmount = (amount * buyTax) / 100;
      }
    } else if(liquidityPool[receiver] == true) {      
      // It's an LP Pair and it's a sell
      taxAmount = (amount * sellTax) / 100;
    } else {
      taxAmount = (amount * transferTax) / 100;
    }

    if(taxAmount > 0) {
      super._transfer(sender, rewardsPool, taxAmount);
    }
    
    super._transfer(sender, receiver, amount - taxAmount);
  }

  function _getMaxBalance(address _address) internal view returns (uint256) {
  	uint256 max;

  	if (!limitMode || excludedMaxBalance[_address]) {
  		max = tSupply;
  	} else {
  		max = (tSupply * maxBalancePermille) / 1000;
  	}

    return max;
  }

  function _getMaxBuy(address _address) internal view returns (uint256) {
  	uint256 max;

  	if (!limitMode || excludedMaxBuy[_address]) {
  		max = tSupply;
  	} else {
  		max = (tSupply * maxBuyPermille) / 1000;
  	}

    return max;
  }

  function _isContract(address _address) internal view returns (bool isContract) {
  	return _address.code.length > 0;
  }

}
