pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {MessagingFee, MessagingReceipt} from "@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/ILayerZeroEndpointV2.sol";
import "../../usdv/interfaces/IUSDV.sol";
import "./interfaces/IRecolorHelper.sol";

/// @notice this contract is a helper to recolor USDV on the way out
contract RecolorHelper is IRecolorHelper {
    using SafeERC20 for IERC20;

    address public immutable usdv;

    constructor(address _usdv) {
        usdv = _usdv;
    }

    /// ------ local transfer function -----

    /// @dev send USDV into this account and then ATOMICALLY transfer out with color
    function transferWithColor(address _receiver, uint256 _amount, uint32 _toColor) external override {
        _transferOutWithColor(_receiver, _amount, _toColor);
    }

    /// @dev it requires the caller to approve this contract to spend their USDV
    function approvedTransferWithColor(address _receiver, uint256 _amount, uint32 _toColor) external override {
        IERC20(usdv).safeTransferFrom(msg.sender, address(this), _amount);
        _transferOutWithColor(_receiver, _amount, _toColor);
    }

    function _transferOutWithColor(address _receiver, uint256 _amount, uint32 _toColor) internal virtual {
        // wrapping all balance to the provided color
        IUSDV(usdv).setDefaultColor(address(this), _toColor);
        // transfer the usdv out with the provided color
        IERC20(usdv).safeTransfer(_receiver, _amount);
    }

    /// ------ crosschain transfer function transfer function -----

    /// @dev send USDV into this account and then ATOMICALLY send across-chain
    function sendWithColor(
        IOFT.SendParam calldata _param,
        uint32 _toColor,
        bytes calldata _extraOptions,
        MessagingFee calldata _msgFee,
        address payable _refundAddress,
        bytes calldata _composeMsg
    ) external payable override returns (MessagingReceipt memory) {
        return _sendWithColor(_param, _toColor, _extraOptions, _msgFee, _refundAddress, _composeMsg);
    }

    /// @dev it requires the caller to approve this contract to spend their USDV
    function approvedSendWithColor(
        IOFT.SendParam calldata _param,
        uint32 _toColor,
        bytes calldata _extraOptions,
        MessagingFee calldata _msgFee,
        address payable _refundAddress,
        bytes calldata _composeMsg
    ) external payable virtual returns (MessagingReceipt memory) {
        IERC20(usdv).safeTransferFrom(msg.sender, address(this), _param.amountLD);
        return _sendWithColor(_param, _toColor, _extraOptions, _msgFee, _refundAddress, _composeMsg);
    }

    function _sendWithColor(
        IOFT.SendParam calldata _param,
        uint32 _toColor,
        bytes calldata _extraOptions,
        MessagingFee calldata _msgFee,
        address payable _refundAddress,
        bytes calldata _composeMsg
    ) internal returns (MessagingReceipt memory msgReceipt) {
        // wrapping all balance to the provided color
        IUSDV(usdv).setDefaultColor(address(this), _toColor);
        // send usdv out with the provided color
        msgReceipt = IUSDV(usdv).send{value: msg.value}(_param, _extraOptions, _msgFee, _refundAddress, _composeMsg);
    }
}
