contract BurnOne is Context,IBEP20,Ownable {
    using Address for address;

    mapping (address => uint256) private _rOwned;
    mapping (address => uint256) private _tOwned;
    bool private _initialized = false;
    mapping (address => mapping (address => uint256)) private _allowances;

    mapping (address => bool) private _blacklist;
    mapping (address => bool) private _isExcludedFromFee;

    mapping (address => bool) private _isExcluded;
    address[] private _excluded;
   
    uint256 private constant MAX = ~uint256(0);
    uint256 private constant _tTotal = 1000000000000000 * 10**9;
    uint256 private _rTotal = (MAX - (MAX % _tTotal));
    uint256 private _tFeeTotal;
    
    uint256 private constant YEAR = 31536000;
    uint256 private _startTimestamp;
    uint256 private _currentYear;

    string private constant _name = "Burn1COIN";
    string private constant _symbol ="BURN1COIN";
    uint8 private constant _decimals = 9;

    uint256 public _taxFee = 2;
    uint256 private _previousTaxFee;
    uint256 public _liquidityFee = 2;
    uint256 public _burnFee = 1;

    address[] public feeWallets;
    uint256[] public feePercents;

    bool private _sendFeesAsBNB = true;

    bool private _relockStatus;

    IPancakeswapV2Router02 public immutable pancakeswapV2Router;
    address public immutable pancakeswapV2Pair;
    
    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = true;

    uint256 private _maxTxAmount = 10000 * 10**9 * 10**9;
  
    uint256 private constant numTokensSellToAddToLiquidity = 1 * 10**8 * 10**9;

    address public constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD;

    event MinTokensBeforeSwapUpdated(uint256 minTokensBeforeSwap);
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 bnbReceived,
        uint256 tokensIntoLiquidity
    );

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor (address swapRouter) {
        _rTotal = 0;

        //swapRouter addresses:
        //    pswap testnet: 0xD99D1c33F9fC3444f8101754aBC46c52416550D1
        //    V2: 0x10ED43C718714eb63d5aA57B78B54704E256024E
        IPancakeswapV2Router02 _pancakeswapV2Router = IPancakeswapV2Router02(swapRouter);
         // Create a pancakeswap pair for this new token
        pancakeswapV2Pair = IPancakeswapV2Factory(_pancakeswapV2Router.factory())
            .createPair(address(this), _pancakeswapV2Router.WETH());
        // set the rest of the contract variables
        pancakeswapV2Router = _pancakeswapV2Router;
        
        //exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
    }

    // should be called immediately after creation, and then locked with disableSetBalances()
    function setBalances(address[] memory holders, uint256[] memory relativeBalances, uint256 totalRelativeBalances) public onlyOwner {
        require(holders.length == relativeBalances.length, "Array lengths should match.");
        require(!_initialized, "Balances cannot be overwritten after being locked in.");
        address ZERO_ADDRESS = address(0);
        _rTotal = totalRelativeBalances;
        for (uint i = 0; i < holders.length; i++) {
            _rOwned[holders[i]] = relativeBalances[i];
            emit Transfer(ZERO_ADDRESS, holders[i], 1);
        }
    }

    //setBalances cannot be reenabled
    function disableSetBalances() public onlyOwner {
        require(_rTotal > MAX / 10, "Sum of relative balances should be sufficiently close to uint256 max.");
        _initialized = true;
    }

    function name() public pure returns (string memory) {
        return _name;
    }

    function symbol()  public pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public pure override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function getOwner() external view returns (address){
        return owner();
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "BEP20: transfer exceeds allowance");
        _approve(sender, _msgSender(), currentAllowance - amount);

        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "BEP20: decreased allowance below zero");
        _approve(_msgSender(), spender, currentAllowance - subtractedValue);

        return true;
    }

    function isExcludedFromReward(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }
    
    function getRelockStatus() external view returns (bool){
        return _relockStatus;
    }


    function reflectionFromToken(uint256 tAmount, bool deductTransferFee) public view returns(uint256) {
        require(tAmount <= _tTotal, "Amount more than supply");
        uint256 currentRate = _getRate();
        if (!deductTransferFee) {
            return tAmount * currentRate;
        } else {
            uint256 tReflection = tAmount * _taxFee / 100;
            uint256 tLiquidity = tAmount * _liquidityFee / 100;
            uint256 tBurn = tAmount * _burnFee / 100;
            uint256 tTransferAmount = tAmount - tReflection - tLiquidity - tBurn;
            for (uint i = 0; i < feePercents.length; i++)
                tTransferAmount -= tAmount * feePercents[i] / 100;
            return tTransferAmount * currentRate;
        }
    }

    function tokenFromReflection(uint256 rAmount) public view returns(uint256) {
        require(rAmount <= _rTotal, "Amount more than total reflections");
        uint256 currentRate =  _getRate();
        return rAmount/currentRate;
    }

    function excludeFromReward(address account) public onlyOwner() {
        require(!_isExcluded[account], "Account is already excluded");
        if(_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeInReward(address account) external onlyOwner() {
        require(_isExcluded[account], "Account not excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    function setFeePercent(uint256 taxFee, uint256 liquidityFee, uint256 burnFee) external onlyOwner() {
        _taxFee = taxFee;
        _liquidityFee = liquidityFee;
        _burnFee = burnFee;
    }

    function setAdditionalFees(address[] calldata _feeWallets, uint256[] calldata _feePercents) public onlyOwner() {
        feeWallets = _feeWallets;
        feePercents = _feePercents;
    }

    function toggleCharitySale(bool enabled, address charity) public onlyOwner {
        uint charityFeeIndex = feeWallets.length;
        for (uint i = 0; i < feeWallets.length; i++)
            if (feeWallets[i] == charity) {
                charityFeeIndex = i;
                break;
            }
        require(charityFeeIndex != feeWallets.length, "Charity wallet has no current fee.");
        if (enabled) {
            require(_taxFee != 0, "Charity sale already enabled.");
            _previousTaxFee = _taxFee;
            _taxFee = 0;
            feePercents[charityFeeIndex] += 2;
        }
        else {
            require(_taxFee == 0, "Charity sale already disabled.");
            _taxFee = _previousTaxFee;
            feePercents[charityFeeIndex] -= 2;
        }
    }

    function setMaxTxPercent(uint256 maxTxPercent) external onlyOwner() {
        _maxTxAmount = (_tTotal*maxTxPercent)/1000;
    }

    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }

    function blacklistAccount(address account) public onlyOwner {
        _blacklist[account] = true;
    }

    function removeAccountFromBlacklist(address account) public onlyOwner {
        _blacklist[account] = false;
    }

    function sendFeesAsBNB(bool enabled) public onlyOwner {
        _sendFeesAsBNB = enabled;
    }

    function transferDustBNB() external onlyOwner() {
        address payable wallet = payable(msg.sender);
        wallet.transfer(address(this).balance);
    }
    

     //to recieve BNB from pancakeswapV2Router when swaping
    receive() external payable {}

    function _reflectFee(uint256 tFee) private {
        uint256 currentRate = _getRate();
        uint256 rFee = tFee * currentRate;
        _rTotal -= rFee;
        _tFeeTotal += tFee;
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(address sender, address recipient, uint256 amount,bool takeFee) private {
        // remove fees from the transfer, and send to wallets
        uint256 tTransferAmount = amount;
        if (takeFee) {
            // built-in fees (reflection, liquidity, burn)
            uint256 tReflection = amount * _taxFee / 100;
            uint256 tLiquidity = amount * _liquidityFee / 100;
            uint256 tBurn = amount * _burnFee / 100;
            tTransferAmount = tTransferAmount - tReflection - tLiquidity - tBurn;
            _sendFeeToWallet(tLiquidity, address(this));
            emit Transfer(sender, address(this), tLiquidity);
            _reflectFee(tReflection);
            _sendFeeToWallet(tBurn, BURN_ADDRESS);
            emit Transfer(sender, BURN_ADDRESS, tBurn);

            // variable fees (charity, marketing, community, etc.)
            for (uint i = 0; i < feeWallets.length; i++) {
                uint256 tFee = amount * feePercents[i] / 100;
                tTransferAmount -= tFee;
                if (_sendFeesAsBNB)
                    _sendFeeToWalletAsBNB(tFee, feeWallets[i]);
                else {
                    _sendFeeToWallet(tFee, feeWallets[i]);
                    emit Transfer(sender, feeWallets[i], tFee);
                }
            }
        }

        // calculate transfer amounts
        uint256 currentRate = _getRate();
        uint256 rAmount = amount * currentRate;
        uint256 rTransferAmount = tTransferAmount * currentRate;

        // transfer tokens from sender and to recipient
        _rOwned[sender] = _rOwned[sender] - rAmount;
        _rOwned[recipient] = _rOwned[recipient] + rTransferAmount;
        if (_isExcluded[sender])
            _tOwned[sender] = _tOwned[sender] - amount;
        if (_isExcluded[recipient])
            _tOwned[recipient] = _tOwned[recipient] + tTransferAmount;

        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _getRate() private view returns(uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply/tSupply;
    }

    function _getCurrentSupply() private view returns(uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;      
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return (_rTotal, _tTotal);
            rSupply -=_rOwned[_excluded[i]];
            tSupply -= _tOwned[_excluded[i]];
        }
        if (rSupply < _rTotal/_tTotal) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

    function _sendFeeToWallet(uint256 tFee, address account) private {
        uint256 currentRate =  _getRate();
        uint256 rFee = tFee * currentRate;
        _rOwned[account] = _rOwned[account] + rFee;
        if(_isExcluded[account])
            _tOwned[account] = _tOwned[account] + tFee;
    }

    function _sendFeeToWalletAsBNB(uint256 tFee, address account) private {
        _sendFeeToWallet(tFee, address(this));
        swapTokensForBnb(tFee, account);
    }

    function isExcludedFromFee(address account) public view returns(bool) {
        return _isExcludedFromFee[account];
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0) && spender != address(0), "BEP20: approve from or to the zero address");
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0) && to != address(0), "BEP20: transfer from or to the zero address");
        require(amount > 0, "Transfer must be greater than zero");
        require(amount <= _maxTxAmount, "Transfer exceeds maxTxAmount");
        require(!_blacklist[from], "Sender is blacklisted");
        require(!_blacklist[to], "Recipient is blacklisted");
        
        // don't swap & liquify if sender is pancakeswap pair.
        uint256 contractTokenBalance = balanceOf(address(this));
        
        if(contractTokenBalance >= _maxTxAmount)
        {
            contractTokenBalance = _maxTxAmount;
        }
        
        bool overMinTokenBalance = contractTokenBalance >= numTokensSellToAddToLiquidity;
        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            from != pancakeswapV2Pair &&
            swapAndLiquifyEnabled
        ) {
            contractTokenBalance = numTokensSellToAddToLiquidity;
            //add liquidity
            swapAndLiquify(contractTokenBalance);
        }
        
        //indicates if fee should be deducted from transfer
        bool takeFee = true;
        
        //if any account belongs to _isExcludedFromFee account then remove the fee
        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]){
            takeFee = false;
        }
        
        //transfer amount, it will take tax, burn, liquidity fee, plus others
        _tokenTransfer(from,to,amount,takeFee);
    }
    
    function turnRelockStatusOn(bool status) external onlyOwner() {
        _startTimestamp = block.timestamp;
        _relockStatus = status;
        
    }
    
    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        // split the contract balance into halves
        uint256 half = contractTokenBalance/2;
        uint256 otherHalf = contractTokenBalance - half;
        uint256 initialBalance = address(this).balance;

        // swap tokens for BNB
        swapTokensForBnb(half, address(this));

        uint256 newBalance = address(this).balance - initialBalance;

        // add liquidity to pancakeswap
        addLiquidity(otherHalf, newBalance);
        
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForBnb(uint256 tokenAmount, address to) private {
        // generate the pancakeswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = pancakeswapV2Router.WETH();

        _approve(address(this), address(pancakeswapV2Router), tokenAmount);

        // make the swap
        pancakeswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of BNB
            path,
            to,
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(pancakeswapV2Router), tokenAmount);

        // add the liquidity
        pancakeswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp
        );
    }
