contract WSGCollectionManagerV3 is ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    struct UserInfo {
        bool collected;
        uint timestamp;
    }

    uint[] public commonIds = [
        10061, 10062, 10063, 10064, 10065, 10066, 10067, 10068, 10069, 10070,
        10081, 10082, 10083, 10084, 10085, 10086, 10087, 10088, 10089, 10090,
        10101, 10102, 10103, 10104, 10105, 10106, 10107, 10108, 10109, 10110];

    uint[] public rareIds = [
        10071, 10072, 10073, 10074, 10075, 10076,
        10091, 10092, 10093, 10094, 10095, 10096,
        10111, 10112, 10113, 10114, 10115, 10116];

    uint[] public mythicIds = [
        10077, 10078, 10079,
        10097, 10098, 10099,
        10117, 10118, 10119];
        
    uint[] public legendaryIds = [
        10080,
        10100,
        10120];

    IERC1155Mintable public nftContract = IERC1155Mintable(0xe86E4b3bB1846a017153CedCD0458dc9Ad835D9b);

    mapping(address => UserInfo) public userInfo;

    function hasCollected(address account) public view returns (bool collected) {
        collected = userInfo[account].collected;
    }

    function hasAllCards(address account) public view returns (bool) {
        for (uint i = 0; i < commonIds.length; i++) {
            if (nftContract.balanceOf(account, commonIds[i]) < 1) return false;
        }

        for (uint i = 0; i < rareIds.length; i++) {
            if (nftContract.balanceOf(account, rareIds[i]) < 1) return false;
        }

        for (uint i = 0; i < mythicIds.length; i++) {
            if (nftContract.balanceOf(account, mythicIds[i]) < 1) return false;
        }

        for (uint i = 0; i < legendaryIds.length; i++) {
            if (nftContract.balanceOf(account, legendaryIds[i]) < 1) return false;
        }

        return true;
    }

    function collect() external notPaused nonReentrant {
        require(!hasCollected(msg.sender), 'already collected');
        require(hasAllCards(msg.sender), 'not all cards');

        for (uint i = 0; i < commonIds.length; i++) {
            nftContract.burn(msg.sender, commonIds[i], 1);
        }

        for (uint i = 0; i < rareIds.length; i++) {
            nftContract.burn(msg.sender, rareIds[i], 1);
        }

        for (uint i = 0; i < mythicIds.length; i++) {
            nftContract.burn(msg.sender, mythicIds[i], 1);
        }

        for (uint i = 0; i < legendaryIds.length; i++) {
            nftContract.burn(msg.sender, legendaryIds[i], 1);
        }

        userInfo[msg.sender] = UserInfo(true, block.timestamp);
    }

    function recover(address token) external onlyOwner {
        SafeERC20.safeTransfer(IERC20(token), owner(), IERC20(token).balanceOf(address(this)));
    }
}
