pragma solidity ^0.8.19;

contract KnightDuel {
    struct Knight {
        address addr;
        string sword;
    }

    Knight public knight1;
    Knight public knight2;
    string public duelLog;
    bool public duelStarted = false;

    function startDuel(string memory sword1, string memory sword2) public {
        require(!duelStarted, "Duel already started");
        knight1 = Knight(msg.sender, sword1);
        knight2 = Knight(tx.origin, sword2);
        duelStarted = true;
        duelLog = "The stage is set, let the Knight Duel begin!";
    }

    function makeMove(string memory move) public {
        require(duelStarted, "Duel not started yet");
        require(msg.sender == knight1.addr || msg.sender == knight2.addr, "Only a knight can make a move");
        duelLog = string(abi.encodePacked(duelLog, "\n", msg.sender == knight1.addr ? knight1.sword : knight2.sword, " made a move: ", move));
    }

    function getDuelLog() public view returns (string memory) {
        return duelLog;
    }
}"
