contract CSC_Airdrop is Claimable {

    using SafeMath for uint256;

    event Claim(address to, uint256 amount);
    address[] private beneficiaries;

    IBEP20 public token;

    uint256 public airDropAmount;

    mapping(address => bool) private hasReceivedAirdrop;
    mapping(address => bool) private isAddressAdded; 

    constructor(address _tokenAddress) {
        token = IBEP20(_tokenAddress);
        airDropAmount = 10000000000000000000000;
    }

    function SetAirdropAmount(uint256 amount) public onlyOwner {
        airDropAmount = amount;
    }

    function deposit(uint256 _amount) public onlyOwner {
        require(
            token.transferFrom(msg.sender, address(this), _amount),
            "deposit failed"
        );
    }

    function withdraw(uint256 _amount) public onlyOwner {
        require(token.transfer(msg.sender, _amount), "withdraw failed");
    }

    function airdrop() public {
        require(
            !hasReceivedAirdrop[msg.sender],
            "You have already received the airdrop."
        );
        require(token.transfer(msg.sender, airDropAmount), "airdrop failed");
        hasReceivedAirdrop[msg.sender] = true;
        _addUniqueAddress(msg.sender);
    }

    function _addUniqueAddress(address _beneficiary) internal {
        if (!isAddressAdded[_beneficiary]) {
            beneficiaries.push(_beneficiary);
            isAddressAdded[_beneficiary] = true;
        }
    }

    function hasAirdropOccurred(address _addr) public view returns (bool) {
        return hasReceivedAirdrop[_addr];
    }

     function getBeneficiaries() external view onlyOwner returns (address[] memory) {
        return beneficiaries;
    }

    receive() external payable {}

    fallback() external payable {}
}
