// SPDX-License-Identifier: MIT
pragma solidity ^0.6.8;

interface IERC20 {
    function transfer(address to, uint256 value) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}

contract PlaystationSurfTokenProxy {
    address public admin;
    address public implementation;

    event Upgraded(address indexed newImplementation);

    constructor(address _implementation) public {
        admin = msg.sender;
        implementation = _implementation;
    }

    modifier onlyAdmin() {
        require(msg.sender == admin, "Not admin");
        _;
    }

    function upgradeImplementation(address _newImplementation) external onlyAdmin {
        require(_newImplementation != address(0), "Invalid implementation address");
        require(_newImplementation != implementation, "Same implementation");

        implementation = _newImplementation;

        emit Upgraded(_newImplementation);
    }

    function recoverToken(address tokenAddress) external onlyAdmin {
        require(tokenAddress != address(0), "Invalid token address");

        uint256 tokenBalance = IERC20(tokenAddress).balanceOf(address(this));
        require(tokenBalance > 0, "No tokens to recover");

        bool success = IERC20(tokenAddress).transfer(admin, tokenBalance);
        require(success, "Token recovery failed");
    }

    receive() external payable {
        address _impl = implementation;
        require(_impl != address(0), "Invalid implementation address");

        assembly {
            let ptr := mload(0x40)
            calldatacopy(ptr, 0, calldatasize())

            let result := delegatecall(gas(), _impl, ptr, calldatasize(), 0, 0)

            let size := returndatasize()
            returndatacopy(ptr, 0, size)

            switch result
            case 0 {
                revert(ptr, size)
            }
            default {
                return(ptr, size)
            }
        }
    }
}
