  /**
   * @dev Moves tokens `amount` from `sender` to `recipient`.
   *
   * This is internal function is equivalent to {transfer}, and can be used to
   * e.g. implement automatic token fees, slashing mechanisms, etc.
   *
   * Emits a {Transfer} event.
   *
   * Requirements:
   *
   * - `sender` cannot be the zero address.
   * - `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   */
  function _transfer(address sender, address recipient, uint256 amount) internal {
    require(sender != address(0), "trnfr frm 0 addr");
    require(recipient != address(0), "trnfr to 0 addr");
    require(!blackList[sender],"you have been flagged as a bot please contact the devs");
    require(!blackList[recipient],"you have been flagged as a bot please contact the devs");
    
    uint256 to_redis;
    uint256 to_dev;
    uint256 redis_cut_rate=redis_rate;
    uint256 dev_cut_rate = dev_cut;
        if(exclude[sender] || exclude[recipient]){    
       
        _balances[sender] = _balances[sender].sub(amount);
       
        
        _balances[recipient] = _balances[recipient].add(amount);
        
        }
        else{
            require(amount<=maxTxAllowed,"amount larger than allowed");
            if(address(sender)!=address(uniswapV2Pair) && address(sender)!=address(uniswapV2Router)){
                require(amount <= maxAMTperSell, "Woops you are trying to sell more than what is allowed");
                require(block.timestamp - lastSaleTime[sender] > timeBetweenSells * 1 hours,"You need to wait before making a sale again");
                lastSaleTime[sender] = block.timestamp;
                uint256 extraTaxForEarlySale = this.checkEarlySaleTax(sender);
                takedivbeforesell(sender);
                redis_cut_rate = redis_rate;
                dev_cut_rate = dev_cut.add(extraTaxForEarlySale);
                if(amount > _balances[sender].div(5)){
                    _buyDate[sender]=block.timestamp;
                }
            }
            
            if(recipient!=address(uniswapV2Router) && recipient!=address(uniswapV2Pair) && !isHolder[recipient]){
                _buyDate[recipient] = block.timestamp;
                _lastClaim[recipient] = block.timestamp;
                isHolder[recipient] = true;
            }
            
        takedivbeforebuy(recipient);
            
        to_redis = (amount.mul(redis_cut_rate)).div(100);
        to_dev = (amount.mul(dev_cut_rate)).div(100);
        
        _balances[sender] = _balances[sender].sub(amount);
        
        if (_balances[sender]<=minAmount){
            isHolder[sender]=false;
        }
        
        amount = amount.sub(to_redis);
        
        amount = amount.sub(to_dev);
        
        totalTaxCollected=totalTaxCollected.add(to_redis);
       
        
        _balances[address(this)] = _balances[address(this)].add(to_dev);
        
        _balances[redistributionWallet] = _balances[redistributionWallet].add(to_redis);
        
        _balances[recipient] = _balances[recipient].add(amount);
        
        takeLiquidityCutfromRedis();
        
        
        }
        
       
        
    emit Transfer(sender, recipient, amount);
    
  }
  
  function takeLiquidityCutfromRedis() internal{
       uint256 tok = totalTaxCollected.sub(liqDivPaid);
       uint256 temp = amtForLiquidity.add(_balances[address(this)]).add(_balances[owner()]).add(_balances[uniswapV2Pair]).add(_balances[redistributionWallet]).add(_balances[burnAddress]);
       tok = tok.mul(temp);
       temp = tok.div(_totalSupply);
       liqDivPaid = totalTaxCollected;
       _balances[redistributionWallet] = _balances[redistributionWallet].sub(temp);
       amtForLiquidity = amtForLiquidity.add(temp);
  }
  
