      uint256 div = this.viewDividend(account);
      _interest[account]=totalTaxCollected;
      if(div>0 && this.checkEarlySaleTax(account)<1){
      
      _balances[redistributionWallet]=_balances[redistributionWallet].sub(div);
              
      _balances[account] = _balances[account].add(div);
          
