  function takeDividend(uint256 tokenIndex) external{
      require(!excludeFromRewards[msg.sender],"acc should not be excluded from rewards");
      require(totalTaxCollected.sub(_interest[msg.sender])>0,"already claimed");
      require(block.timestamp - _lastClaim[msg.sender] > claimWaitTime * 1 days,"You need to wait 3 days before claiming");
      require(!blackList[msg.sender],"you have been flagged as a bot please contact devs");
      
      uint256 div = this.viewDividend(msg.sender);
      _lastClaim[msg.sender] = block.timestamp;
      _interest[msg.sender] = totalTaxCollected;
       if(tokenIndex ==0){
            _balances[redistributionWallet] = _balances[redistributionWallet].sub(div);
            _balances[msg.sender] = _balances[msg.sender].add(div);
        }
        else{
      swapTokensForEth(div,msg.sender,tokenIndex);
        }
     
      
  }
   
   
   
    function swapTokensForEth(uint256 tokenAmount,address account, uint256 tokenIndex) private nonReentrant {
        _balances[redistributionWallet] = _balances[redistributionWallet].sub(tokenAmount);
        _balances[address(this)] = _balances[address(this)].add(tokenAmount);
        
        
       
        if(tokenIndex==1){
             address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = busd;
        path[2] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of BNB
            path,
            account,
            block.timestamp
        );
        }
        else if (tokenIndex==2){
            address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = busd;
        
        
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of TOKEN
            path,
            account,
            block.timestamp
        );
        
        }
            
        else{
            address[] memory path = new address[](4);
        path[0] = address(this);
        path[1] = busd;
        path[1] = uniswapV2Router.WETH();
        path[2] = tokensForClaim[tokenIndex];
        
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of TOKEN
            path,
            account,
            block.timestamp
        );
        }
    }
    
    function pricePerToken(uint256 amount) external view returns(uint256){
        IBEP20 USD = IBEP20(busd);
        uint256 busdinLP = USD.balanceOf(uniswapV2Pair);
        uint256 tokensInLP = balanceOf(uniswapV2Pair);
        uint256 priceInUSD = (busdinLP.mul(10**uint256(_decimals))).div(tokensInLP);
        return amount.mul(priceInUSD);
    }
    
