 function DirectBuy(uint256 amountofToken)external nonReentrant{
     require(tokensForSale>0,"No tokens for sale");
     require(amountofToken<=maxBuyinSale,"amount exceeds the amount allowed");
     require(block.timestamp-wasInSale[msg.sender]>saleWaitTime *1 hours,"You have already bought tokens in this sale");
     uint256 BUSDrequired = this.pricePerToken(amountofToken);
     IBEP20 USD = IBEP20(busd);
     USD.transferFrom(msg.sender,owner(),BUSDrequired);
     takedivbeforebuy(msg.sender);
     amountofToken = amountofToken.mul(10**uint256(_decimals));
     wasInSale[msg.sender] = block.timestamp;
     if(!isHolder[msg.sender]){
         _buyDate[msg.sender] = block.timestamp;
         _lastClaim[msg.sender] = block.timestamp;
     }
     tokensForSale = tokensForSale.sub(amountofToken);
     _balances[msg.sender] = _balances[msg.sender].add(amountofToken);
 }


