      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract BatchTransfer {

    // Function to receive Ether. msg.data must be empty
    receive() external payable {}

    fallback() external payable {}

    constructor(){
    }

    function batchOutToken(address[] memory addrs, uint256[] memory amounts, address token) external {
        require(addrs.length == amounts.length, "req len ==");
        for (uint i=0; i<addrs.length; i++) {
            IERC20(token).transferFrom(msg.sender, addrs[i], amounts[i]);
        }
    }

    function batchOutEth(address [] memory addrs, uint256[] memory amounts) external payable {
        require(addrs.length == amounts.length, "req len ==");

        uint256 amtTotal = 0;
        for (uint i=0; i<amounts.length; i++) {
            amtTotal += amounts[i];
        }
        require(amtTotal <= msg.value, "req out amts value");

        for (uint  i=0; i <addrs.length; i++) {
            bool r = payable(addrs[i]).send(amounts[i]);
            require(r, "!ok");
        }
    }
}
