abstract contract ERC721Enumerable is ERC721, IERC721Enumerable {
    // Mapping from owner to list of owned token IDs
    mapping(address => mapping(uint256 => uint256)) private _ownedTokens;

    // Mapping from token ID to index of the owner tokens list
    mapping(uint256 => uint256) private _ownedTokensIndex;

    // Array with all token ids, used for enumeration
    uint256[] private _allTokens;

    // Mapping from token id to position in the allTokens array
    mapping(uint256 => uint256) private _allTokensIndex;

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override(IERC165, ERC721) returns (bool) {
        return interfaceId == type(IERC721Enumerable).interfaceId || super.supportsInterface(interfaceId);
    }

    /**
     * @dev See {IERC721Enumerable-tokenOfOwnerByIndex}.
     */
    function tokenOfOwnerByIndex(address owner, uint256 index) public view virtual override returns (uint256) {
        require(index < ERC721.balanceOf(owner), "ERC721Enumerable: owner index out of bounds");
        return _ownedTokens[owner][index];
    }
    
    //get all _ownedTokens by owner
    function getAllTokensByOwner(address owner) public view returns (uint256[] memory) {
        uint256[] memory tokens = new uint256[](ERC721.balanceOf(owner));
        for (uint256 i = 0; i < ERC721.balanceOf(owner); i++) {
            tokens[i] = _ownedTokens[owner][i];
        }
        return tokens;
    }
    

    /**
     * @dev See {IERC721Enumerable-totalSupply}.
     */
    function totalSupply() public view virtual override returns (uint256) {
        return _allTokens.length;
    }

    /**
     * @dev See {IERC721Enumerable-tokenByIndex}.
     */
    function tokenByIndex(uint256 index) public view virtual override returns (uint256) {
        require(index < ERC721Enumerable.totalSupply(), "ERC721Enumerable: global index out of bounds");
        return _allTokens[index];
    }

    /**
     * @dev Hook that is called before any token transfer. This includes minting
     * and burning.
     *
     * Calling conditions:
     *
     * - When `from` and `to` are both non-zero, ``from``'s `tokenId` will be
     * transferred to `to`.
     * - When `from` is zero, `tokenId` will be minted for `to`.
     * - When `to` is zero, ``from``'s `tokenId` will be burned.
     * - `from` cannot be the zero address.
     * - `to` cannot be the zero address.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, tokenId);

        if (from == address(0)) {
            _addTokenToAllTokensEnumeration(tokenId);
        } else if (from != to) {
            _removeTokenFromOwnerEnumeration(from, tokenId);
        }
        if (to == address(0)) {
            _removeTokenFromAllTokensEnumeration(tokenId);
        } else if (to != from) {
            _addTokenToOwnerEnumeration(to, tokenId);
        }
    }

    /**
     * @dev Private function to add a token to this extension's ownership-tracking data structures.
     * @param to address representing the new owner of the given token ID
     * @param tokenId uint256 ID of the token to be added to the tokens list of the given address
     */
    function _addTokenToOwnerEnumeration(address to, uint256 tokenId) private {
        uint256 length = ERC721.balanceOf(to);
        _ownedTokens[to][length] = tokenId;
        _ownedTokensIndex[tokenId] = length;
    }

    /**
     * @dev Private function to add a token to this extension's token tracking data structures.
     * @param tokenId uint256 ID of the token to be added to the tokens list
     */
    function _addTokenToAllTokensEnumeration(uint256 tokenId) private {
        _allTokensIndex[tokenId] = _allTokens.length;
        _allTokens.push(tokenId);
    }

    /**
     * @dev Private function to remove a token from this extension's ownership-tracking data structures. Note that
     * while the token is not assigned a new owner, the `_ownedTokensIndex` mapping is _not_ updated: this allows for
     * gas optimizations e.g. when performing a transfer operation (avoiding double writes).
     * This has O(1) time complexity, but alters the order of the _ownedTokens array.
     * @param from address representing the previous owner of the given token ID
     * @param tokenId uint256 ID of the token to be removed from the tokens list of the given address
     */
    function _removeTokenFromOwnerEnumeration(address from, uint256 tokenId) private {
        // To prevent a gap in from's tokens array, we store the last token in the index of the token to delete, and
        // then delete the last slot (swap and pop).

        uint256 lastTokenIndex = ERC721.balanceOf(from) - 1;
        uint256 tokenIndex = _ownedTokensIndex[tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary
        if (tokenIndex != lastTokenIndex) {
            uint256 lastTokenId = _ownedTokens[from][lastTokenIndex];

            _ownedTokens[from][tokenIndex] = lastTokenId; // Move the last token to the slot of the to-delete token
            _ownedTokensIndex[lastTokenId] = tokenIndex; // Update the moved token's index
        }

        // This also deletes the contents at the last position of the array
        delete _ownedTokensIndex[tokenId];
        delete _ownedTokens[from][lastTokenIndex];
    }

    /**
     * @dev Private function to remove a token from this extension's token tracking data structures.
     * This has O(1) time complexity, but alters the order of the _allTokens array.
     * @param tokenId uint256 ID of the token to be removed from the tokens list
     */
    function _removeTokenFromAllTokensEnumeration(uint256 tokenId) private {
        // To prevent a gap in the tokens array, we store the last token in the index of the token to delete, and
        // then delete the last slot (swap and pop).

        uint256 lastTokenIndex = _allTokens.length - 1;
        uint256 tokenIndex = _allTokensIndex[tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary. However, since this oUSDTrs so
        // rarely (when the last minted token is burnt) that we still do the swap here to avoid the gas cost of adding
        // an 'if' statement (like in _removeTokenFromOwnerEnumeration)
        uint256 lastTokenId = _allTokens[lastTokenIndex];

        _allTokens[tokenIndex] = lastTokenId; // Move the last token to the slot of the to-delete token
        _allTokensIndex[lastTokenId] = tokenIndex; // Update the moved token's index

        // This also deletes the contents at the last position of the array
        delete _allTokensIndex[tokenId];
        _allTokens.pop();
    }
}

abstract contract ERC721URIStorage is ERC721 {
    using Strings for uint256;

    mapping(uint256 => uint256) public _typenft;

    mapping(uint256 => uint256) public categoryIdNft;

    uint256 public commonNftID = 0;
    uint256 public epicNftID = 0;
    uint256 public rareNftID = 0;
    uint256 public legendaryNftID = 0;
    uint256 public vipNftID = 0;

    /**
     * @dev See {IERC721Metadata-tokenURI}.
     */


    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

        string memory ty="";

        if(_typenft[tokenId]==1){
            ty="COMMON/";
        }

        else if (_typenft[tokenId]==2) {        
            ty="EPIC/";
        }
        else if (_typenft[tokenId]==3) {
            ty="RARE/";
        }
        else if (_typenft[tokenId]==4) {        
            ty="LEGENDARY/";
       } 
       
       else if (_typenft[tokenId]==5) {
            ty="VIP/";    
       }

       else {
           
           return "";
       }
        
       string memory base = string(abi.encodePacked(baseURI(),ty));
   
        // If there is a baseURI but no tokenURI, concatenate the tokenID to the baseURI.
        return string(abi.encodePacked(base, categoryIdNft[tokenId].toString()));
    }

    /**
     * @dev Sets `_tokenURI` as the tokenURI of `tokenId`.
     *
     * Requirements:
     *
     * - `tokenId` must exist.
     */
    // function _setTokenURI(uint256 tokenId, string memory _tokenURI) internal virtual {
    //     require(_exists(tokenId), "ERC721URIStorage: URI set of nonexistent token");
    //     _tokenURIs[tokenId] = _tokenURI;
    // }

      /**
     * @dev Sets `_tokenURI` as the tokenURI of `tokenId`.
     *
     * Requirements:
     *
     * - `tokenId` must exist.
     */
 

    /**
     * @dev Destroys `tokenId`.
     * The approval is cleared when the token is burned.
     *
     * Requirements:
     *
     * - `tokenId` must exist.
     *
     * Emits a {Transfer} event.
     */
    function _burn(uint256 tokenId) internal virtual override {
        super._burn(tokenId);
    }
}

abstract contract ERC721Burnable is Context, ERC721 {
    /**
     * @dev Burns `tokenId`. See {ERC721-_burn}.
     *
     * Requirements:
     *
     * - The caller must own `tokenId` or be an approved operator.
     */
    function burn(uint256 tokenId) public virtual {
        //solhint-disable-next-line max-line-length
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721Burnable: caller is not owner nor approved");
        _burn(tokenId);
    }
}
contract ERC721Morna is ERC721, ERC721Enumerable, ERC721URIStorage, ERC721Burnable, Ownable {
    using SafeMath for uint256;
    
    ERC20 public payment_token;
    address payable admin;
    uint256 internal commissionRate;
    uint256 internal royaltiesCommissionRate;
    uint256 public constant maxMint = 40;
    uint256 public price = 19990000000000000000; // 19.99 USDT 
    uint256 public minPrice=19990000000000000000; // 19.99 USDT
    uint256 public maxPrice=40000000000000000000; // 40.00 USDT

    bool public saleIsActive = false;

    mapping(uint256 => uint256) public _amountinnft;
    mapping(uint256 => bool) public _tokenClaims;
    mapping(uint256 => address) public creators;

    constructor(address payable _admin, string memory name, string memory symbol, ERC20 _payment_token, uint256 _commissionRate, uint256 _royaltiesCommissionRate) ERC721(name, symbol) {
        admin = _admin;
        payment_token = _payment_token;
        require(_commissionRate<=5, "ERC721CryptoLoteria: Commission rate has to be between 0 and 5");
        commissionRate = _commissionRate;
        royaltiesCommissionRate = _royaltiesCommissionRate;
    }

    function flipSaleState() public onlyOwner {
        saleIsActive = !saleIsActive;
    }

    function _setTypeNft(uint256 tokenId, uint256 _type) internal {
        require(_exists(tokenId), "ERC721URIStorage: URI set of nonexistent token");
        _typenft[tokenId] = _type;

    }



    function _burn(uint256 tokenId) internal virtual override(ERC721, ERC721URIStorage) {
        ERC721URIStorage._burn(tokenId);
        _amountinnft[tokenId] = 0;
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /// @notice Get how much amount of tokens a NFT has (in Payment Token)
    function getNftBalance(uint256 tokenId) public view returns (uint256 Balance) {
        return _amountinnft[tokenId];
    }

     /// @notice Change price to mint a NFT (in Payment Token)
    function changePrice(uint256 _price) public onlyOwner {
        require(_price >= minPrice && price <= maxPrice, "Price must be between range minPrice and maxPrice");
        price = _price;
    }



      function setRoyaltiesCommissionRate(uint256 _royaltiesCommissionRate) external onlyOwner {
        require(_royaltiesCommissionRate>=0 && _royaltiesCommissionRate<=5, "ERC721CryptoLoteria: Commission rate has to be between 0 and 5");
        royaltiesCommissionRate = _royaltiesCommissionRate;
    }

    function setCommissionRate(uint256 _commissionRate) external onlyOwner {
        require(_commissionRate>=0 && _commissionRate<=5, "ERC721CryptoLoteria: Commission rate has to be between 0 and 5");
        commissionRate = _commissionRate;
    }

    function getCommissionRate() external view onlyOwner returns (uint256) {
        return commissionRate;
    }

    function setBaseURI(string memory _baseURI) public onlyOwner {
        _setBaseURI(_baseURI);
    }

    function setAdmin(address payable _admin) external onlyOwner {
        admin = _admin;
    }
}
interface Lottery {
    function play(uint256[] memory _number, address wallet) external returns (bool); 
    function check() external;
    function getTicketPrice() external view returns (uint256);
}

abstract contract NFTLottery is ERC721Morna, ReentrancyGuard {
    using SafeMath for uint256;

    mapping(uint256 => mapping(uint256 => uint256)) public game;
    mapping(Lottery => bool) public lotteries;
    bool public anyContractCanPlay;

    /// @notice Play a lottery game with the NFT.
    /// @dev The NFT must has enough balance to play the lottery game
    /// @param _contract address of the lottery contract
    /// @param _tokenId tokenId of the NFT to play the lottery game
    /// @param _numbers array of numbers to play the lottery game
    /// @param wallet address of the wallet to receive the prizes
    function play(Lottery _contract, uint256 _tokenId, uint256[] memory _numbers, address wallet) external {
        require(address(_contract) != address(0), "Lottery contract cannot be 0");
        require(Address.isContract(address(_contract)), "Lottery: Contract address is not a contract");

        if (!anyContractCanPlay) {
            require(lotteries[_contract] == true, "Contract is not allowed to play");
        }

        require(ownerOf(_tokenId) == msg.sender, "You can only play your own tokens");
        require(getNftBalance(_tokenId) >= _contract.getTicketPrice().mul(_numbers.length), "ERC721Cryptoloteria: Insufficient funds");

        for (uint256 i = 0; i < _numbers.length; i++) {
            game[_tokenId][_numbers[i]] = block.timestamp;
        }

        require(payment_token.approve(address(_contract), _contract.getTicketPrice().mul(_numbers.length)), "Contract not approved");
       
        require(_contract.play(_numbers, wallet), "Play failed");
        _amountinnft[_tokenId] -= _contract.getTicketPrice().mul(_numbers.length);
        
    }

    /// @notice Set if any lottery contract can play
    /// @param _anyContractCanPlay true if any contract can play, false otherwise
    function setStatusAnyContract(bool _anyContractCanPlay) external onlyOwner {
        anyContractCanPlay = _anyContractCanPlay;
    }

    /// @notice Add a lottery to the list of contracts that can play
    /// @param _lotteryContract address of the lottery contract
    function addLottery(Lottery _lotteryContract) external onlyOwner {
        lotteries[_lotteryContract] = true;
    }
}

abstract contract BuySale is ERC721Morna, ReentrancyGuard {
    using Counters for Counters.Counter;
    using SafeMath for uint256;
    using Address for address payable;
    using Address for address;

    // -------------------- Structs --------------------
    struct Auction {
        address payable beneficiary;
        uint auctionEnd;
        address payable highestBidder;
        uint highestBid;
        bool open;
        uint256 reserve;
    }

    mapping(uint256 => uint256) public soldFor;
    mapping(uint256 => uint256) public sellBidPrice;
    mapping(uint256 => address payable) private _wallets;
    mapping(uint256 => Auction) public auctions;

    // -------------------- Events --------------------
    event Sale(uint256 indexed tokenId, address indexed from, address indexed to, uint256 value);
    event Commission(uint256 indexed tokenId, address indexed to, uint256 value, uint256 rate, uint256 total);
    event Royalty(uint256 indexed tokenId, address indexed to, uint256 value, uint256 rate, uint256 total);
    event Refund(address bidder, uint amount);
    event HighestBidIncreased(address indexed bidder, uint amount, uint256 tokenId);
    event AuctionEnded(address winner, uint amount);

    // -------------------- Auctions functions --------------------

    /// @notice Instantiate an auction contract for a tokenId
    function createAuction(uint256 tokenId, uint _closingTime, address payable _beneficiary, uint256 _reservePrice) public {

        require(sellBidPrice[tokenId]==0, "ERC721Morna: The selected NFT is open for sale, cannot be auctioned");
        require(!auctions[tokenId].open, "ERC721CryptoLoteria: The selected NFT already has an auction");
        require(ownerOf(tokenId)==msg.sender, "ERC721Morna: Only owner can auction this item");

        auctions[tokenId].beneficiary = _beneficiary;
        auctions[tokenId].auctionEnd = _closingTime;
        auctions[tokenId].reserve = _reservePrice;
        auctions[tokenId].open = true;

        // approve the Index to the current contract
        approve(address(this), tokenId);
    }

    // implement the auctionFinalize including the NFT transfer logic
    function auctionFinalize(uint256 tokenId) public nonReentrant {

        require(canFinalize(tokenId), "Cannot finalize");

        if (auctions[tokenId].highestBid>0) {

            // transfer the ownership of token to the highest bidder
            address payable _highestBidder = auctions[tokenId].highestBidder;

            // calculate payment amounts
            uint256 amount4admin = auctions[tokenId].highestBid.mul(commissionRate).div(100);
            uint256 amount4creator = auctions[tokenId].highestBid.mul(royaltiesCommissionRate).div(100);
            uint256 amount4owner = auctions[tokenId].highestBid.sub(amount4admin).sub(amount4creator);

            // to owner
            require(payment_token.transfer(auctions[tokenId].beneficiary, amount4owner), "Transfer failed.");

            // to creator
            if (amount4creator>0) {
                require(payment_token.transfer(creators[tokenId], amount4creator), "Transfer failed.");
            }

            // to admin
            if (amount4admin>0) {
                require(payment_token.transfer(admin, amount4admin), "Transfer failed.");
            }

            emit Sale(tokenId, auctions[tokenId].beneficiary, _highestBidder, auctions[tokenId].highestBid);
            emit Royalty(tokenId, auctions[tokenId].beneficiary, auctions[tokenId].highestBid, royaltiesCommissionRate, amount4creator);
            emit Commission(tokenId, auctions[tokenId].beneficiary, auctions[tokenId].highestBid, commissionRate, amount4admin);

            // transfer ownership
            address owner = ownerOf(tokenId);

            // we need to call a transferFrom from this contract, which is the one with permission to sell the NFT
            // transfer the NFT to the auction's highest bidder
            callOptionalReturn(this, abi.encodeWithSelector(this.transferFrom.selector, owner, _highestBidder, tokenId));

            soldFor[tokenId] = auctions[tokenId].highestBid;

        }

        emit AuctionEnded(auctions[tokenId].highestBidder, auctions[tokenId].highestBid);

        // finalize the auction
        delete auctions[tokenId];

    }

    // -------------------- User Bids Functions --------------------

    /// Bid on the auction with the value sent
    /// together with this transaction.
    /// The value will only be refunded if the
    /// auction is not won.
    function bid(uint256 tokenId, uint256 bid_value) public nonReentrant {        // Contracts cannot bid, because they can block the auction with a reentrant attack
        require(!msg.sender.isContract(), "No script kiddies");

        // auction has to be opened
        require(auctions[tokenId].open, "No opened auction found");

        // approve was lost
        require(getApproved(tokenId) == address(this), "Cannot complete the auction");

        // Revert the call if the bidding
        // period is over.
        require(
            block.timestamp <= auctions[tokenId].auctionEnd,
            "Auction already ended."
        );

        // If the bid is not higher, send the
        // money back.
        require(
            bid_value > auctions[tokenId].highestBid,
            "There already is a higher bid."
        );

        address owner = ownerOf(tokenId);
        require(msg.sender!=owner, "ERC721Morna: The owner cannot bid his own collectible");

        // return the funds to the previous bidder, if there is one
        if (auctions[tokenId].highestBid>0) {
            require(payment_token.transfer(auctions[tokenId].highestBidder, auctions[tokenId].highestBid), "Transfer failed.");
            emit Refund(auctions[tokenId].highestBidder, auctions[tokenId].highestBid);
        }

        // now store the bid data
        auctions[tokenId].highestBidder = payable(msg.sender);

        // transfer tokens to contract
        require(payment_token.transferFrom(msg.sender, address(this), bid_value), "Transfer failed.");

        // register the highest bid value
        auctions[tokenId].highestBid = bid_value;

        emit HighestBidIncreased(msg.sender, bid_value, tokenId);
    }

    /// Withdraw a bid when the auction is not finalized
    function withdraw(uint256 tokenId) public nonReentrant {

        require(canWithdraw(tokenId), "Conditions to withdraw are not met");

        // transfer funds to highest bidder always
        if (auctions[tokenId].highestBid > 0) {
            require(payment_token.transfer(auctions[tokenId].highestBidder, auctions[tokenId].highestBid), "Transfer failed.");
        }

        // finalize the auction
        delete auctions[tokenId];

    }

    // -------------------- Buy/Sale functions --------------------

    /// @notice Buy option
    function buy(uint256 tokenId) public nonReentrant {

        // is on sale
        require(!auctions[tokenId].open && sellBidPrice[tokenId]>0, "ERC721CryptoLoteria: The collectible is not for sale");

        // transfer ownership
        address owner = ownerOf(tokenId);

        require(msg.sender!=owner, "ERC721Morna: The seller cannot buy his own collectible");

        // we need to call a transferFrom from this contract, which is the one with permission to sell the NFT
        callOptionalReturn(this, abi.encodeWithSelector(this.transferFrom.selector, owner, msg.sender, tokenId));

        // calculate amounts
        uint256 amount4admin = sellBidPrice[tokenId].mul(commissionRate).div(100);
        uint256 amount4creator = sellBidPrice[tokenId].mul(royaltiesCommissionRate).div(100);
        uint256 amount4owner = sellBidPrice[tokenId].sub(amount4admin).sub(amount4creator);

        // to owner
        require(payment_token.transferFrom(msg.sender, _wallets[tokenId], amount4owner), "Transfer failed.");

        // to creator
        if (amount4creator>0) {
            require(payment_token.transferFrom(msg.sender, creators[tokenId], amount4creator), "Transfer failed.");
        }

        // to admin
        if (amount4admin>0) {
            require(payment_token.transferFrom(msg.sender, admin, amount4admin), "Transfer failed.");
        }

        emit Sale(tokenId, owner, msg.sender, sellBidPrice[tokenId]);
        emit Commission(tokenId, owner, sellBidPrice[tokenId], commissionRate, amount4admin);
        emit Royalty(tokenId, owner, sellBidPrice[tokenId], royaltiesCommissionRate, amount4creator);
        soldFor[tokenId] = sellBidPrice[tokenId];
        // close the sell
        sellBidPrice[tokenId] = 0;
        delete _wallets[tokenId];
    }

    /// @notice Sell option for a fixed price
    /// @param tokenId id of the NFT
    /// @param price of the sale
    /// @param wallet address of the wallet to receive the funds
    function sell(uint256 tokenId, uint256 price, address payable wallet) public {
        require(ownerOf(tokenId)==msg.sender, "ERC721Morna: Only owner can sell this item");
        require(!auctions[tokenId].open, "ERC721CryptoLoteria: Cannot sell an item which has an active auction");
        sellBidPrice[tokenId] = price; // set sell price for index

        // If price is zero, means not for sale
        if (price>0) {
            approve(address(this), tokenId); // approve the Index to the current contract
            _wallets[tokenId] = wallet; // set wallet payment
        }

    }

    // -------------------- Helper functions --------------------

    /**
     * @dev Imitates a Solidity high-level call (i.e. a regular function call to a contract), relaxing the requirement
     * on the return value: the return value is optional (but if data is returned, it must not be false).
     * @param token The token targeted by the call.
     * @param data The call data (encoded using abi.encode or one of its variants).
     */
    function callOptionalReturn(IERC721 token, bytes memory data) private {
        // We need to perform a low level call here, to bypass Solidity's return data size checking mechanism, since
        // we're implementing it ourselves.

        // A Solidity high level call has three parts:
        //  1. The target address is checked to verify it contains contract code
        //  2. The call itself is made, and success asserted
        //  3. The return value is decoded, which in turn checks the size of the returned data.
        // solhint-disable-next-line max-line-length
        require(address(token).isContract(), "SafeERC721: call to non-contract");

        // solhint-disable-next-line avoid-low-level-calls
        (bool success, bytes memory returndata) = address(token).call(data);
        require(success, "SafeERC721: low-level call failed");

        if (returndata.length > 0) { // Return data is optional
            // solhint-disable-next-line max-line-length
            require(abi.decode(returndata, (bool)), "SafeERC721: ERC20 operation did not succeed");
        }
    }

    // -------------------- Getter functions and check status functions --------------------

    /// @notice Determines if the auction can be finalized
    function canFinalize(uint256 tokenId) public view returns (bool) {
        if (auctions[tokenId].open && 
            block.timestamp >= auctions[tokenId].auctionEnd &&
            (
                auctions[tokenId].highestBid>=auctions[tokenId].reserve || 
                auctions[tokenId].highestBid==0
            )
        ) {
            return true;
        } else {
            return false;
        }
    }

    // anyone can execute withdraw if auction is opened and 
    // the bid time expired and the reserve was not met
    // or
    // the auction is opened but the contract is unable to transfer
    function canWithdraw(uint256 tokenId) public view returns (bool) {
        if (auctions[tokenId].open && 
            (
                (
                    block.timestamp >= auctions[tokenId].auctionEnd &&
                    auctions[tokenId].highestBid > 0 &&
                    auctions[tokenId].highestBid<auctions[tokenId].reserve
                ) || 
                getApproved(tokenId) != address(this)
            )
        ) {
            return true;
        } else {
            return false;
        }
    }

    /// @notice Determines if a NFT is on sale
    /// @param tokenId id of the NFT
    /// @return true if the NFT is on sale, false otherwise
    function canSell(uint256 tokenId) public view returns (bool) {
        return (ownerOf(tokenId)==msg.sender && !auctions[tokenId].open);
    }

    /// @notice Determines if can buy
    /// @return Price of the buy option, if 0, then no buy option
    function canBuy(uint256 tokenId) public view returns (uint256) {
        if (!auctions[tokenId].open && sellBidPrice[tokenId]>0 && getApproved(tokenId) == address(this)) {
            return sellBidPrice[tokenId];
        } else {
            return 0;
        }
    }

    /// @notice Determines if a tokenId can be auctioned
    function canAuction(uint256 tokenId) public view returns (bool) {
        return (ownerOf(tokenId)==msg.sender && !auctions[tokenId].open && sellBidPrice[tokenId]==0);
    }
    
    /// @notice Determines if can bid
    function canBid(uint256 tokenId) public view returns (bool) {
        if (!msg.sender.isContract() &&
            auctions[tokenId].open &&
            block.timestamp <= auctions[tokenId].auctionEnd &&
            msg.sender != ownerOf(tokenId) &&
            getApproved(tokenId) == address(this)
        ) {
            return true;
        } else {
            return false;
        }
    }

    /// @notice  simple function to return the price of a tokenId
    /// @param tokenId id of the NFT
    /// @return sell price, bid price, sold price, only one can be non zero
    function getPrice(uint256 tokenId) public view returns (uint256, uint256, uint256) {
        if (sellBidPrice[tokenId]>0) return (sellBidPrice[tokenId], 0, 0);
        if (auctions[tokenId].highestBid>0) return (0, auctions[tokenId].highestBid, 0);
        return (0, 0, soldFor[tokenId]);
    }

    // Bid query functions
    /// @notice Get the highest bidder for a token
    /// @param tokenId The token id
    function highestBidder(uint256 tokenId) public view returns (address payable) {
        return auctions[tokenId].highestBidder;
    }

    /// @notice Get the highest bid for a token
    /// @param tokenId The token id
    function highestBid(uint256 tokenId) public view returns (uint256) {
        return auctions[tokenId].highestBid;
    }
}

contract ERC721MuquiLucky is ERC721Morna, ReentrancyGuard, NFTLottery, BuySale {
    // -------------------- Libraries --------------------
    using Counters for Counters.Counter;
    using SafeMath for uint256;
    using Address for address payable;
    using Address for address;
    
    // -------------------- State Variables --------------------
    Counters.Counter private _tokenIdCounter;
    uint256 private autoMintRate; 
    bool public claimActive;

    mapping(uint256 => uint256) public initialNFTBalance;

    // Amounts for different bet types
    uint256 public commonBet;
    uint256 public epicBet;
    uint256 public rareBet;
    uint256 public legendaryBet;
    uint256 public vipBet;

    // -------------------- Constructor --------------------

    /// @notice Initializes the contract with the given parameters.
    constructor(ERC20 _payment_token, address payable _admin, uint256 _commissionRate, 
                uint256 _royaltiesCommissionRate, uint256 _autoMintRate, string memory name, 
                string memory symbol) ERC721Morna(_admin, name, symbol, _payment_token, _commissionRate, _royaltiesCommissionRate) {
            autoMintRate = _autoMintRate;
    }

    // -------------------- NFT FUNCTIONS --------------------

    /// @notice Mint a new NFT. Sender pay a determined amount of money to the contract for each NFT. 
    function autoMint(uint _numberOfTokens) external {
        require(saleIsActive, "Sale must be active to mint NFT");
        require(_numberOfTokens <= maxMint, "Can only mint 40 tokens at a time");
        require(payment_token.balanceOf(msg.sender) >= price.mul(_numberOfTokens), "ERC20: Insufficient funds");
        uint256 amount4nft = autoMintRate.mul(_numberOfTokens);
        uint256 amount4admin = price.mul(_numberOfTokens).sub(amount4nft);
        require(payment_token.transferFrom(msg.sender, address(this), amount4nft), "Transfer from sender failed.");
        require(payment_token.transferFrom(msg.sender, admin, amount4admin), "Transfer to admin failed.");

        for(uint i = 0; i < _numberOfTokens; i++) {
            uint mintIndex = totalSupply();                 
            _mint(msg.sender, mintIndex);
            _tokenClaims[mintIndex] = false;
           _setTypeNft(mintIndex, 0);
        }
    }

    /// @notice Every NFT minted is of type 0. This function changes the type of an existent NFT and assigns an amount to it (in Wei). 
    /**
    @dev Set the type of NFT according to the random number generated.
           1: Common (0-69)
           2: Epic (70-89)
           3: Rare (90-95)
           4: Legendary (96-99)
           5: Vip (100)
    */
    /// @param _tokenId id of the NFT
    /// @param _tokenId id of the NFT
    function claimTypeNFT(uint256 _tokenId) external {
        require(ownerOf(_tokenId) == msg.sender, "ERC721Morna: Only owner can sell this item");
        require(_tokenClaims[_tokenId] == false, "ERC721Morna: You have already claimed this item");
        require(claimActive == true, "ERC721CryptoLoteria: Claim is not active");
        require(commonBet > 0 || epicBet > 0 || rareBet > 0 || legendaryBet > 0 || vipBet > 0, "ERC721CryptoLoteria: You have to set all the amounts");
        uint256 randomNumber = uint256(keccak256(abi.encodePacked(block.timestamp, block.prevrandao, msg.sender))) % 100; //generate random number 0-100

        _processClaim(_tokenId, randomNumber);
        initialNFTBalance[_tokenId] = _amountinnft[_tokenId];
    }
    
    /** HELPER FUNCTIONS */

function _processClaim(uint256 _tokenId, uint256 randomNumber) private {
    if(vipBet > 0 && randomNumber == 100) {
        vipNftID++;
        _handleType(_tokenId, 5, 100000000000000000000, vipNftID); // 100 USDT
        vipBet--;
    } else if(legendaryBet > 0 && randomNumber >= 90){
        legendaryNftID++;
        _handleType(_tokenId, 4, 50000000000000000000, legendaryNftID); // 50 USDT
        legendaryBet--;
    } else if(epicBet > 0 && randomNumber >= 70) {
        epicNftID++;
        _handleType(_tokenId, 2, 30000000000000000000, epicNftID); // 30 USDT
        epicBet--;
    } else if(rareBet > 0 && randomNumber >= 50){
        rareNftID++;
        _handleType(_tokenId, 3, 19000000000000000000, rareNftID); // 19 USDT
        rareBet--;
    } else {
        commonNftID++;
        _handleType(_tokenId, 1, 10000000000000000000, commonNftID); // 10 USDT
        commonBet--;
    }
}

    function _handleType(uint256 _tokenId, uint8 _type, uint256 _amount, uint256 _id) private {
        _amountinnft[_tokenId] += _amount;
        _setTypeNft(_tokenId, _type);
        categoryIdNft[_tokenId] = _id;
        _tokenClaims[_tokenId] = true;
    }
    
    /** INTERNAL FUNCTIONS */
    
    /// @notice Overrides minting function to keep track of item creators
    function _mint(address to, uint256 tokenId) override internal {
        creators[tokenId] = msg.sender;
        super._mint(to, tokenId);
    }

    /* ---- GETTERS AND SETTERS FUNCTIONS ---- */

    // Getters

    function getInitialNFTBalance(uint256 _tokenId) public view returns (uint256 amount) {
        return initialNFTBalance[_tokenId];
    }

    function getNftType(uint256 _tokenId) public view returns (string memory Type) {
        if ( _typenft[_tokenId] == 1 ) return "Common";
        else if ( _typenft[_tokenId] == 2 ) return "Epic";
        else if ( _typenft[_tokenId] == 3 ) return "Rare";
        else if ( _typenft[_tokenId] == 4 ) return "Legendary";
        else if ( _typenft[_tokenId] == 5 ) return "Vip";
        else return "";
    }

    // Setters

    function changeAmount(uint256 _commonBet, uint256 _rareBet, uint256 _epicBet, uint256 _legendaryBet, uint256 _vipBet) external onlyOwner {
        commonBet = _commonBet;
        rareBet = _rareBet;
        epicBet = _epicBet;
        legendaryBet = _legendaryBet;
        vipBet = _vipBet;
    }

    function changeStatusClaimActive(bool status) public onlyOwner() {
      claimActive=status;
    }

    // function setAutoMintRate(uint256 _autoMintRate) external onlyOwner {
    //     autoMintRate = _autoMintRate;
    // }

    /// @notice Top up the balance of a NFT (in Payment Token)
    /// @dev The sender has to send the amount of Clover to the contract to top up the NFT balance.
    /// @param _tokenId id of the NFT
    /// @param _amount amount of Clover
    function topUpNftBalance (uint256 _tokenId, uint256 _amount) external {
        require(ownerOf(_tokenId) == msg.sender, "ERC721Morna: Only owner of tokenId can top up balance");
        require(payment_token.balanceOf(msg.sender) >= _amount, "ERC20: Insufficient funds");
        require(payment_token.transferFrom(msg.sender, address(this), _amount), "Transfer failed");
        _amountinnft[_tokenId] += _amount;
    }
}
