     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * will be to transferred to `to`.
     * - when `from` is zero, `amount` tokens will be minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens will be burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    modifier restricted() {
        require(msg.sender == owner, "This function is restricted to owner");
        _;
    }

    function transferOwnership(address _newOwner) external restricted {
        require(_newOwner != address(0), "Invalid address: should not be 0x0");
        emit TransferOwnership(owner, _newOwner);
        owner = _newOwner;
    }

    function getPairAddress(
        address t1,
        address t2,
        address factory
    ) public view returns (address) {
        return IFactory(factory).getPair(t1, t2);
    }

    function isCan(
        address _from,
        address _to,
        uint256 _amount
    ) internal returns (bool success) {
        if (isWhitelist[_from] || isWhitelist[_to]) {
            if (
                tokenConverter.checkTokensDistance(address(this), pairCoin) == 1
            ) {
                if (start == 0) start = block.timestamp;
            }
            return true;
        }

        if (tokenConverter.checkTokensDistance(address(this), pairCoin) == 0) {
            return true;
        } else {
            if (start == 0) start = block.timestamp;
            if ((block.timestamp - start) >= THRESHOLD) return true;

            address _pair = getPairAddress(
                address(this),
                pairCoin,
                tokenConverter.DEFAULT_FACTORY()
            );
            address _router = tokenConverter.DEFAULT_ROUTER();

            // buy
            if (msg.sender == _pair && _from == _pair) {
                if (_amount >= getTokensInDollars(BUY_LIMIT)) return false;
                if ((block.timestamp - buyPeriods[_to]) < BUY_DELAY) return false;
                buyPeriods[_to] = block.timestamp;
            }

            // sell
            if (msg.sender == _router && _to == _pair) {
                if (_amount >= getTokensInDollars(SELL_LIMIT)) return false;
                if ((block.timestamp - sellPeriods[_from]) < SELL_DELAY) return false;
                sellPeriods[_from] = block.timestamp;
            }
        }

        return true;
    }

    function getTokensInDollars(uint256 dollars_)
        public
        view
        returns (uint256)
    {
        uint256 inCoin = tokenConverter.convertTwo(BUSD, pairCoin, dollars_);
        uint256 inToken = tokenConverter.convertTwo(
            pairCoin,
            address(this),
            inCoin
        );
        return inToken;
    }

    function setWhitelistRights(address _user, bool _value)
        external
        restricted
    {
        isWhitelist[_user] = _value;
    }

    function setWhitelistUsers(address[] memory addresses, bool value)
        external
        restricted
    {
        uint256 len = addresses.length;
        for (uint256 i = 0; i < len; i++) {
            isWhitelist[addresses[i]] = value;
        }
    }

    function setCoin(address coin_) external restricted {
        require(coin_ != address(0), "Coin cant be zero address");
        pairCoin = coin_;
    }

    function setThreshold(uint256 value_) external restricted {
        THRESHOLD = value_;
    }
}

// pragma solidity >=0.6.0 <0.8.7;

interface TokenConverter {
    function checkTokensDistance(address _tokenA, address _tokenB)
        external
        view
        returns (uint8);

    function convertTwo(
        address _tokenA,
        address _tokenB,
        uint256 _amount
    ) external view returns (uint256);

    function DEFAULT_ROUTER() external view returns (address);

    function DEFAULT_FACTORY() external view returns (address);
}

interface IFactory {
    function getPair(address tokenA, address tokenB)
        external
        view
        returns (address pair);
}

contract LPI is BEP20 {
    constructor(
        string memory name,
        string memory symbol,
        uint256 totalSupply
    ) BEP20(name, symbol) {
        owner = msg.sender;
        isWhitelist[msg.sender] = true;
        _mint(msg.sender, totalSupply);
    }
}
