      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router01.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";

/// @author thedao.dev@gmail.com
contract EvanToken is AccessControl, ERC20 {
    bytes32 public constant BLACKLISTED_ROLE = keccak256("BLACKLISTED_ROLE");
    bytes32 public constant WHITELISTED_ROLE = keccak256("WHITELISTED_ROLE");

    uint256 public constant MAX_SUPPLY = 777_777e18;
    uint256 public constant FEE_DENOMINATOR = 1000;

    IUniswapV2Router02 public immutable uniswapV2Router;

    address public immutable uniswapV2Pair;

    struct FeeRate {
        uint8 total;
        uint8 reward;
        uint8[] referrals;
    }

    FeeRate public feeRate;

    uint256 public rewardPerShare;

    struct User {
        address referrer;
        uint256 reward;
        uint256 lastRewardPerToken;
    }

    mapping(address => User) public users;

    event FeeRateUpdated(uint8 previousTotalRate, uint8 previousRewardRate, uint8[] previousReferralRates, uint8 burnRate, uint8 rewardRate, uint8[] referralRates);
    event Claimed(address indexed account, uint256 amount);

    constructor(IUniswapV2Router02 uniswapV2Router_, address pairToken_) ERC20("Evan Token", "EVAN") {
        require(address(uniswapV2Router_) != address(0), "uniswapV2Router cannot be zero address");
        require(pairToken_ != address(0), "pairToken cannot be zero address");

        uniswapV2Router = uniswapV2Router_;
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router_.factory()).createPair(address(this), pairToken_);

        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());

        _grantRole(WHITELISTED_ROLE, _msgSender());
        _grantRole(WHITELISTED_ROLE, address(0));
        _grantRole(WHITELISTED_ROLE, address(this));

        feeRate.total = 70;
        feeRate.reward = 30;
        feeRate.referrals = [15, 10, 5];
    }

    function balanceOf(address account) public view override returns (uint256) {
        return super.balanceOf(account) + releasable(account);
    }

    function totalShares() public view returns (uint256) {
        return IERC20(uniswapV2Pair).totalSupply() - IERC20(uniswapV2Pair).balanceOf(address(0));
    }

    function releasable(address account) public view returns (uint256) {
        if (rewardPerShare == 0) {
            return users[account].reward;
        }
        return users[account].reward + (IERC20(uniswapV2Pair).balanceOf(account) * (rewardPerShare - users[account].lastRewardPerToken) / 1e18);
    }

    function release(address account) public {
        uint256 amount = releasable(account);
        if (amount > 0) {
            users[account].reward = 0;

            _transfer(address(this), account, amount);
            emit Claimed(account, amount);
        }

        users[account].lastRewardPerToken = rewardPerShare;
    }

    function mint(address to, uint256 amount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(amount + totalSupply() <= MAX_SUPPLY, "Max supply exceeded");
        _mint(to, amount);
    }

    function setFeeRate(uint8 totalRate, uint8 rewardRate, uint8[] memory referralRates) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(totalRate > 0 && totalRate <= FEE_DENOMINATOR, "Invalid total rate");
        require(rewardRate > 0, "Invalid reward rate");
        require(referralRates.length > 0, "Invalid referral rates");

        uint256 referralRate = 0;
        for (uint256 i = 0; i < referralRates.length; i++) {
            referralRate += referralRates[i];
        }
        require(rewardRate + referralRate <= totalRate, "Invalid fee rate");

        uint8 previousTotalRate = feeRate.total;
        uint8 previousRewardRate = feeRate.reward;
        uint8[] memory previousReferralRates = feeRate.referrals;

        feeRate.total = totalRate;
        feeRate.reward = rewardRate;
        feeRate.referrals = referralRates;

        emit FeeRateUpdated(previousTotalRate, previousRewardRate, previousReferralRates, totalRate, rewardRate, referralRates);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override {
        require(!hasRole(BLACKLISTED_ROLE, from), "Transfer from a blacklisted account");

        if (!hasRole(WHITELISTED_ROLE, from) && !_isUniswapV2Pair(from)) {
            release(from);

            if (!hasRole(WHITELISTED_ROLE, to) && !_isUniswapV2Pair(to) && users[to].referrer == address(0) && super.balanceOf(to) == 0) {
                require(amount > 0, "Transfer amount must be greater than zero");
                users[to].referrer = from;
            }
        }
    }

    function _afterTokenTransfer(address from, address to, uint256 amount) internal override {
        if (!hasRole(WHITELISTED_ROLE, from) && !hasRole(WHITELISTED_ROLE, to)) {
            address target = _isUniswapV2Pair(to) ? from : to;

            uint256 feeAmount = amount * feeRate.total / FEE_DENOMINATOR;
            _transfer(target, address(this), feeAmount);

            if (totalShares() > 0) {
                uint256 rewardAmount = amount * feeRate.reward / FEE_DENOMINATOR;
                feeAmount -= rewardAmount;

                rewardPerShare += (rewardAmount * 1e18 / totalShares());
            }

            address referrer = users[target].referrer;
            for (uint256 i = 0; i < feeRate.referrals.length; i++) {
                if (referrer == address(0)) {
                    break;
                }

                uint256 inviteRewardAmount = amount * feeRate.referrals[i] / FEE_DENOMINATOR;
                feeAmount -= inviteRewardAmount;

                users[referrer].reward += inviteRewardAmount;

                referrer = users[referrer].referrer;
            }

            if (feeAmount > 0) {
                _burn(address(this), feeAmount);
            }

            if (!_isUniswapV2Pair(from) && !_isUniswapV2Pair(to)) {
                require(super.balanceOf(from) > 0, "Balance cannot be empty");
            }
        }
    }

    function _isUniswapV2Pair(address account) private view returns (bool) {
        if (Address.isContract(account)) {
            try IUniswapV2Pair(account).factory() returns (address retval) {
                return retval == uniswapV2Router.factory();
            } catch {
                return false;
            }
        }
        return false;
    }
}
