library Math {
    function min(uint x, uint y) internal pure returns (uint z) {
        z = x < y ? x : y;
    }

    function sqrt(uint y) internal pure returns (uint z) {
        if (y > 3) {
            z = y;
            uint x = y / 2 + 1;
            while (x < z) {
                z = x;
                x = (y / x + x) / 2;
            }
        } else if (y != 0) {
            z = 1;
        }
    }
}

library PancakeLibrary {
    // returns sorted token addresses, used to handle return values from pairs sorted in this order
    function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1) {
        require(tokenA != tokenB, 'PancakeLibrary: IDENTICAL_ADDRESSES');
        (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), 'PancakeLibrary: ZERO_ADDRESS');
    }

    // calculates the CREATE2 address for a pair without making any external calls
    function pairFor(address factory, address tokenA, address tokenB) internal pure returns (address pair) {
        (address token0, address token1) = sortTokens(tokenA, tokenB);
        pair = address(uint160(uint(keccak256(abi.encodePacked(
                hex'ff',
                factory,
                keccak256(abi.encodePacked(token0, token1)),
                hex'00fb7f630766e6a796048ea87d01acd3068e8ff67d078148a3fa3f4a84f69bd5' // init code hash
            )))));
    }
}

contract Recv {
    IERC20 public token;
    IERC20 public usdt;

    constructor(IERC20 _token, IERC20 _usdt) {
        token = _token;
        usdt = _usdt;
    }

    function withdraw() public {
        uint256 usdtBalance = usdt.balanceOf(address(this));
        if (usdtBalance > 0) {
            usdt.transfer(address(token), usdtBalance);
        }
        uint256 tokenBalance = token.balanceOf(address(this));
        if (tokenBalance > 0) {
            token.transfer(address(token), tokenBalance);
        }
    }
}


contract QJGaia is IERC20, Ownable {

    mapping(address => uint256) public _balances;
    mapping(address => mapping(address => uint256)) private _allowances;


    string private _name;
    string private _symbol;
    uint8 private _decimals;

    mapping(address => bool) public _feeWhiteList;
    mapping(address => bool) public _isBlacklist;

    uint256 private _tTotal;

    Recv public recv;


    ISwapRouter public immutable _swapRouter;
    mapping(address => bool) public _swapPairList;
    mapping(address => bool) public _swapRouters;

    bool private inSwap;

    uint256 private constant MAX = ~uint256(0);

    uint256 public  _buyDestroyFee = 200;
    uint256 public _buyLpshareFee = 100;
    uint256 public _buyFoundFee = 100;
    uint256 public _buyFlowBackFee = 100;


    uint256  public  _sellDestoryFee = 200;
    uint256  public _sellLpShareFee = 100;
    uint256 public _sellFoundFee = 100;
    uint256 public _sellFlowBackFee = 100;

    bool public openSwap;
    address public immutable _mainPair;
    address public  immutable _usdt;

    bool public _strictCheck = true;
    uint256 public _LPFeefenhong;
    uint256 public _minPeriod = 600;

    address RouterAddress = address(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    address USDTAddress = address(0x55d398326f99059fF775485246999027B3197955);
    string Name = "QJ-Gaia";
    string Symbol = "QJ-Gaia";
    uint8 Decimals = 9;
    uint256 Supply = 26889;
    address ReceiveAddress = address(0x980c1da42aFf5cb2E2c74672492100eae6b6aF2a);
    address FoundAddress = address(0x8AA58760C547ddd654ED2C4761195a99ddE2663A);

    modifier lockTheSwap {
        inSwap = true;
        _;
        inSwap = false;
    }

    constructor (){
        _name = Name;
        _symbol = Symbol;
        _decimals = Decimals;

        ISwapRouter swapRouter = ISwapRouter(RouterAddress);
        _swapRouter = swapRouter;
        _allowances[address(this)][address(swapRouter)] = MAX;
        _swapRouters[address(swapRouter)] = true;

        IERC20(USDTAddress).approve(address(swapRouter), MAX);

        ISwapFactory swapFactory = ISwapFactory(swapRouter.factory());
        _usdt = USDTAddress;

        address lpPair = PancakeLibrary.pairFor(address(swapFactory), _usdt, address(this));
       
        _swapPairList[lpPair] = true;
        _mainPair = lpPair;

        recv = new Recv(IERC20(this), IERC20(USDTAddress));

        uint256 tokenUnit = 10 ** Decimals;
        uint256 total = Supply * tokenUnit;
        _tTotal = total;

        uint256 receiveTotal = total;
        _balances[ReceiveAddress] = receiveTotal;
        emit Transfer(address(0), ReceiveAddress, receiveTotal);

        _feeWhiteList[ReceiveAddress] = true;
        _feeWhiteList[FoundAddress] = true;
        _feeWhiteList[address(recv)] = true;
        _feeWhiteList[address(this)] = true;
        _feeWhiteList[msg.sender] = true;
        _feeWhiteList[address(0)] = true;
        _feeWhiteList[address(swapRouter)] = true;
        _feeWhiteList[address(0xdead)] = true;
        
    }

    function symbol() external view override returns (string memory) {
        return _symbol;
    }

    function name() external view override returns (string memory) {
        return _name;
    }

    function decimals() external view override returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        if (_allowances[sender][msg.sender] != MAX) {
            _allowances[sender][msg.sender] = _allowances[sender][msg.sender] - amount;
        }
        return true;
    }

    function _approve(address owner, address spender, uint256 amount) private {
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        if (_isBlacklist[from] || _isBlacklist[to]) {
            require(!_isBlacklist[from], "the sender address is blacklist");
            require(!_isBlacklist[to], "the recipient address is blacklist");
        }

        uint256 balance = balanceOf(from);
        require(balance >= amount, "not enough amount");


        bool takeFee = false;
        if (to == _mainPair || from == _mainPair) {
            takeFee = true;
        }
        if (_feeWhiteList[from] || _feeWhiteList[to]) {
            takeFee = false;
        }

        uint256 addLPLiquidity;
        if (to == _mainPair) {
            addLPLiquidity = _isAddLiquidity(amount);
        }

        if (!openSwap && addLPLiquidity == 0 && takeFee){
            _funTransfer(from, to, amount, 99);
        } else {
            _tokenTransfer(from, to, amount, takeFee, addLPLiquidity);
        }

        if (from != address(this) && addLPLiquidity > 0) {
            _addLpProvider(from);
        }

        if (
            balanceOf(address(this)) >= 1 * 10 ** 9 &&
            from != address(this) &&
            _LPFeefenhong + _minPeriod <= block.timestamp
        ) {
            processLPReward(_rewardGas);
            _LPFeefenhong = block.timestamp;
        }
    }

    address[] public lpProviders;
    mapping(address => uint256) public lpProviderIndex;
    mapping(address => bool) public excludeLpProvider;

    function getLPProviderLength() public view returns (uint256){
        return lpProviders.length;
    }

    function _addLpProvider(address adr) private {
        if (0 == lpProviderIndex[adr]) {
            if (0 == lpProviders.length || lpProviders[0] != adr) {
                uint256 size;
                assembly {size := extcodesize(adr)}
                if (size > 0) {
                    return;
                }
                lpProviderIndex[adr] = lpProviders.length;
                lpProviders.push(adr);
            }
        }
    }

    uint256 public _rewardGas = 2000000;

    function setExcludeLPProvider(address addr, bool enable) public onlyOwner {
        excludeLpProvider[addr] = enable;
    }

    receive() external payable {}

    function setRewardGas(uint256 rewardGas) public onlyOwner {
        require(rewardGas >= 200000 && rewardGas <= 2000000, "20-200w");
        _rewardGas = rewardGas;
    }

    uint256 public currentLPIndex;
    function processLPReward(uint256 gas) private {
        IERC20 holdToken = IERC20(_mainPair);
        uint holdTokenTotal = holdToken.totalSupply();
        if (0 == holdTokenTotal) {
            return;
        }

        address shareHolder;
        uint256 tokenBalance;
        uint256 amount;
        uint256 rewardCondition = balanceOf(address(this));
        uint256 shareholderCount = lpProviders.length;

        uint256 gasUsed = 0;
        uint256 iterations = 0;
        uint256 gasLeft = gasleft();
        while (gasUsed < gas && iterations < shareholderCount) {
            if (currentLPIndex >= shareholderCount) {
                currentLPIndex = 0;
            }
            shareHolder = lpProviders[currentLPIndex];
            if (!excludeLpProvider[shareHolder] && balanceOf(shareHolder) >= 2 * 10 ** 9) {
                tokenBalance = holdToken.balanceOf(shareHolder);
                if (balanceOf(_mainPair) * tokenBalance / holdTokenTotal >= 1 * 10 ** 9) {
                    amount = rewardCondition * tokenBalance / holdTokenTotal;
                    if (amount > 0) {
                        _balances[address(this)] = _balances[address(this)] - amount;
                        _balances[shareHolder] = _balances[shareHolder] + amount;
                        emit Transfer(address(this), shareHolder, amount);
                    }
                }
            }

            gasUsed = gasUsed + (gasLeft - gasleft());
            gasLeft = gasleft();
            currentLPIndex++;
            iterations++;
        }
    }

    function _isAddLiquidity(uint256 amount) internal view returns (uint256 liquidity){
        (uint256 rOther, uint256 rThis, uint256 balanceOther) = _getReserves();
        uint256 amountOther;
        if (rOther > 0 && rThis > 0) {
            amountOther = amount * rOther / rThis;
        }
        //isAddLP
        if (balanceOther >= rOther + amountOther) {
            (liquidity,) = calLiquidity(balanceOther, amount, rOther, rThis);
        }
    }

    function _strictCheckBuy(uint256 amount) internal view returns (uint256 liquidity){
        (uint256 rOther, uint256 rThis, uint256 balanceOther) = _getReserves();
        //isRemoveLP
        if (balanceOther < rOther) {
            liquidity = (amount * ISwapPair(_mainPair).totalSupply()) /
            (_balances[_mainPair] - amount);
        } else if (_strictCheck) {
            uint256 amountOther;
            if (rOther > 0 && rThis > 0) {
                amountOther = amount * rOther / (rThis - amount);
                //strictCheckBuy 2
                require(balanceOther >= amountOther + rOther);
            }
        }
    }

    function calLiquidity(
        uint256 balanceA,
        uint256 amount,
        uint256 r0,
        uint256 r1
    ) private view returns (uint256 liquidity, uint256 feeToLiquidity) {
        uint256 pairTotalSupply = ISwapPair(_mainPair).totalSupply();
        address feeTo = ISwapFactory(_swapRouter.factory()).feeTo();
        bool feeOn = feeTo != address(0);
        uint256 _kLast = ISwapPair(_mainPair).kLast();
        if (feeOn) {
            if (_kLast != 0) {
                uint256 rootK = Math.sqrt(r0 * r1);
                uint256 rootKLast = Math.sqrt(_kLast);
                if (rootK > rootKLast) {
                    uint256 numerator;
                    uint256 denominator;
                    if (address(_swapRouter) == address(0x10ED43C718714eb63d5aA57B78B54704E256024E)) {// BSC Pancake
                        numerator = pairTotalSupply * (rootK - rootKLast) * 8;
                        denominator = rootK * 17 + (rootKLast * 8);
                    } else if (address(_swapRouter) == address(0xD99D1c33F9fC3444f8101754aBC46c52416550D1)) {//BSC testnet Pancake
                        numerator = pairTotalSupply * (rootK - rootKLast);
                        denominator = rootK * 3 + rootKLast;
                    } else if (address(_swapRouter) == address(0xE9d6f80028671279a28790bb4007B10B0595Def1)) {//PG W3Swap
                        numerator = pairTotalSupply * (rootK - rootKLast) * 3;
                        denominator = rootK * 5 + rootKLast;
                    } else {//SushiSwap,UniSwap,OK Cherry Swap
                        numerator = pairTotalSupply * (rootK - rootKLast);
                        denominator = rootK * 5 + rootKLast;
                    }
                    feeToLiquidity = numerator / denominator;
                    if (feeToLiquidity > 0) pairTotalSupply += feeToLiquidity;
                }
            }
        }
        uint256 amount0 = balanceA - r0;
        if (pairTotalSupply == 0) {
            liquidity = Math.sqrt(amount0 * amount) - 1000;
        } else {
            liquidity = Math.min(
                (amount0 * pairTotalSupply) / r0,
                (amount * pairTotalSupply) / r1
            );
        }
    }

    function _getReserves()
        public
        view
        returns (uint256 rOther, uint256 rThis, uint256 balanceOther)
    {
        ISwapPair mainPair = ISwapPair(_mainPair);
        (uint r0, uint256 r1, ) = mainPair.getReserves();

        address tokenOther = USDTAddress;
        if (tokenOther < address(this)) {
            rOther = r0;
            rThis = r1;
        } else {
            rOther = r1;
            rThis = r0;
        }

        balanceOther = IERC20(tokenOther).balanceOf(address(mainPair));
    }

    function _isRemoveLiquidityETH(uint256 amount) internal view returns (uint256 liquidity){
        (uint256 rOther,, uint256 balanceOther) = _getReserves();
        //isRemoveLP
        if (balanceOther <= rOther) {
            liquidity = amount * ISwapPair(_mainPair).totalSupply() / balanceOf(_mainPair);
        }
    }

    function _funTransfer(
        address sender,
        address recipient,
        uint256 tAmount,
        uint256 fee
    ) private {
        _balances[sender] = _balances[sender] - tAmount;
        uint256 feeAmount = tAmount * fee / 100;
        if (feeAmount > 0) {
            _takeTransfer(sender, address(0xdead), feeAmount);
        }
        _takeTransfer(sender, recipient, tAmount - feeAmount);
    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 tAmount,
        bool takeFee,
        uint256 addLiquidity
    ) private {
        uint256 senderBalance = _balances[sender];
        senderBalance -= tAmount;
        _balances[sender] = senderBalance;
        uint256 feeAmount;

        if (takeFee) {
            bool isSell;
            uint256 swapFeeAmount;
            uint256 destroyFeeAmount;
            uint256 foundFeeAmount;
            uint256 flowBackFeeAmount;
            
            if (_swapPairList[sender]) {//Buy
                swapFeeAmount = tAmount * _buyLpshareFee / 10000;
                destroyFeeAmount = tAmount * _buyDestroyFee /10000;
                foundFeeAmount = tAmount * _buyFoundFee / 10000;
                flowBackFeeAmount = tAmount * _buyFlowBackFee / 10000;
            } else if (_swapPairList[recipient]) {//Sell
                isSell = true;
                swapFeeAmount = tAmount * _sellLpShareFee / 10000;
                destroyFeeAmount = tAmount * _sellDestoryFee / 10000;
                foundFeeAmount = tAmount * _sellFoundFee / 10000;
                flowBackFeeAmount = tAmount * _sellFlowBackFee / 10000;
            }

            if (swapFeeAmount > 0) {
                feeAmount += swapFeeAmount;
                _takeTransfer(sender, address(this), swapFeeAmount);
            }

            if (destroyFeeAmount > 0) {
                feeAmount += destroyFeeAmount;
                _takeTransfer(sender, address(0xdead), destroyFeeAmount);
            }

            if (foundFeeAmount > 0) {
                feeAmount += foundFeeAmount;
                _takeTransfer(sender, address(FoundAddress), foundFeeAmount);
            }

            if (flowBackFeeAmount > 0) {
                feeAmount += flowBackFeeAmount;
                _takeTransfer(sender, address(recv), flowBackFeeAmount);
            }

             if (!inSwap && isSell && addLiquidity == 0) {
                uint256 balanceBefore = _balances[address(this)];
                recv.withdraw();
                uint256 balanceExtra = _balances[address(this)] - balanceBefore;
                if (balanceExtra > 0) {
                    _swapAndAddLiquidity(balanceExtra);
                }
            }

        }


        _takeTransfer(sender, recipient, tAmount - feeAmount);
    }

    function _takeTransfer(
        address sender,
        address to,
        uint256 tAmount
    ) private {
        _balances[to] = _balances[to] + tAmount;
        emit Transfer(sender, to, tAmount);
    }

    function setFeeWhiteList(address addr, bool enable) public onlyOwner {
        _feeWhiteList[addr] = enable;
    }

    function batchSetFeeWhiteList(address [] memory addr, bool enable) public onlyOwner {
        for (uint i = 0; i < addr.length; i++) {
            _feeWhiteList[addr[i]] = enable;
        }
    }

    function setBlackList(address addr, bool enable) public onlyOwner {
        _isBlacklist[addr] = enable;
    }

    function batchSetBlackList(address [] memory addr, bool enable) public onlyOwner {
        for (uint i = 0; i < addr.length; i++) {
            _isBlacklist[addr[i]] = enable;
        }
    }

    function setSwapPairList(address addr, bool enable) public onlyOwner {
        _swapPairList[addr] = enable;
    }

    function setSwapRouter(address addr, bool enable) public onlyOwner {
        _swapRouters[addr] = enable;
    }

    function claimBalance(uint256 amount) public onlyOwner {
        payable(msg.sender).transfer(amount);
    }

    function claimToken(address token, uint256 amount) public onlyOwner {
        IERC20(token).transfer(msg.sender, amount);
    }

    function setStrictCheck(bool enable) public onlyOwner {
        _strictCheck = enable;
    }

    function setOpenSwap() public onlyOwner(){
        openSwap=!openSwap;
    }

    function setBuyFee(
        uint256 _setBuyDestory,
        uint256 _setBuyLp,
        uint256 _setBuyFound,
        uint256 _setBuyFlowBack
    ) public onlyOwner {
        _buyDestroyFee = _setBuyDestory;
        _buyLpshareFee = _setBuyLp;
        _buyFoundFee = _setBuyFound;
        _buyFlowBackFee = _setBuyFlowBack;
    }

    function setSellFee(
        uint256 _setSellDestory,
        uint256 _setSellLp,
        uint256 _setSellFound,
        uint256 _setSellFlowBack
    ) public onlyOwner {
        _sellDestoryFee = _setSellDestory;
        _sellLpShareFee = _setSellLp;
        _sellFoundFee = _setSellFound;
        _sellFlowBackFee = _setSellFlowBack;
    }

    function _swapAndAddLiquidity(uint stakeAmount) internal lockTheSwap returns (bool) {
        uint stakingTokenAmount = stakeAmount / 2;
        uint balanceBefore = IERC20(USDTAddress).balanceOf(address(this));
        _swapCakeForToken(stakingTokenAmount);
        uint swapedAmount = IERC20(USDTAddress).balanceOf(address(this)) - balanceBefore;
        _addLiquidity(swapedAmount, stakingTokenAmount);
        return true;
    }

    function _swapCakeForToken(
        uint256 amount
    ) internal returns (bool) {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = address(USDTAddress);
        _swapRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            amount,
            0,
            path,
            address(recv),
            MAX
        );
        recv.withdraw();
        return true;
    }

    function _addLiquidity(
        uint256 uAmount,
        uint256 lpAmount
    ) internal returns (bool) {
        _swapRouter.addLiquidity(
            address(USDTAddress),
            address(this),
            uAmount,
            lpAmount,
            0,
            0,
            address(0xdead),
            MAX
        );
        return true;
    }
}
