    "contracts/CDUToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

// import "@openzeppelin/contracts/utils/Strings.sol";

contract CDUToken is ERC20, Ownable {
    // using Strings for uint256;
    using SafeERC20 for IERC20;
    uint256 public constant MAX_SUPPLY = 105_000_000_000_000 ether;
    uint256 constant BASE_COEFFICIENT = 100000000;
    uint256 constant FIRST_STAGE = 1000000000000;
    uint256 constant PER_MAX_MINT = 100000000000 ether;
    // address public founder = 0x3A17f6395B933dbB9Eb80AcfAB9D3F397b91D4D8;
    address public founder = 0x0780cbe8293C6578Fd9C8E312d9915441D5bb883;
    address public coFounder = 0x0780cbe8293C6578Fd9C8E312d9915441D5bb883;
    address public addressCMO;
    uint256 public cmoRewardRatio = 0; // 100/10000 = 1%

    address public addressUSDX;

    address public uniswapPairAddress;
    address public uniswapV2Router;
    mapping(string => address) public partnerMap;

    event Mint(
        address indexed sender,
        string partnerName,
        address indexed partner,
        address indexed promoter,
        uint256 mintAmount,
        uint256 usdxAmount,
        uint256 mintPrice
    );
    event Gather(address indexed to, uint256 cduAmount, uint256 usdxAmount);

    event CreatePool(address owner, address pairAddress);

    event Airdrop(uint256 amount, uint256 leftAmount);
    event CDUReward(
        uint256 total,
        uint256 coFounderAmount,
        uint256 cmoAmount,
        uint256 ownerAmount
    );
    event AddBlackHole(
        address pair,
        uint256 amount0,
        uint256 amount1,
        uint256 price
    );
    event AddedLiquidity(
        uint256 cduAmount,
        uint256 usdxAmount,
        uint256 currentPrice,
        address pairAddress
    );

    constructor(
        address initialOwner,
        address _usdxAddress,
        address _uniswapV2Router
    ) ERC20("Clear_Debt_USD", "CDU") Ownable(initialOwner) {
        addressUSDX = _usdxAddress;
        uniswapV2Router = _uniswapV2Router;
        // ready for the airdrop
        _mint(address(this), 5000000000000 ether);
    }

    function addPartner(
        string calldata account,
        address addr
    ) public onlyOwner {
        partnerMap[account] = addr;
    }

    function removePartner(string calldata account) public onlyOwner {
        delete partnerMap[account];
    }

    function setFounder(address addr) public onlyOwner {
        founder = addr;
    }

    function airdrop(
        uint256 totalAmount,
        address[] calldata consultantList
    ) public onlyOwner {
        require(
            balanceOf(address(this)) >= totalAmount,
            "Airdrop Amount exceed balance"
        );
        address[] memory tempList = consultantList;
        uint256 len = tempList.length;
        uint256 amount = totalAmount / len;
        for (uint256 i = 0; i < tempList.length; i++) {
            _transfer(address(this), tempList[i], amount);
        }
        emit Airdrop(totalAmount, balanceOf(address(this)));
        // Maybe not needed
        // resetConsultant();
    }

    function setPairAddress(address pair) public onlyOwner {
        uniswapPairAddress = pair;
    }

    function setCMO(address _addressCMO, uint256 _rewardCMO) public onlyOwner {
        require(_addressCMO != address(0), "Invalid CMO address");
        require(_rewardCMO <= 1000, "Max ratio is 10%");
        addressCMO = _addressCMO;
        cmoRewardRatio = _rewardCMO;
    }

    function mint(string calldata partner, address promoter) public {
        require(uniswapPairAddress != address(0), "Uniswap pool not ready");
        IERC20 usdxContract = IERC20(addressUSDX);
        uint256 usdxAmount = usdxContract.allowance(msg.sender, address(this));
        require(usdxAmount > 0, "Deposit USDT must larger than 0");

        uint256 _totalSupply = totalSupply();
        require(
            _totalSupply <= MAX_SUPPLY,
            "Total mint quantity exceeds maximum"
        );

        uint256 mintPrice = _getCurrentPrice(_totalSupply);

        uint256 shouldMintNumber = usdxAmount * mintPrice;
        require(
            shouldMintNumber <= PER_MAX_MINT,
            "Exceeds the maximum value per mint of 100 billion"
        );

        // mint cdu to sender
        _mint(msg.sender, shouldMintNumber);

        giveCDUAwards(shouldMintNumber, promoter);

        // all usdx to contract address
        usdxContract.safeTransferFrom(msg.sender, address(this), usdxAmount);

        giveUSDXAwards(usdxAmount, partner, promoter);

        // 50% to black hole, and 20% to add liquidity
        depositToLP(usdxAmount);

        emit Mint(
            msg.sender,
            partner,
            partnerMap[partner],
            promoter,
            shouldMintNumber,
            usdxAmount,
            mintPrice
        );
    }

    function giveCDUAwards(uint256 shouldMintNumber, address promoter) private {
        // give 5% cdu to promoter
        if (promoter != address(0)) {
            _mint(promoter, shouldMintNumber / 20);
        }
        // give 5% cdu to contract, use for airdrop
        _mint(address(this), shouldMintNumber / 20);

        // 5% usdx to coFounder
        _mint(coFounder, shouldMintNumber / 20);
        // usdx to cmo
        if (addressCMO != address(0)) {
            _mint(addressCMO, (shouldMintNumber * cmoRewardRatio) / 10000);
        }

        // give left cdu to owner， 20% - cmo - coFounder
        uint256 leftNum = (shouldMintNumber * 2000) -
            (shouldMintNumber * cmoRewardRatio) -
            (shouldMintNumber * 500);
        _mint(address(this), leftNum / 10000);
        emit CDUReward(
            shouldMintNumber,
            shouldMintNumber / 20,
            (shouldMintNumber * cmoRewardRatio) / 10000,
            leftNum / 10000
        );
    }

    function giveUSDXAwards(
        uint256 usdxAmount,
        string calldata partner,
        address promoter
    ) private {
        IERC20 usdxContract = IERC20(addressUSDX);
        // 5% usdx to coFounder
        usdxContract.safeTransfer(coFounder, usdxAmount / 20);
        // usdx to cmo
        if (addressCMO != address(0)) {
            usdxContract.safeTransfer(
                addressCMO,
                (usdxAmount * cmoRewardRatio) / 10000
            );
        }

        // 5% usdx to partner
        if (partnerMap[partner] != address(0)) {
            // usdxContract.approve(address(this), partner, usdxAmount / 20);
            usdxContract.safeTransfer(partnerMap[partner], usdxAmount / 20);
        }
        // 5% usdx to promoter
        if (promoter != address(0)) {
            // usdxContract.approve(address(this), promoter, usdxAmount / 20);
            usdxContract.safeTransfer(promoter, usdxAmount / 20);
        }
    }

    function depositToLP(uint256 usdxAmount) private {
        IERC20 usdxContract = IERC20(addressUSDX);
        uint256 afterMintTotalSupply = totalSupply();

        uint256 priceAfterMint = _getCurrentPrice(afterMintTotalSupply);

        // 50% to LP black hole
        uint256 usdxToLP = (usdxAmount * 5) / 10;
        uint256 cduToLP = usdxToLP * priceAfterMint;

        _mint(uniswapPairAddress, cduToLP);

        usdxContract.safeTransfer(uniswapPairAddress, usdxToLP);

        IUniswapV2Pair(uniswapPairAddress).sync();
        emit AddBlackHole(
            uniswapPairAddress,
            cduToLP,
            usdxToLP,
            priceAfterMint
        );

        // 20% add LP to owner
        uint256 usdxToAddLP = (usdxAmount * 2) / 10;
        _addLiquidity(usdxToAddLP);
    }

    function getPriceInCDU() public view returns (uint256) {
        uint256 _totalSupply = totalSupply();
        return _getCurrentPrice(_totalSupply);
    }

    function getPriceInUniswap() public view returns (uint256 price) {
        IUniswapV2Pair pair = IUniswapV2Pair(uniswapPairAddress);
        (uint112 reserve0, uint112 reserve1, ) = pair.getReserves();
        // address token0 = pair.token0();
        price = reserve1 / reserve0;
    }

    function _getCurrentPrice(
        uint256 _totalSupply
    ) public pure returns (uint256) {
        _totalSupply = _totalSupply / 10 ** 18 - 5000000000000;

        uint256 numerator = BASE_COEFFICIENT * 10 ** 18;
        uint256 denominator = (10 ** 18 / 10) +
            ((_totalSupply * 10 ** 18) / FIRST_STAGE);
        return numerator / denominator;
    }

    function gather(
        address _to,
        uint256 cduAmount,
        uint256 usdxAmount
    ) external onlyOwner {
        IERC20 usdxContract = IERC20(addressUSDX);
        uint256 usdxBalance = usdxContract.balanceOf(address(this));
        uint256 cduBalance = balanceOf(address(this));
        require(cduAmount <= cduBalance, "Exceeds CDU balance");
        require(usdxAmount <= usdxBalance, "Exceeds USDT balance");
        if (cduAmount > 0) {
            _transfer(address(this), _to, cduAmount);
        }
        if (usdxAmount > 0) {
            usdxContract.safeTransfer(_to, usdxAmount);
        }

        emit Gather(_to, cduBalance, usdxAmount);
    }

    function createUniswapPool() public onlyOwner returns (address) {
        IUniswapV2Factory factory = IUniswapV2Factory(
            IUniswapV2Router02(uniswapV2Router).factory()
        );
        address pairAddress = factory.createPair(address(this), addressUSDX);
        setPairAddress(pairAddress);
        emit CreatePool(owner(), pairAddress);
        return pairAddress;
    }

    function addLiquidity(uint256 usdxAmount) public onlyOwner {
        _addLiquidity(usdxAmount);
    }

    function _addLiquidity(uint256 usdxAmount) private {
        IERC20 usdxContract = IERC20(addressUSDX);
        uint256 currentPrice = _getCurrentPrice(totalSupply());

        uint256 cduAmount = usdxAmount * currentPrice;

        require(
            usdxContract.balanceOf(address(this)) >= usdxAmount,
            "Less than the minimum amount required to create a liquidity pool"
        );
        _mint(address(this), cduAmount);

        _approve(address(this), uniswapV2Router, cduAmount);
        usdxContract.approve(uniswapV2Router, usdxAmount);
        IUniswapV2Router02(uniswapV2Router).addLiquidity(
            address(this),
            addressUSDX,
            cduAmount,
            usdxAmount,
            0,
            0,
            founder, // LP tokens are minted to this contract
            block.timestamp + 30 // Deadline
        );

        emit AddedLiquidity(
            cduAmount,
            usdxAmount,
            currentPrice,
            uniswapPairAddress
        );
    }
}
"
