    "/contracts/LockerRegistry.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.14;

import { ISuperfluid, SuperAppDefinitions } from "@streamable-finance/ethereum-contracts/contracts/interfaces/superfluid/ISuperfluid.sol";

import { Initializable } from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import { Clones } from "@openzeppelin/contracts/proxy/Clones.sol";

import { ILockerRegistry } from "./interfaces/ILockerRegistry.sol";
import { ILockingSchedulerAutomationInitializer } from "./interfaces/ILockingSchedulerAutomation.sol";

/**
 * @title Locker registry contract
 * @author Streampay
 */
contract LockerRegistry is ILockerRegistry, Initializable {
    /* ========== LIBRARIES ========== */

    using Clones for address;

    /* ========== STATE VARIABLES ========== */

    mapping(bytes32 => address) internal lockers; // id = keccak(owner, version)
    address internal currentLockerImplementation;
    uint16 internal currentLockerVersion;

    ISuperfluid internal host;
    address payable internal gelatoAutomate;
    uint256 internal gelatoFee;
    address internal admin;

    /* ========== CONSTRUCTOR ========== */

    function initialize(
        address _implementation,
        ISuperfluid _host,
        address payable _automate,
        uint256 _fee,
        address _admin
    ) external initializer {
        if (
            _implementation == address(0) ||
            address(_host) == address(0) ||
            _automate == address(0) ||
            _admin == address(0)
        ) revert ZeroAddress();
        if (_fee == 0) revert ZeroFee();

        currentLockerImplementation = _implementation;
        currentLockerVersion = 1;

        host = _host;
        gelatoAutomate = _automate;
        gelatoFee = _fee;
        admin = _admin;
    }

    /* ========== MODIFIERS ========== */

    modifier onlyAdmin() {
        if (msg.sender != admin) revert NotAdmin();
        _;
    }

    /* ========== EXTERNAL FUNCTIONS ========== */

    function createLocker() external returns (address) {
        if (getLocker(msg.sender, currentLockerVersion) != address(0))
            revert LockerAlreadyExists();

        ILockingSchedulerAutomationInitializer locker = ILockingSchedulerAutomationInitializer(
                currentLockerImplementation.clone()
            );
        locker.initialize(host, gelatoAutomate, gelatoFee, admin, msg.sender);

        address lockerAddress = address(locker);
        lockers[
            getConfigHash(msg.sender, currentLockerVersion)
        ] = lockerAddress;

        // Superfluid SuperApp registration. This is a dumb SuperApp, only for front-end tx batch calls.
        uint256 configWord = SuperAppDefinitions.APP_LEVEL_FINAL |
            SuperAppDefinitions.BEFORE_AGREEMENT_CREATED_NOOP |
            SuperAppDefinitions.AFTER_AGREEMENT_CREATED_NOOP |
            SuperAppDefinitions.BEFORE_AGREEMENT_UPDATED_NOOP |
            SuperAppDefinitions.AFTER_AGREEMENT_UPDATED_NOOP |
            SuperAppDefinitions.BEFORE_AGREEMENT_TERMINATED_NOOP |
            SuperAppDefinitions.AFTER_AGREEMENT_TERMINATED_NOOP;
        host.registerAppByFactory(locker, configWord);

        emit LockerCreated(lockerAddress);

        return lockerAddress;
    }

    /* ========== EXTERNAL ADMIN FUNCTIONS ========== */

    function setNewLockerImplementation(
        address _implementation
    ) external onlyAdmin {
        if (_implementation == address(0)) revert ZeroAddress();
        currentLockerImplementation = _implementation;
        currentLockerVersion++;

        emit ImplementationUpdated(_implementation, currentLockerVersion);
    }

    function setHost(ISuperfluid _host) external onlyAdmin {
        if (address(_host) == address(0)) revert ZeroAddress();
        host = _host;

        emit HostUpdated(address(_host));
    }

    function setGelatoAutomate(address payable _automate) external onlyAdmin {
        if (_automate == address(0)) revert ZeroAddress();
        gelatoAutomate = _automate;

        emit GelatoAutomateUpdated(_automate);
    }

    function setGelatoFee(uint256 _fee) external onlyAdmin {
        if (_fee == 0) revert ZeroFee();
        gelatoFee = _fee;

        emit GelatoFeeUpdated(_fee);
    }

    function setAdmin(address _admin) external onlyAdmin {
        if (_admin == address(0)) revert ZeroAddress();
        admin = _admin;

        emit AdminUpdated(_admin);
    }

    /* ========== EXTERNAL GETTER FUNCTIONS ========== */

    function getCurrentLockerImplementation() external view returns (address) {
        return currentLockerImplementation;
    }

    function getCurrentLockerVersion() external view returns (uint16) {
        return currentLockerVersion;
    }

    function getHost() external view returns (address) {
        return address(host);
    }

    function getGelatoAutomate() external view returns (address payable) {
        return gelatoAutomate;
    }

    function getGelatoFee() external view returns (uint256) {
        return gelatoFee;
    }

    function getAdmin() external view returns (address) {
        return admin;
    }

    /* ========== PUBLIC GETTER FUNCTIONS ========== */

    function getLocker(
        address _owner,
        uint16 _version
    ) public view returns (address) {
        return lockers[getConfigHash(_owner, _version)];
    }

    function getConfigHash(
        address _owner,
        uint16 _version
    ) public pure returns (bytes32 hashConfig) {
        return keccak256(abi.encodePacked(_owner, _version));
    }
}
"
