    event LockerCreated(address indexed lockerContract);
    event ImplementationUpdated(
        address indexed newImplementation,
        uint16 newVersion
    );
    event HostUpdated(address host);
    event GelatoAutomateUpdated(address gelatoAutoamte);
    event GelatoFeeUpdated(uint256 fee);
    event AdminUpdated(address admin);
}

