    /* ========== ERRORS ========== */

    error ZeroAddress();
    error ZeroFee();
    error NotAdmin();
    error LockerAlreadyExists();

    /* ========== FUNCTIONS ========== */

    function createLocker() external returns (address);

    function setNewLockerImplementation(address _implementation) external;

    function setHost(ISuperfluid _host) external;

    function setGelatoAutomate(address payable _automate) external;

    function setGelatoFee(uint256 _fee) external;

    function setAdmin(address _admin) external;

    function getCurrentLockerImplementation() external view returns (address);

    function getCurrentLockerVersion() external view returns (uint16);

    function getHost() external view returns (address);

    function getGelatoAutomate() external view returns (address payable);

    function getGelatoFee() external view returns (uint256);

    function getAdmin() external view returns (address);

    function getLocker(
        address _owner,
        uint16 _version
    ) external view returns (address);

    function getConfigHash(
        address _owner,
        uint16 _version
    ) external pure returns (bytes32 hashConfig);
}
