pragma solidity 0.8.19;

interface IOraclePrices {
    /// @notice Emitted when changing the address of the GQ price oracle
    event OraclePricesGQUpdated();

    /// @notice Emitted when changing the address of the Bamboo price oracle
    event OraclePricesBambooUpdated();

    /// @notice Emitted when changing the address of the BNB price oracle
    event OraclePricesBNBUpdated();

    /// @notice This function gets the amount of Bamboo to be paid for a certain amount of BUSD
    /// @param amount The amount in BUSD
    function convertBUSDToBAMBOO(uint256 amount) external returns (uint256);

    ///  This function gets the amount of BNB to be paid for a certain amount of BUSD. There is no need for update sin we use Chainlink's price feeds
    /// @param amount The amount in BUSD
    function getBNBAmountByStable(
        uint256 amount
    ) external view returns (uint256);

    /// @notice This function gets the amount of GQ to be paid for a certain amount of BUSD
    /// @param amount The amount in BUSD
    function convertBUSDToGQ(uint256 amount) external returns (uint256);
}
"
