pragma solidity 0.8.19;

interface IOraclePricesBNB {
    /// @notice Event emitted when the aggregator address is updated.
    /// @param sender The address that changes the aggregator address.
    /// @param newAgreggator The new aggregator address.
    event AggregatorChanged(address sender, address newAgreggator);

    /// @notice Event emitted when the lastUpdatedAtMargin is updated.
    /// @param sender The address that changes the lastUpdatedAtMargin value.
    /// @param lastUpdatedAtMargin The new lastUpdatedAtMargin value.
    event LastUpdatedAtMarginChanged(address sender, uint256 lastUpdatedAtMargin);

    /// @notice Change the aggregator address.
    /// @dev Only callable by the owner.
    /// @param newAggregator The aggregator address.
    function changeAggregator(address newAggregator) external;

    /// @notice Change the lastUpdatedAtMargin value.
    /// @dev Only callable by the owner.
    /// @param lastUpdatedAtMargin_ The new margin.
    function updateLastUpdatedAtMargin(uint256 lastUpdatedAtMargin_) external;

    /// @notice Returns the latest price of BNB in stable.
    function getBNBLatestPrice() external view returns (uint256);

    /// @notice Gets the price of BNB per stable.
    /// @param _amountInStable Amount in stable to convert.
    function getBNBAmountByStable(
        uint256 _amountInStable
    ) external view returns (uint256);

    /// @notice Gets the price of Stable per BNB.
    /// @param _amountInBNB Amount in BNB to convert.
    function getStableAmountByBNB(
        uint256 _amountInBNB
    ) external view returns (uint256);
