      "content": "// SPDX-License-Identifier: Unlicense
pragma solidity 0.8.19;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "./interfaces/IOraclePricesBNB.sol";
import "./interfaces/IOraclePricesBAMBOO.sol";
import "./interfaces/IOraclePricesGQ.sol";
import "./interfaces/IOraclePrices.sol";

contract OraclePrices is Initializable, OwnableUpgradeable, IOraclePrices {
    /// @notice The Bamboo oracle address
    IOraclePricesBAMBOO public oraclePricesBamboo;

    /// @notice The BNB oracle address
    IOraclePricesBNB public oraclePricesBNB;

    /// @notice The GQ oracle address
    IOraclePricesGQ public oraclePricesGQ;

    /// @notice The address of BUSD
    address public busdToken;

    //============== CONSTRUCTOR ==============
    // constructor() {
    //     _disableInitializers();
    // }

    //============== INITIALIZE ==============
    function initialize(
        IOraclePricesBAMBOO oraclePricesBamboo_,
        IOraclePricesBNB oraclePricesBNB_,
        IOraclePricesGQ oraclePricesGQ_,
        address busdToken_
    ) public initializer {
        __Ownable_init();
        oraclePricesBamboo = oraclePricesBamboo_;
        oraclePricesBNB = oraclePricesBNB_;
        oraclePricesGQ = oraclePricesGQ_;
        busdToken = busdToken_;
    }

    /// @notice Changed the GQ price oracle address
    /// @param newAddress The new address
    function changeOraclePricesGQ(IOraclePricesGQ newAddress) external {
        require(address(newAddress) != address(0), "Zero address");
        oraclePricesGQ = newAddress;
        emit OraclePricesGQUpdated();
    }

     /// @notice Changed the BAMBOO price oracle address
    /// @param newAddress The new address
    function changeOraclePricesBamboo(IOraclePricesBAMBOO newAddress) external {
        require(address(newAddress) != address(0), "Zero address");
        oraclePricesBamboo = newAddress;
        emit OraclePricesBambooUpdated();
    }

    /// @notice Changed the BNB price oracle address
    /// @param newAddress The new address
    function changeOraclePricesBNB(IOraclePricesBNB newAddress) external {
        require(address(newAddress) != address(0), "Zero address");
        oraclePricesBNB = newAddress;
        emit OraclePricesBNBUpdated();
    }

    /// @inheritdoc IOraclePrices
    function convertBUSDToBAMBOO(uint256 amount) external view returns (uint256) {
        return oraclePricesBamboo.consult(busdToken, amount);
    }

    /// @inheritdoc IOraclePrices
    function getBNBAmountByStable(
        uint256 amount
    ) external view returns (uint256) {
        return oraclePricesBNB.getBNBAmountByStable(amount);
    }

    /// @inheritdoc IOraclePrices
    function convertBUSDToGQ(uint256 amount) external view returns (uint256) {
        return oraclePricesGQ.consult(busdToken, amount);
    }
}
