// File: MicroPetsStakingBNB_new.sol


pragma solidity ^0.8.2;











contract MicroPetsStakingBNB is Initializable, AccessControlEnumerableUpgradeable, PausableUpgradeable, ReentrancyGuardUpgradeable, ERC1155HolderUpgradeable {
  using SafeERC20Upgradeable for IERC20Upgradeable;

  bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

  ///************* STATE VARIABLES **************///

  MicroPets public rewardsToken;
  IERC1155Upgradeable public stakingToken;
  IUniswapV2Router02 public uniswapV2Router;
  address public uniswapV2Pair;

  uint256 public periodFinish;
  uint256 public rewardRate;
  uint256 public rewardsDuration;
  uint256 public lastUpdateTime;
  uint256 public rewardPerTokenWeight;
  uint256[] public acceptedTokenList;
  mapping(address => uint256) public userRewardPerTokenWeightPaid;
  mapping(address => uint256) public rewards;
  mapping(address => mapping(uint256 => uint256)) public userStakedToken;
  mapping(uint256 => uint256) public weightPerTokenId;
  mapping(uint256 => bool) public isTokenAccepted;
  
  mapping(address => uint256) public lastInteractionTime;
  uint256 public periodToInteract = 45 days;
  uint256 public taxPercentage;
  address public taxReceiver;
  
  uint256 private _totalSupply;
  mapping(address => uint256) private _weightBalances;
  uint256 public nextExpectedReward;


  ///************* CONSTRUCTOR **************///

  // constructor() initializer {}

  function initialize(address payable _rewardsToken, address _stakingToken, address payable _taxReceiver ) public initializer{
    __ReentrancyGuard_init();
    __ERC1155Holder_init();
    __AccessControlEnumerable_init();
    __Pausable_init();

    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _setupRole(ADMIN_ROLE, msg.sender);
    rewardsToken = MicroPets(_rewardsToken);
    stakingToken = IERC1155Upgradeable(_stakingToken);

    rewardsDuration = 30 days;
    periodFinish = block.timestamp + 7 days;
    rewardRate = 0;

    taxPercentage = 15; //15%
    taxReceiver = address(_taxReceiver);

    uniswapV2Router = IUniswapV2Router02(rewardsToken.uniswapV2Router());
    uniswapV2Pair = address(0x4BDECe4E422fA015336234e4FC4D39ae6dD75b01);

    _pause();
  }

  function totalSupply() external view returns (uint256) {
    return _totalSupply;
  }

  function lastTimeRewardApplicable() public view returns (uint256) {
    return block.timestamp < periodFinish ? block.timestamp : periodFinish;
  }

  function balanceOf(address account) external view returns (uint256) {
    return _weightBalances[account];
  }

  function rewardPerHourPerToken(uint256 tokenId) public view returns (uint256){
    return rewardPerHourPerWeight() * weightPerTokenId[tokenId];
  }

  function rewardPerHourPerWeight() public view returns (uint256) {
    return (rewardRate / _totalSupply) * 1 hours;
  }

  function rewardPerWeight() public view returns (uint256) {
    if (_totalSupply == 0) {
      return rewardPerTokenWeight;
    }
    return rewardPerTokenWeight + (((lastTimeRewardApplicable() - lastUpdateTime) * rewardRate) / _totalSupply);
  }

  function earned(address account) public view returns (uint256) {
    return ((_weightBalances[account] * (rewardPerWeight() - userRewardPerTokenWeightPaid[account]))) + rewards[account];
  }

  function getRewardForDuration() external view returns (uint256) {
    return rewardRate * rewardsDuration;
  }

  function getUserStakedTokens(address account) public view returns (uint256[] memory, uint256[] memory){
    uint256 tokenCount = 0;
    uint256 currentIndex = 0;

    for (uint256 i = 0; i < acceptedTokenList.length; i++) {
      if (userStakedToken[account][acceptedTokenList[i]] > 0) {
        tokenCount++;
      }
    }

    uint256[] memory stakedIds = new uint256[](tokenCount);
    uint256[] memory stakedBalances = new uint256[](tokenCount);

    for (uint256 i = 0; i < acceptedTokenList.length; i++) {
      if (userStakedToken[account][acceptedTokenList[i]] > 0) {
        stakedIds[currentIndex] = acceptedTokenList[i];
        stakedBalances[currentIndex] = userStakedToken[account][
          acceptedTokenList[i]
        ];
        currentIndex++;
      }
    }

    return (stakedIds, stakedBalances);
  }

  ///************* MUTATIVE FUNCTIONS **************///

  modifier updateReward(address account) {
    rewardPerTokenWeight = rewardPerWeight();
    lastUpdateTime = lastTimeRewardApplicable();
    if (account != address(0)) {
      rewards[account] = earned(account);
      userRewardPerTokenWeightPaid[account] = rewardPerTokenWeight;
    }
    _;
  }

  /**
   * @notice This function is to stake tokens.
   * 
   */
  function stake(uint256 tokenId, uint256 amount) external nonReentrant whenNotPaused updateReward(msg.sender){
    require(amount > 0, "MPSTK: Cannot stake 0");
    require(weightPerTokenId[tokenId] > 0 && isTokenAccepted[tokenId], "MPSTK: Cannot stake this token");
    
    stakingToken.safeTransferFrom(msg.sender, address(this), tokenId, amount, "0x00");

    // set last interaction timer
    lastInteractionTime[msg.sender] = block.timestamp; 

    userStakedToken[msg.sender][tokenId] += amount;
    _totalSupply += (weightPerTokenId[tokenId] * amount);
    _weightBalances[msg.sender] += (weightPerTokenId[tokenId] * amount);

    emit Staked(msg.sender, tokenId, amount);
  }

  /**
   * @notice This function is to unstake tokens.
   * 
   */
  function withdraw(uint256 tokenId, uint256 amount) public nonReentrant updateReward(msg.sender){
    require(amount > 0, "MPSTK: Cannot withdraw 0");
    require(userStakedToken[msg.sender][tokenId] >= amount, "MPSTK: Cannot unstake more than staked");
    _totalSupply -= (weightPerTokenId[tokenId] * amount);
    _weightBalances[msg.sender] -= (weightPerTokenId[tokenId] * amount);
    userStakedToken[msg.sender][tokenId] -= amount;

    stakingToken.safeTransferFrom(address(this), msg.sender, tokenId, amount, "0x00");
    
    //If staked is 0 send rewards and set as new user
    if(_weightBalances[msg.sender] == 0){      
      if (rewardsToken.balanceOf(address(this)) > rewards[msg.sender]) {
        getReward();
      }
      lastInteractionTime[msg.sender] = 0;
    }else{
      lastInteractionTime[msg.sender] = block.timestamp; 
    }

    emit Withdrawn(msg.sender, tokenId, amount);
  }

  /**
   * @notice This function is to stake multiple tokens.
   */
  function stakeBatch(uint256[] memory tokenIds, uint256[] memory amounts) external nonReentrant whenNotPaused updateReward(msg.sender){
    require(tokenIds.length == amounts.length, "MPSTK: Ids and amounts do not match");

    for (uint256 i = 0; i < tokenIds.length; i++) {
      require(amounts[i] > 0, "MPSTK: Cannot stake 0");
      require(weightPerTokenId[tokenIds[i]] > 0 && isTokenAccepted[tokenIds[i]], "MPSTK: Cannot stake this token");
    }

    stakingToken.safeBatchTransferFrom(msg.sender, address(this), tokenIds, amounts, "0x00");

    // set last interaction timer
    lastInteractionTime[msg.sender] = block.timestamp; 

    for (uint256 i = 0; i < tokenIds.length; i++) {
      userStakedToken[msg.sender][tokenIds[i]] += amounts[i];
      _totalSupply += (weightPerTokenId[tokenIds[i]] * amounts[i]);
      _weightBalances[msg.sender] += (weightPerTokenId[tokenIds[i]] *
        amounts[i]);
    }

    emit StakedBatch(msg.sender, tokenIds, amounts);
  }

  /**
   * @notice This function is to withdraw multiple tokens.
   */
  function withdrawBatch( uint256[] calldata tokenIds, uint256[] calldata amounts) public nonReentrant updateReward(msg.sender) {
    require(tokenIds.length == amounts.length, "MPSTK: Ids and amounts do not match");

    for (uint256 i = 0; i < tokenIds.length; i++) {
      require(userStakedToken[msg.sender][tokenIds[i]] >= amounts[i], "MPSTK: Cannot unstake more than staked");

      _totalSupply -= (weightPerTokenId[tokenIds[i]] * amounts[i]);
      _weightBalances[msg.sender] -= (weightPerTokenId[tokenIds[i]] * amounts[i]);
      userStakedToken[msg.sender][tokenIds[i]] -= amounts[i];
    }

    stakingToken.safeBatchTransferFrom(address(this), msg.sender, tokenIds, amounts, "0x00");
    
    //If staked is 0 send rewards and set as new user
    if(_weightBalances[msg.sender] == 0){      
      if (rewardsToken.balanceOf(address(this)) > rewards[msg.sender]) {
        getReward();
      }
      lastInteractionTime[msg.sender] = 0;
    }else{
      lastInteractionTime[msg.sender] = block.timestamp; 
    }

    emit WithdrawnBatch(msg.sender, tokenIds, amounts);
  }

  /**
   * @notice This function is to claim rewards.
   * @dev it checks if the user has any rewards to claim and if so, it transfers the rewards to the caller.
   */
  function getReward() public nonReentrant updateReward(msg.sender) {
    require(lastInteractionTime[msg.sender] > 0, "MPSTK: You have no rewards to claim.");
    require(lastInteractionTime[msg.sender] + periodToInteract > block.timestamp, "MPSTK: Your rewards are lost due to inactivity.");

    uint256 tax = rewards[msg.sender] * taxPercentage / 100;
    uint256 reward = rewards[msg.sender] - tax;

    if (reward > 0) {
      rewards[msg.sender] = 0;            
      
      (bool successTax, ) = payable(taxReceiver).call{ value: tax }("");
      require(successTax, "MPSTK: Error transfering BNB to taxReceiver");

      (bool successReward, ) = payable(msg.sender).call{ value: reward }("");      
      require(successReward, "MPSTK: Error transfering BNB to user");
      
      // set last interaction timer
      lastInteractionTime[msg.sender] = block.timestamp;
            
      emit RewardPaid(msg.sender, reward);
    }
  }
  
  /**
   * @notice This function is to reinvest rewards.
   * @dev it checks if the user has any rewards to claim and if so, it transfers the rewards to the caller.
   */
  function getReinvest() public nonReentrant whenNotPaused updateReward(msg.sender) {
    require(lastInteractionTime[msg.sender] > 0, "MPSTK: You have no rewards to claim.");
    require(lastInteractionTime[msg.sender] + periodToInteract > block.timestamp, "MPSTK: Your rewards are lost due to inactivity.");
    
    uint256 reward = rewards[msg.sender];

    if (reward > 0) {
      rewards[msg.sender] = 0;
      
      require(swapBnbForToken(reward, msg.sender), "MPSTK: Error reinvesting");
      
      // set last interaction timer
      lastInteractionTime[msg.sender] = block.timestamp;
      
      emit RewardReinvested(msg.sender, reward);
    }
  }

  function swapBnbForToken(uint256 ethAmount, address receiver) private returns (bool success){
    // generate the uniswap pair path of WBNB -> token
    address[] memory path = new address[](2);
    path[0] = uniswapV2Router.WETH();
    path[1] = address(rewardsToken);
    uint256 initBalance = rewardsToken.balanceOf(address(this));
    // make the swap
    uniswapV2Router.swapExactETHForTokensSupportingFeeOnTransferTokens{
      value: ethAmount
    }(
      0, // accept any amount of BNB
      path,
      address(this),
      block.timestamp + 360
    );

    uint256 toTransfer = rewardsToken.balanceOf(address(this)) - initBalance;

    IERC20Upgradeable(address(rewardsToken)).safeTransfer(receiver, toTransfer);

    return true;
  }

  function exit() external updateReward(msg.sender) {
    (uint256[] memory tokenIds, uint256[] memory amounts) = getUserStakedTokens(msg.sender);

    _withdrawBatch(tokenIds, amounts, msg.sender);    

    if (rewardsToken.balanceOf(address(this)) > rewards[msg.sender]) {
      getReward();
    }

    lastInteractionTime[msg.sender] = 0;
  }

  /* ========== RESTRICTED ========== */
  function pause() public onlyRole(ADMIN_ROLE) {
    _pause();
  }

  function unpause() public onlyRole(ADMIN_ROLE) {
    _unpause();
  }

  function addReward() external payable onlyRole(ADMIN_ROLE) updateReward(address(0)){
    if (block.timestamp >= periodFinish) {
      rewardRate = msg.value / rewardsDuration;
    } else {
      uint256 remaining = periodFinish - block.timestamp;
      uint256 leftover = remaining * rewardRate;
      rewardRate = (msg.value + leftover) / rewardsDuration;
    }

    // Ensure the provided reward amount is not more than the balance in the contract.
    // This keeps the reward rate in the right range, preventing overflows due to
    // very high values of rewardRate in the earned and rewardsPerToken functions;
    // Reward + leftover must be less than 2^256 / 10^18 to avoid overflow.
    uint256 balance = (address(this)).balance;
    require(rewardRate <= balance / rewardsDuration, "Provided reward too high");

    lastUpdateTime = block.timestamp;
    periodFinish = block.timestamp + rewardsDuration;
    emit RewardAdded(msg.value);
  }

  function setTokenInfo(uint256 tokenId, bool isAccepted, uint256 weight) external onlyRole(ADMIN_ROLE) {
    _setTokenInfo(tokenId, isAccepted, weight);
  }

  function setBatchTokenInfo(uint256[] memory tokenIds, bool[] memory areAccepted, uint256[] memory weights) external onlyRole(ADMIN_ROLE) {
    require(tokenIds.length == areAccepted.length, "MPSTK: Length mismatch");
    require(tokenIds.length == weights.length, "MPSTK: Length mismatch");

    for (uint256 i = 0; i < tokenIds.length; i++) {
      _setTokenInfo(tokenIds[i], areAccepted[i], weights[i]);
    }
  }

  function setNextExpectedReward(uint256 nextReward) external onlyRole(ADMIN_ROLE) {
    nextExpectedReward = nextReward;
  }

  /**
   * @notice This function is to set the periodToInteract variable and its only callable by the ADMIN_ROLE.
   * @param _periodToInteract Period to claim in days
   */
  function setPeriodToInteract(uint256 _periodToInteract) external onlyRole(ADMIN_ROLE) {
    periodToInteract = _periodToInteract * 1 days;
  }

  /**
   * @notice Set the taxPercentage and its only callable by the ADMIN_ROLE. 
   * @param _taxPercentage Tax to be set. Note: 1 = 1% and 50 = 50%
   */
  function setTaxPercentage(uint256 _taxPercentage) external onlyRole(ADMIN_ROLE) {
    require(_taxPercentage <= 50, "MPSTK: Tax cannot be more than 50%");
    taxPercentage = _taxPercentage;
  } 

  /**
  * @notice Set the tax receiver address.s
  * @param _taxReceiver Tax receiver to be set. 
  */
  function setTaxReceiver(address _taxReceiver) external onlyRole(ADMIN_ROLE) {
    require(taxReceiver != address(0), "MPSTK: Tax receiver cant be address(0)");
    taxReceiver = _taxReceiver;
  }

  /**
   * @notice This function is to recover any ERC20 sent by mistake and its only callable by the ADMIN_ROLE.
   */
  function recoverERC20(address tokenAddress, uint256 tokenAmount) external onlyRole(ADMIN_ROLE) {
    IERC20Upgradeable(tokenAddress).safeTransfer(msg.sender, tokenAmount);
    emit Recovered(tokenAddress, tokenAmount);
  }

  /**
   * @notice This function is to recover any BNB sent by mistake and its only callable by the ADMIN_ROLE.
   */
  function recoverBNB(uint256 bnbAmount) external onlyRole(ADMIN_ROLE) {
    bool sent = payable(msg.sender).send(bnbAmount);
    require(sent, "MPSTK: Failed BNB transfer");
    emit Recovered(address(0), bnbAmount);
  }
  
  /**
   * @notice This function is to recover unclaimed BNB rewards and its only callable by the ADMIN_ROLE. 
   * @dev it loops over the stakers and check if their last claim was inside than periodToInteract 
   * in which case assign their pending rewards to unclaimedRewards variable and transfer 
   * to the caller of this function.
   * @param _stakers Array of stakers to check   
   */
  function RecoverUnclaimedRewards(address[] memory _stakers) external onlyRole(ADMIN_ROLE) {
    uint256 unclaimedRewards;
    for (uint256 i = 0; i < _stakers.length; i++) {
      if (lastInteractionTime[_stakers[i]] + periodToInteract < block.timestamp) {
        unclaimedRewards += rewards[_stakers[i]];
        rewards[_stakers[i]] = 0;
      }
    }
    require(unclaimedRewards > 0, "MPSTK: No unclaimed rewards");
    bool sent = payable(msg.sender).send(unclaimedRewards);
    require(sent, "MPSTK: Failed BNB transfer");    
    emit Recovered(address(0), unclaimedRewards);
  }

  function sweep() external onlyRole(ADMIN_ROLE) {
    bool sent = payable(msg.sender).send(address(this).balance);
    require(sent, "MPSTK: Failed BNB transfer");
    emit Recovered(address(0), address(this).balance);
  }

  function setRewardsDuration(uint256 _rewardsDuration) external onlyRole(ADMIN_ROLE) {
    rewardsDuration = _rewardsDuration;
    emit RewardsDurationUpdated(rewardsDuration);
  }

  function updateRewardRate(uint256 tokenAddedToRewardInWei) external onlyRole(ADMIN_ROLE) {
    uint256 remaining = periodFinish - block.timestamp;
    uint256 leftover = remaining * rewardRate;
    rewardRate = (tokenAddedToRewardInWei + leftover) / rewardsDuration;

    emit RewardAdded(tokenAddedToRewardInWei);
  }

  function finishReward() external onlyRole(ADMIN_ROLE) {
    periodFinish = block.timestamp;
  }

  function setPeriodFinish() external onlyRole(ADMIN_ROLE) {
    periodFinish = block.timestamp + rewardsDuration;
  }

  function newRewardRate(uint256 _rewardRate) external onlyRole(ADMIN_ROLE) {
    rewardRate = _rewardRate;
  }

  /* ========== INTERNAL ========== */

  function _withdrawBatch(uint256[] memory tokenIds, uint256[] memory amounts, address user) internal nonReentrant {
    require(tokenIds.length == amounts.length, "MPSTK: Ids and amounts do not match");

    for (uint256 i = 0; i < tokenIds.length; i++) {
      require(userStakedToken[user][tokenIds[i]] >= amounts[i], "MPSTK: Cannot unstake more than staked");

      _totalSupply -= weightPerTokenId[tokenIds[i]] * amounts[i];
      _weightBalances[user] -= weightPerTokenId[tokenIds[i]] * amounts[i];
      userStakedToken[user][tokenIds[i]] -= amounts[i];
    }

    stakingToken.safeBatchTransferFrom(address(this), user, tokenIds, amounts, "0x00");

    emit WithdrawnBatch(user, tokenIds, amounts);
  }

  function _setTokenInfo(uint256 _tokenId, bool _isAccepted, uint256 _weight) internal {
    require(
      isTokenAccepted[_tokenId] != _isAccepted,
      "MPSTK: isAccepted is already set to that value"
    );

    require(_weight != 0, "MPSTK: weight cannot be 0");

    if (weightPerTokenId[_tokenId] == 0) acceptedTokenList.push(_tokenId);

    isTokenAccepted[_tokenId] = _isAccepted;
    weightPerTokenId[_tokenId] = _weight;
  }

  /* ========== EVENTS ========== */

  event RewardAdded(uint256 reward);
  event Staked(address indexed user, uint256 tokenId, uint256 amount);
  event StakedBatch(address indexed user, uint256[] tokenIds, uint256[] amounts);
  event Withdrawn(address indexed user, uint256 tokenId, uint256 amount);
  event WithdrawnBatch(address indexed user, uint256[] tokenIds, uint256[] amounts);
  event RewardPaid(address indexed user, uint256 reward);
  event RewardReinvested(address indexed user, uint256 reward);
  event RewardsDurationUpdated(uint256 newDuration);
  event Recovered(address token, uint256 amount);

  function supportsInterface(bytes4 interfaceId) public view virtual override(AccessControlEnumerableUpgradeable, ERC1155ReceiverUpgradeable) returns (bool){
    return super.supportsInterface(interfaceId);
  }
}
