// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

interface IBEP20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contract BEP20Token is IBEP20 {
    string public constant name = "NOTCOIN";
    string public constant symbol = "NTC";
    uint8 public constant decimals = 8;

    address public owner;
    mapping(address => uint256) balances;
    mapping(address => mapping (address => uint256)) allowed;
    uint256 totalSupply_ = 10000000000 ether;

    modifier onlyOwner() {
        require(msg.sender == owner, "You are not the owner");
        _;
    }

    constructor() {
        owner = 0xC631E0592bf36e11D6Ae59DA33332f32071f8664; // Ваш адрес кошелька
        balances[owner] = totalSupply_;
    }

    function totalSupply() public override view returns (uint256) {
        return totalSupply_;
    }

    function balanceOf(address tokenOwner) public override view returns (uint256 balance) {
        return balances[tokenOwner];
    }

    function transfer(address receiver, uint256 numTokens) public override onlyOwner returns (bool) {
        require(numTokens <= balances[msg.sender], "Insufficient balance.");
        balances[msg.sender] = balances[msg.sender] - numTokens;
        balances[receiver] = balances[receiver] + numTokens;
        emit Transfer(msg.sender, receiver, numTokens);
        return true;
    }

    function approve(address delegate, uint256 numTokens) public override returns (bool) {
        allowed[msg.sender][delegate] = numTokens;
        emit Approval(msg.sender, delegate, numTokens);
        return true;
    }

    function allowance(address tokenOwner, address delegate) public override view returns (uint) {
        return allowed[tokenOwner][delegate];
    }

    function transferFrom(address from, address buyer, uint256 numTokens) public override returns (bool) {
        require(numTokens <= balances[from], "Insufficient balance.");
        require(numTokens <= allowed[from][msg.sender], "Insufficient allowance.");

        balances[from] = balances[from] - numTokens;
        allowed[from][msg.sender] = allowed[from][msg.sender] - numTokens;
        balances[buyer] = balances[buyer] + numTokens;
        emit Transfer(from, buyer, numTokens);
        return true;
    }
}
