pragma solidity ^0.8.17;

import "@openzeppelin/contracts/proxy/Proxy.sol";
import "../interfaces/IMOSTProxyImplementationStorage.sol";

contract MOSTProxy is Proxy {
    // keccak256('MOSTProxy') - 1
    bytes32 public constant STORAGE_SLOT = 0x66cc09eacb25d54bfdb1c08e48467dd9bd1e25e42022136c8c00eab4702e7a45;

    constructor(IMOSTProxyImplementationStorage _storage) {
        assembly {
            sstore(STORAGE_SLOT, _storage)
        }
    }

    function _getStorage() internal view returns (IMOSTProxyImplementationStorage _storage) {
        assembly {
            _storage := sload(STORAGE_SLOT)
        }
    }

    function _implementation() internal view override returns (address) {
        return _getStorage().implementation();
    }
}
