pragma solidity ^0.8.0;



contract ScroogeToken is IERC20, Ownable {
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    string private _name;
    string private _symbol;

    uint256 private reflectionRate = 5; // 1% reflection
    uint256 private burnRate = 1; // 1% burn
    uint256 private marketingRate = 3; // 1% marketing
    address private marketingWallet;

    address[] private holders; // List of token holders
    mapping(address => bool) private isHolder; // Mapping to check if an address is a holder
    mapping(address => bool) private isExcludedFromFees; // New mapping to track excluded holders

    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply,
        address _marketingWallet
    ) {
        _name = name;
        _symbol = symbol;

        // Ensure that the initial supply is greater than zero
        require(initialSupply > 0, "Initial supply must be greater than zero");

        // Ensure that the marketing wallet address is not the zero address
        require(
            _marketingWallet != address(0),
            "Marketing wallet cannot be the zero address"
        );

        // Mint the initial supply and set the marketing wallet
        _mint(msg.sender, initialSupply * 10 ** 18); // Initial supply, 1,000,000 tokens
        marketingWallet = _marketingWallet;
        holders.push(msg.sender); // Add the deployer as the first holder
        isHolder[msg.sender] = true;
    }

    function name() public view virtual returns (string memory) {
        return _name;
    }

    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    function decimals() public pure virtual returns (uint8) {
        return 18;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender] - amount);
        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public returns (bool) {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender] + addedValue
        );
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public returns (bool) {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender] - subtractedValue
        );
        return true;
    }

    // Function to exclude a holder from transfer fees (only owner can call this)
    function excludeTransferFee(address holder) external onlyOwner {
        require(holder != address(0), "Cannot exclude the zero address");
        require(!isExcludedFromFees[holder], "Holder is already excluded");
        isExcludedFromFees[holder] = true;
    }

    // Function to include a holder in transfer fees (only owner can call this)
    function includeTransferFee(address holder) external onlyOwner {
        require(holder != address(0), "Cannot include the zero address");
        require(isExcludedFromFees[holder], "Holder is not excluded");
        isExcludedFromFees[holder] = false;
    }

    // Function to check if a holder is excluded from transfer fees
    function isExcludedFromFee(address holder) public view returns (bool) {
        return isExcludedFromFees[holder];
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        // Ensure that the transfer amount is greater than zero
        require(amount > 0, "Transfer amount must be greater than zero");

        // Ensure that the recipient is not the zero address
        require(
            recipient != address(0),
            "Transfer to the zero address is not allowed"
        );

        uint256 fromBalance = _balances[sender];
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );

        // Check if the sender is excluded from transfer fees
        if (!isExcludedFromFees[sender]) {
            // Calculate tax amounts for burn, reflection, and marketing
            uint256 burnAmount = (amount * burnRate) / 100; // 1% burn
            uint256 reflectionAmount = (amount * reflectionRate) / 100; // 1% reflection
            uint256 marketingAmount = (amount * marketingRate) / 100; // 1% marketing
            uint256 taxAmount = burnAmount + reflectionAmount + marketingAmount;

            // Calculate the net amount to be transferred after tax
            uint256 netAmount = amount - taxAmount;

            // Transfer the net amount to the recipient
            _balances[sender] -= amount;
            _balances[recipient] += netAmount;
            _balances[address(this)] += taxAmount;

            // Add new holder if the recipient is not a holder
            addHolder(recipient);

            // Burn the specified amount of tokens
            _burn(address(this), burnAmount); // Burn the specified amount of tokens

            // Distribute the reflection amount to all holders (except the sender)
            distributeReflection(sender, reflectionAmount);

            // Transfer the marketing share to the marketing wallet
            _balances[address(this)] -= marketingAmount;
            _balances[marketingWallet] += marketingAmount;

            emit Transfer(sender, recipient, netAmount);
            emit Transfer(sender, address(this), taxAmount);
            emit Transfer(address(this), marketingWallet, marketingAmount);
        } else {
            // If sender is excluded, transfer the full amount without fees
            _balances[sender] -= amount;
            _balances[recipient] += amount;

            // Add new holder if the recipient is not a holder
            addHolder(recipient);

            emit Transfer(sender, recipient, amount);
        }
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: mint to the zero address");

        _totalSupply += amount;
        unchecked {
            // Overflow not possible: balance + amount is at most totalSupply + amount, which is checked above.
            _balances[account] += amount;
        }
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: burn from the zero address");

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
            // Overflow not possible: amount <= accountBalance <= totalSupply.
            _totalSupply -= amount;
        }

        emit Transfer(account, address(0), amount);
    }

    function setreflectionRate(uint256 tax) public onlyOwner {
        require(tax <= 100, "Tax percentage must be 100 or less");
        reflectionRate = tax;
    }

    function getreflectionRate() public view onlyOwner returns (uint256) {
        return reflectionRate;
    }

    function setBurnRate(uint256 burn) public onlyOwner {
        require(burn <= 100, "Burn percentage must be 100 or less");
        burnRate = burn;
    }

    function getBurnRate() public view onlyOwner returns (uint256) {
        return burnRate;
    }

    function setMarketingRate(uint256 fee) public onlyOwner {
        require(fee <= 100, "Marketing fee must be 100 or less");
        marketingRate = fee;
    }

    function getMarketingRate() public view onlyOwner returns (uint256) {
        return marketingRate;
    }

    // Function to set the marketing wallet address (only owner can set it)
    function setMarketingWallet(address _wallet) external onlyOwner {
        // Ensure that the new marketing wallet address is not the zero address
        require(
            _wallet != address(0),
            "Marketing wallet cannot be the zero address"
        );
        marketingWallet = _wallet;
    }

    // Function to distribute the reflection amount to all holders
    function distributeReflection(address sender, uint256 amount) internal {
        // Distribute the reflection amount to all holders (except the sender)
        for (uint256 i = 0; i < holders.length; i++) {
            address holder = holders[i];
            if (holder != sender) {
                uint256 holderShare = (_balances[holder] * amount) /
                    _totalSupply;
                _balances[address(this)] -= holderShare;
                _balances[holder] += holderShare;
                emit Transfer(address(this), holder, holderShare);
            }
        }
    }

    // Function to add a new holder
    function addHolder(address holder) public {
        if (!isHolder[holder]) {
            holders.push(holder);
            isHolder[holder] = true;
        }
    }
}
