// File: sDAO/SpacemanNFT.sol



pragma solidity 0.8.4;












interface IWETH {
    function deposit() external payable;

    function withdraw(uint wad) external;
}

contract Spaceman is CfoNftTakeable,Adminable,ERC721Enumerable,ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    mapping(address => bool) private _minters;

    mapping(uint256 => address) public rewardTokens;
    mapping(address => uint256) public rewardTokenIndexOf;
    uint256 public rewardTokensLength;

    // mainnet
    address public constant wbnb = address(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);
    address public constant eth = address(0x2170Ed0880ac9A755fd29B2688956BD959F933F8);
    address public constant usdt = address(0x55d398326f99059fF775485246999027B3197955);
    address public constant stone = address(0xF577Be9f58930570e4992672B148eF1E2DE14BAb);
    address public constant busd = address(0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56);

    mapping(address => uint256) public rewardPerTokenStoredOf;
    mapping(address => mapping(uint256 => uint256)) public rewardPaidOf;

    mapping(address => uint256) public tokenBalanceOf;

    mapping(uint256 => string) public speacilImages;
    
    string public imageBaseUri;

    address public tokenDescriptor;

    uint256 public nextTokenId = 1;

    string public constant DESCRIPTION = 'Spaceman is one of the identity of StoneDAO, The NFT holders can stake and earn dividend with StoneDAO.';

    modifier onlyMinter {
        require(_minters[msg.sender],"ERC721Mintable: forbidden");
        _;
    }

    event RewardAdded(address caller,address rewardsToken,uint reward);
    event RewardPaid(address rewardsToken,address caller,uint reward);

    constructor() ERC721('Spaceman', 'Spaceman') {
        
        _minters[msg.sender] = true;
        imageBaseUri = "https://dapp.stonedao.network/matestoneimages/spaceman.png";

        addRewardToken(wbnb);
        addRewardToken(eth);
        addRewardToken(usdt);
        addRewardToken(stone);
        addRewardToken(busd);
    }

    receive() external payable {        
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");
        if(tokenDescriptor!=address(0)){
            return INFTDescriptor(tokenDescriptor).tokenURI(address(this),tokenId);
        }

        string memory json = Base64.encode(abi.encodePacked('{"name": "',name(),' #', Strings.toString(tokenId), '", "description": "',DESCRIPTION,'", "image": "', imageBaseUri, '","attributes":',_propertiesToJson(tokenId),'}'));
        return string(abi.encodePacked('data:application/json;base64,', json));
    }

    function _propertiesToJson(uint tokenId) internal view returns(string memory) {

        bytes memory output = abi.encodePacked('[{"trait_type":"BNB","value":"',Strings.toString(earned(wbnb, tokenId)),'"}');
        output = abi.encodePacked(output,',{"trait_type":"ETH","value":"',Strings.toString(earned(eth, tokenId)),'"}');
        output = abi.encodePacked(output,',{"trait_type":"USDT","value":"',Strings.toString(earned(usdt, tokenId)),'"}');
        output = abi.encodePacked(output,',{"trait_type":"BUSD","value":"',Strings.toString(earned(busd, tokenId)),'"}');
        output = abi.encodePacked(output,',{"trait_type":"Stone","value":"',Strings.toString(earned(stone, tokenId)),'"}');
        output = abi.encodePacked(output,']');

        return string(output);
    }

    function earned(address rewardsToken, uint tokenId) public view returns(uint){
        return rewardPerTokenStoredOf[rewardsToken].sub(rewardPaidOf[rewardsToken][tokenId]) / 1e18;
    }

    function earnedBatch(address rewardsToken,uint[] memory tokenIds) public view returns(uint){
        uint reward = 0;
        for(uint i=0;i<tokenIds.length;i++){
            reward = reward.add(earned(rewardsToken,tokenIds[i]));
        }

        return reward;        
    }

    function earnedAll(address rewardsToken,address account) external view returns(uint){
        return earnedBatch(rewardsToken,userAllTokens(account));
    }

    function claim(address rewardsToken,uint tokenId) external nonReentrant {
        require(ownerOf(tokenId) == msg.sender,"caller must be owner of token");
        require(rewardsToken != address(0),"rewards token cannot be 0");

        uint reward = earned(rewardsToken, tokenId);
        if(reward > 0){
            _payRewards(tokenId,rewardsToken,reward);
            _syncTokenBalance(rewardsToken);
        }       
    }

    function _payRewards(uint tokenId,address rewardsToken,uint reward) internal {
        rewardPaidOf[rewardsToken][tokenId] = rewardPerTokenStoredOf[rewardsToken];  
        if(rewardsToken == wbnb){
            IWETH(wbnb).withdraw(reward);
            _safeTransferETH(msg.sender, reward);
        }else{
            IERC20(rewardsToken).safeTransfer(msg.sender,reward);
        }

        emit RewardPaid(rewardsToken,msg.sender,reward);        
    }

    function claimBatch(address rewardsToken,uint[] memory tokenIds) public nonReentrant {
        require(rewardsToken != address(0),"rewards token cannot be 0");
        require(tokenIds.length > 0,"token ids can not be empty");
        for(uint i=0;i<tokenIds.length;i++){
            require(ownerOf(tokenIds[i]) == msg.sender,"caller must be owner of token");
            uint reward = earned(rewardsToken, tokenIds[i]);
            if(reward > 0){
                _payRewards(tokenIds[i],rewardsToken,reward);
            }
        }

        _syncTokenBalance(rewardsToken);
    }

    function claimAll(address rewardsToken) external {
        uint[] memory allTokenIds = userAllTokens(msg.sender);
        uint rl = rewardTokensLength;
        for(uint i=0;i<rl;i++){
            claimBatch(rewardsToken,allTokenIds);
        }
    }

    function addReward(address rewardsToken) public nonReentrant {
        require(rewardsToken != address(0),"rewards token can not be 0");
        if(isRewardsToken(rewardsToken)){
            uint totalSupply_ = totalSupply();
            if(totalSupply_ > 0){
                uint currentBalance = IERC20(rewardsToken).balanceOf(address(this));
                uint reward = currentBalance.sub(tokenBalanceOf[rewardsToken]);
                rewardPerTokenStoredOf[rewardsToken] = rewardPerTokenStoredOf[rewardsToken].add(reward.mul(1e18) / totalSupply_);
                emit RewardAdded(msg.sender,rewardsToken,reward);
            }       

            _syncTokenBalance(rewardsToken);
        }
    }

    function _syncTokenBalance(address rewardsToken) internal {
        tokenBalanceOf[rewardsToken] = IERC20(rewardsToken).balanceOf(address(this));
    }

    function exists(uint256 tokenId) public view returns(bool) {
        return _exists(tokenId);
    }

    function userAllTokens(address account) public view returns(uint256[] memory) {
        uint balance_ = balanceOf(account);
        uint[] memory ids = new uint[](balance_);
        if(balance_ > 0){
            for(uint i = 0;i < balance_;i++){
                ids[i] = tokenOfOwnerByIndex(account, i);
            }
        }

        return ids;
    }

    function userAllRewards(address account) external view returns(address[] memory _rewardTokens,uint[] memory _rewards) {
        uint[] memory allTokenIds = userAllTokens(account);
        _rewardTokens = new address[](rewardTokensLength);
        _rewards = new uint[](_rewardTokens.length);
        for(uint i=0;i<_rewardTokens.length;i++){
            _rewardTokens[i] = rewardTokens[i];
            for(uint j=0;j<allTokenIds.length;j++){
                _rewards[i] = _rewards[i].add(earned(_rewardTokens[i],allTokenIds[j]));
            }
        }
    }

    function safeMint(address to) public onlyMinter returns(uint256){
        uint tokenId = nextTokenId++;
        _safeMint(to,tokenId);

        uint len = rewardTokensLength;
        for(uint i=0;i<len;i++){
            address token = rewardTokens[i];
            rewardPaidOf[token][tokenId] = rewardPerTokenStoredOf[token];
        }

        return tokenId;
    }

    function safeMintBatch(address to,uint mintCount) external onlyMinter {
        require(to != address(0),"to can not be address 0");
        uint nid = nextTokenId;
        for(uint i=0;i<mintCount;i++){
            _safeMint(to,nid);
            nid += 1;
        }

        nextTokenId = nid;
    }

    // function _burn(uint256 tokenId) override internal {
    //     // delete rewardPaidOf[tokenId];
    //     super._burn(tokenId);
    // }

    function burn(uint256 tokenId) external {
        require(msg.sender == ownerOf(tokenId),"burn: caller must be owner of token");
        _burn(tokenId);
    }

    function _safeTransferETH(address to, uint value) internal {
        (bool success,) = to.call{value:value}(new bytes(0));
        require(success, 'TransferHelper: ETH_TRANSFER_FAILED');
    }

    function addMinter(address minter) external virtual onlyOwner {
        require(minter != address(0),"ERC721Mintable: invalid minter");
        _minters[minter] = true;
    }

    function removeMinter(address minter) external virtual onlyOwner {
        require(minter != address(0),"ERC721Mintable: invalid minter");
        _minters[minter] = false;
    }

    function isMinter(address minter) public view returns(bool) {
        return _minters[minter];
    }

    function setSpeicalImage(uint tokenId,string memory uri) public onlyOwner {
        require(tokenId > 0,"invalid token id");
        speacilImages[tokenId] = uri;
    }

    function setImageBaseURI(string memory uri) external onlyOwner {
        imageBaseUri = uri;
    }

    function managerBurn(uint tokenId) external onlyOwner {
        _burn(tokenId);
    }

    function setDescriptor(address descriptor) external onlyOwner {
        tokenDescriptor = descriptor;
    }

    function isRewardsToken(address token) public view returns(bool) {
        return rewardTokenIndexOf[token] > 0;
    }

    function addRewardToken(address token) public onlyOwner {
        require(token != address(0),"token cannot be address 0");
        require(rewardTokenIndexOf[token] == 0,"token existed");
        rewardTokens[rewardTokensLength] = token;
        rewardTokenIndexOf[token] = rewardTokensLength + 1;
        rewardTokensLength = rewardTokensLength + 1;
    }

    function removeRewardToken(address token) external onlyOwner {
        require(token != address(0),"token cannot be address 0");
        uint index = rewardTokenIndexOf[token];
        require(index > 0,"token not existed");
        uint len = rewardTokensLength;
        if(index != len){
            address lastToken = rewardTokens[len - 1];
            rewardTokens[index -1] = lastToken;
            rewardTokenIndexOf[lastToken] = index;
        }

        delete rewardTokens[len -1];
        delete rewardTokenIndexOf[token];

        rewardTokensLength = len - 1;
    }
}
