     * By default (when calling {_approve}) the flag is set to true. On the other hand, approval changes made by
     * `_spendAllowance` during the `transferFrom` operation set the flag to false. This saves gas by not emitting any
     * `Approval` event during `transferFrom` operations.
     *
     * Anyone who wishes to continue emitting `Approval` events on the`transferFrom` operation can force the flag to
     * true using the following override:
     * ```
     * function _approve(address owner, address spender, uint256 value, bool) internal virtual override {
     *     super._approve(owner, spender, value, true);
     * }
     * ```
     *
     * Requirements are the same as {_approve}.
     */
    function _approve(
        address owner,
        address spender,
        uint256 value,
        bool emitEvent
    ) internal virtual {
        if (owner == address(0)) {
            revert ERC20InvalidApprover(address(0));
        }
        if (spender == address(0)) {
            revert ERC20InvalidSpender(address(0));
        }
        _allowances[owner][spender] = value;
        if (emitEvent) {
            emit Approval(owner, spender, value);
        }
    }

    /**
     * @dev Updates `owner` s allowance for `spender` based on spent `value`.
     *
     * Does not update the allowance value in case of infinite allowance.
     * Revert if not enough allowance is available.
     *
     * Does not emit an {Approval} event.
     */
    function _spendAllowance(
        address owner,
        address spender,
        uint256 value
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            if (currentAllowance < value) {
                revert ERC20InsufficientAllowance(
                    spender,
                    currentAllowance,
                    value
                );
            }
            unchecked {
                _approve(owner, spender, currentAllowance - value, false);
            }
        }
    }
}

// File: contratos/agro_prod.sol

pragma solidity ^0.8.0;

contract Agrobox is ERC20 {
    using SafeMath for uint256;
    IERC20 private _usdtContract;
    IERC20 private _agrContract;
    address private _owner;
    mapping(address => bool) private _withdrawalAddresses;
    mapping(address => mapping(uint256 => Staker)) private _userStaker;
    uint256 public _token = 100000000 * 10**uint256(18);
    uint256 public _melon = 700000 * 10**uint256(18);
    uint256 public _onion = 2000000 * 10**uint256(18);
    uint256 public _banana = 1100000 * 10**uint256(18);
    uint256 public _yucca = 500000 * 10**uint256(18);
    mapping(uint256 => Plan) public plans;

    struct Staker {
        uint256 idStaker;
        uint256 startTime;
        uint256 paymentStaker;
        uint256 status;
        Plan planStaker;
    }

    struct Plan {
        uint256 amount;
        uint256 payments;
        uint256 benefitPercentage;
        uint256 totalBenefitPercentage;
        uint256 stakingQuarterly;
        uint256 stakingCategory;
    }

    modifier onlyOwner() {
        require(msg.sender == _owner, "No permissions");
        _;
    }

    event buyAgr(address indexed user, uint256 amount);
    event stakingAgr(address indexed user, uint256 amount);
    event claimAgr(address indexed user, uint256 amount);

    constructor(address usdtContract) ERC20("Agrobox", "AGR") {
        _usdtContract = IERC20(usdtContract);
        _agrContract = IERC20(address(this));
        _owner = msg.sender;
        _mint(address(this), 100000000 * 10**uint256(18));
    }

    function userStaker(uint256 idStake)
        public
        view
        returns (Staker memory staker)
    {
        staker = _userStaker[msg.sender][idStake];
    }

    function manageContractBalance(
        uint256 category,
        uint256 amount,
        bool increase
    ) internal {
        if (category == 1) {
            _melon = increase ? _melon.add(amount) : _melon.sub(amount);
        } else if (category == 2) {
            _onion = increase ? _onion.add(amount) : _onion.sub(amount);
        } else if (category == 3) {
            _banana = increase ? _banana.add(amount) : _banana.sub(amount);
        } else if (category == 4) {
            _yucca = increase ? _yucca.add(amount) : _yucca.sub(amount);
        } else if (category == 5) {
            _token = increase ? _token.add(amount) : _token.sub(amount);
        }
    }

    function stake(uint256 idStake, uint256 planStake) external {
        Staker storage staker = _userStaker[msg.sender][idStake];
        require(staker.startTime == 0, "Already staked");
        Plan storage plan = plans[planStake];
        uint256 balancePlan = 0;
        if (plan.stakingCategory == 1) {
            balancePlan = _melon;
        } else if (plan.stakingCategory == 2) {
            balancePlan = _onion;
        } else if (plan.stakingCategory == 3) {
            balancePlan = _banana;
        } else if (plan.stakingCategory == 4) {
            balancePlan = _yucca;
        }
        require(
            balancePlan >= plan.amount,
            "the quota for this staking has already been sold"
        );
        require(
            plan.stakingQuarterly != 0,
            "The selected plan does not exist or is no longer available"
        );
        uint256 _balance = _agrContract.balanceOf(msg.sender);
        require(
            _balance >= plan.amount,
            "Insufficient balance. Please buy more ARG"
        );
        uint256 benefit = plan.amount.mul(plan.totalBenefitPercentage).div(100);
        require(
            _token >= benefit,
            "Insufficient contract balance. That amount cannot be purchased"
        );
        uint256 allowance = _agrContract.allowance(msg.sender, address(this));
        require(
            allowance >= plan.amount,
            "Insufficient allowance. Please approve the contract to spend ARG"
        );
        _userStaker[msg.sender][idStake] = Staker({
            idStaker: idStake,
            paymentStaker: plan.payments,
            startTime: block.timestamp,
            status: 1,
            planStaker: plan
        });
        _agrContract.transferFrom(msg.sender, address(this), plan.amount);
        manageContractBalance(plan.stakingCategory, plan.amount, false);
        manageContractBalance(5, benefit, false);
        emit stakingAgr(msg.sender, plan.amount);
    }

    function claimReward(uint256 idStake) external {
        Staker storage staker = _userStaker[msg.sender][idStake];
        require(staker.status <= 3 && staker.status != 0, "Not a staker");
        require(staker.status != 2, "Paid staking");
        require(
            block.timestamp >=
                staker.startTime + staker.planStaker.stakingQuarterly,
            "Staking duration not met"
        );
        uint256 benefitTransaction = 0;
        while (
            staker.paymentStaker > 0 &&
            block.timestamp >
            staker.startTime + staker.planStaker.stakingQuarterly
        ) {
            uint256 benefit = 0;
            if (staker.paymentStaker != 1) {
                benefit = staker
                    .planStaker
                    .amount
                    .mul(staker.planStaker.benefitPercentage)
                    .div(1000);
                staker.startTime += 90 days;
            } else {
                benefit =
                    staker
                        .planStaker
                        .amount
                        .mul(staker.planStaker.benefitPercentage)
                        .div(1000) +
                    staker.planStaker.amount;
                staker.status = 2;
            }
            _agrContract.transfer(msg.sender, benefit);
            benefitTransaction += benefit;
            staker.paymentStaker -= 1;
        }
        emit buyAgr(msg.sender, benefitTransaction);
    }

    function sellToken(
        uint256 amount,
        uint256[] memory percentage,
        address[] memory wallet,
        address withdrawalAddress
    ) external {
        require(
            _withdrawalAddresses[withdrawalAddress],
            "Unauthorized withdrawal address"
        );
        require(
            _token >= amount,
            "Insufficient contract balance. That amount cannot be purchased"
        );
        uint256 allowance = _usdtContract.allowance(msg.sender, address(this));
        require(
            allowance >= amount,
            "Insufficient allowance. Please approve the contract to spend USDT"
        );
        bool transferSuccess = _usdtContract.transferFrom(
            msg.sender,
            address(this),
            amount
        );
        require(transferSuccess, "USDT transfer failed");
        _token -= amount;
        bool success = _agrContract.transfer(msg.sender, amount);
        require(success, "BLR transfer failed");
        if (wallet.length > 0) {
            uint256 owe = 0;
            for (uint256 i = 0; i < wallet.length; i++) {
                uint256 _valorReferido = amount.mul(percentage[i]).div(1000);
                _usdtContract.transfer(wallet[i], _valorReferido);
                owe += percentage[i];
            }
            uint256 aux = 1000 - owe;
            uint256 oweTotal = amount.mul(aux).div(1000);
            _usdtContract.transfer(withdrawalAddress, oweTotal);
        } else {
            _usdtContract.transfer(withdrawalAddress, amount);
        }
        emit buyAgr(msg.sender, amount);
    }

    function addPlan(
        uint256 id,
        uint256 amount,
        uint256 payments,
        uint256 benefitPercentage,
        uint256 totalBenefitPercentage,
        uint256 category
    ) external onlyOwner {
        require(
            id != 0 &&
                amount != 0 &&
                benefitPercentage != 0 &&
                category != 0 &&
                category <= 4,
            "no plan data can be 0"
        );
        plans[id] = Plan({
            amount: amount * 10**uint256(18),
            payments: payments,
            benefitPercentage: benefitPercentage,
            totalBenefitPercentage: totalBenefitPercentage,
            stakingQuarterly: 270 days,
            stakingCategory: category
        });
    }

    function changeInvestmentExchange(
        uint256 category,
        uint256 amount,
        bool increase
    ) external onlyOwner {
        require(
            amount != 0 && category != 0 && category <= 4,
            "category not available"
        );
        manageContractBalance(category, amount, increase);
    }

    function changeOwner(address ownerNew) external onlyOwner {
        _owner = ownerNew;
    }

    function removePlan(uint256 id) external onlyOwner {
        delete plans[id];
    }

    function addWithdrawalAddress(address newAddress) external onlyOwner {
        _withdrawalAddresses[newAddress] = true;
    }

    function removeWithdrawalAddress(address addressToRemove)
        external
        onlyOwner
    {
        delete _withdrawalAddresses[addressToRemove];
    }

    function takeInternalBalance(address wallet, uint256 amount)
        external
        onlyOwner
    {
        uint256 tokensTake = amount * 10**uint256(18);
        manageContractBalance(5, tokensTake, false);
        _agrContract.transfer(wallet, tokensTake);
    }

    function mint(uint256 tokens) external onlyOwner {
        uint256 tokensMint = tokens * 10**uint256(18);
        manageContractBalance(5, tokensMint, true);
        _mint(address(this), tokensMint);
    }

    function burn(uint256 tokens) external onlyOwner {
        uint256 tokensBurn = tokens * 10**uint256(18);
        manageContractBalance(5, tokensBurn, false);
        _burn(address(this), tokensBurn);
    }
}
