abstract contract BlackList is Ownable {
    mapping (address => bool) private _blackList;
    event LogBlackListChanged(address indexed _user, bool _status);
    modifier onlyBlackList() {
        require(_blackList[_msgSender()], "Black list");_;
    }
    function isBlackListed(address _maker) public view returns (bool) {
        return _blackList[_maker];
    }
    function setBlackList (address _evilUser, bool _status) public virtual onlyOwner returns (bool){
        _blackList[_evilUser] = _status;
        emit LogBlackListChanged(_evilUser, _status);
        return _blackList[_evilUser];
    }
}

contract Token is ERC20,Ownable,BlackList {
    using SafeMath for uint256;

    uint256 public total = 50000000000 * 10 ** 18;

    uint256 public buyFeeRatio = 0;
    uint256 public sellFeeRatio = 30;

    address public destoryAddress = address(0x0000000000000000000000000000000000000001);

    address public uniswapV2Pair;
    IUniswapV2Router02 public uniswapV2Router;
    

    bool public isSwap;

    modifier filterSwap {
        isSwap = true;
        _;
        isSwap = false;
    }

    mapping (address => bool) public uniswapV2PairMapping;
    mapping (uint256 => uint256) public releaseMapping; // time => amount

    constructor(address routerAddress_) ERC20("GPT Token","GPTT"){
        super._mint(msg.sender, total);
        uniswapV2Router = IUniswapV2Router02(routerAddress_);
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this),  uniswapV2Router.WETH());
        uniswapV2PairMapping[uniswapV2Pair] = true;    
    }

    function setUniswapRouter(address routerAddress_) public onlyOwner() {
        uniswapV2Router = IUniswapV2Router02(routerAddress_);
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this),  uniswapV2Router.WETH());
        uniswapV2PairMapping[uniswapV2Pair] = true;
    }

    function setBuyFeeRatio(uint256 ratio)public onlyOwner(){
        buyFeeRatio = ratio;
    }
    function setSellFeeRatio(uint256 ratio)public onlyOwner(){
        sellFeeRatio = ratio;
    }

    function setUniswapV2PairMapping(address addr,bool flag)public onlyOwner {
        uniswapV2PairMapping[addr] = flag;
    }

    function _transfer(address sender,address recipient,uint256 amount) internal virtual override{
        _beforeTokenTransfer(sender, recipient, amount);
        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
        uint256 arrivalAmount = amount;
        if(IERC20(uniswapV2Pair).totalSupply() > 0 && !isSwap && (uniswapV2PairMapping[sender] || uniswapV2PairMapping[recipient])){
            arrivalAmount = arrivalAmount.sub(feeHandle(sender,recipient,amount));
        }
        unchecked {
            _balances[sender] = senderBalance - amount;
        }
        _balances[recipient] += arrivalAmount;
        emit Transfer(sender, recipient, arrivalAmount);
        _afterTokenTransfer(sender, recipient, amount);
    }

    
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal view  override  {
        require(!isBlackListed(from) && !isBlackListed(to),"Blacklist prohibited transactions");
        
        if(from == address(this) || to == address(this)) return ;
    }

    function feeHandle(address from,address to,uint256 amount)internal returns(uint256){
        uint256 fee;
        if(uniswapV2PairMapping[to]){
            fee = calculateRatio(amount, sellFeeRatio);
            if(fee <= 0) return 0;
            emit Transfer(from, destoryAddress, fee);
            _balances[destoryAddress] += fee;
        }else{
            fee = calculateRatio(amount, buyFeeRatio);
            if(fee <= 0) return 0;
            _balances[destoryAddress] += fee;
            emit Transfer(from, destoryAddress, fee);
        }
        return fee;
    }

    function calculateRatio(uint256 _amount,uint256 ratio) private pure returns (uint256) {
        return _amount.mul(ratio).div(
            10**4
        );
    }

}
