interface IERC721 {
    function safeTransferFrom(address from, address to, uint256 tokenId) external;
    function burn(uint256 tokenId) external;
    function ownerOf(uint256 tokenId) external view returns (address owner);
    function tokenData(uint256 tokenId) external view returns (address _owner, string memory _rnd, string memory _item, string memory _data, uint256 _level);   
    function tokenFullInfo(uint256 tokenId) external view returns (address owner, string memory tokenUri, string memory tokenRnd, string memory tokenItem, string memory tokenData, uint256 tokenSeason, uint256 tokenTime);    
}

contract AxesUpgrade is ReentrancyGuard, ERC721Holder, AccessControl, Pausable {

    bytes32 public constant INFO_ROLE = keccak256("INFO_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    uint256 public upgradeId;
    uint256 public requestId;
    address public immutable contract721;
    address public contractToken;
    address public beneficiary;
    string[] typesList;
    mapping(string => bool) typesExist;
    using Address for address;

    constructor(address _nft, address _token) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(INFO_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
        beneficiary = _msgSender();
        contract721 = _nft;
        contractToken = _token;
    }

    mapping (uint256 => UpgradedTokenInfo) token;
    struct UpgradedTokenInfo {
        bool[]    burned;
        uint256[] upgradeId;
        uint256[] upgradeAmount;
        address[] owner;
        string[]  tokenItem;
        string[]  tokenData;
        string[]  tokenRnd;
    }

    mapping (uint256 => UpgradeInfo) upgrades;
    struct UpgradeInfo {
        address   owner;
        uint256[] sourceIds;
        string    newIds;
        uint256   time;
        string    upgradeType;
        uint256   tokenAmount;
        string    upgradeMemo;
    }

    mapping (uint256 => RequestInfo) requests;
    struct RequestInfo {
        address   sender;
        string    requestType;
        string    requestMemo;
        uint256   time;
        uint256   amount;
        uint256   tokenAmount;
    }

    mapping (string => Type) types;
    struct Type {
        uint256 price;
        bool    active;
        bool    burn;
        uint256 tokenPrice;
    }

    event Upgrade(uint256 indexed _id, address indexed owner, uint256[] tokens);
    event ChangeSettings(address indexed _beneficiary, address indexed _tokenContract);
    event ChangeType(string indexed _type, uint256 indexed _price, bool _active, bool _burn, uint256 _tokenPrice);
    event Request(uint256 indexed _id, address indexed _sender, string indexed _type, uint256 _tokenPrice, uint256 _coins, string _memo);

    function request(string memory _type, uint256 _tokenPrice, string memory _memo) external whenNotPaused() payable returns (uint256 _requestId) {
        require(types[_type].active, "Wrong operation type");
        if (types[_type].price != 0) { require(types[_type].price == msg.value, "Price not right!"); }
        requestId++;
        (bool succ, ) = payable(beneficiary).call{value: msg.value}("");
        require(succ, "Transfer failed!");
        uint256 _token = 0;
        if (types[_type].tokenPrice != 0) {
            require(IERC20(contractToken).transferFrom(_msgSender(), beneficiary, types[_type].tokenPrice), "BEP20 tokens transfer not successful.");
            _token = types[_type].tokenPrice;
        }
        if (types[_type].tokenPrice == 0 && _tokenPrice != 0) {
            require(IERC20(contractToken).transferFrom(_msgSender(), beneficiary, _tokenPrice), "BEP20 tokens from function transfer not successful.");
            _token = _tokenPrice;
        }
        requests[requestId] = RequestInfo(_msgSender(), _type, _memo, block.timestamp, msg.value, _token);
        emit Request(requestId, _msgSender(), _type, _token, msg.value, _memo);
        return requestId;
    }

    function upgrade(uint256[] memory _ids, string memory _type, uint256 _tokenPrice, string memory _memo) external nonReentrant whenNotPaused() payable returns (uint256 _upgradeId) {
        require(_ids.length <= 10, "Decrease tokens amount. <=10");
        require(types[_type].active, "Wrong operation type");
        require(types[_type].price == msg.value, "Price not right!");
        upgradeId++;
        (bool succ, ) = payable(beneficiary).call{value: msg.value}("");
        require(succ, "Transfer failed!");
        if (types[_type].tokenPrice != 0) {
            require(IERC20(contractToken).transferFrom(_msgSender(), beneficiary, types[_type].tokenPrice), "BEP20 tokens transfer not successful.");
            upgrades[upgradeId].tokenAmount = types[_type].tokenPrice;
        }
        if (types[_type].tokenPrice == 0 && _tokenPrice != 0) {
            require(IERC20(contractToken).transferFrom(_msgSender(), beneficiary, _tokenPrice), "BEP20 tokens from function transfer not successful.");
            upgrades[upgradeId].tokenAmount = _tokenPrice;
        }
        upgrades[upgradeId].owner = _msgSender();
        upgrades[upgradeId].time = block.timestamp;
        upgrades[upgradeId].upgradeType = _type;
        upgrades[upgradeId].upgradeMemo = _memo;
        for (uint i; i < _ids.length; i++) {
            require(IERC721(contract721).ownerOf(_ids[i]) == msg.sender, "Not your token.");
            upgrades[upgradeId].sourceIds.push(_ids[i]);
            string memory _tokenItem;
            string memory _tokenData;
            string memory _tokenRnd;
            (,,_tokenRnd,_tokenItem,_tokenData,,) = IERC721(contract721).tokenFullInfo(_ids[i]);
            token[_ids[i]].burned.push(types[_type].burn);
            token[_ids[i]].upgradeId.push(upgradeId);
            token[_ids[i]].upgradeAmount.push(_ids.length);
            token[_ids[i]].owner.push(_msgSender());
            token[_ids[i]].tokenItem.push(_tokenItem);
            token[_ids[i]].tokenData.push(_tokenData);
            token[_ids[i]].tokenRnd.push(_tokenRnd);
            if (types[_type].burn) {
            IERC721(contract721).safeTransferFrom(_msgSender(), address(this), _ids[i]);
            IERC721(contract721).burn(_ids[i]); }
        }
        emit Upgrade(upgradeId, _msgSender(), upgrades[upgradeId].sourceIds);
        return upgradeId;
    }

    function setUpgradeNewIds(uint256 _id, string memory newIds) external returns (bool) {
        require(hasRole(INFO_ROLE, _msgSender()), "You must have info role to add new token ids to upgrade.");
        require(upgrades[_id].sourceIds.length > 0, "Nonexistent upgrade ID.");
        upgrades[_id].newIds = newIds;
        return true;
    }

    function changeSettings( address _beneficiary, address _contractToken) external returns (bool) {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "You must have admin role to change beneficiary address.");
        require(_beneficiary != address(0), "Wrong beneficiary address.");
        require(_contractToken.isContract(), "BEP20 address is not a contract.");
        beneficiary = _beneficiary;
        contractToken = _contractToken;
        emit ChangeSettings(_beneficiary, _contractToken);
        return true;
    }

    function changeType(string memory _type, uint256 _price, bool _active, bool _burn, uint256 _tokenPrice) external returns (bool) {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "You must have admin role to change upgrade types.");
        if (!typesExist[_type]) { typesExist[_type] = true; typesList.push(_type); }
        types[_type] = Type(_price, _active, _burn, _tokenPrice);
        emit ChangeType(_type, _price, _active, _burn, _tokenPrice);
        return true;
    }

    function typeInfo(string memory _type) external view returns(uint256 _price, bool _active, bool _burn, uint256 _tokenPrice) {
        return (types[_type].price, types[_type].active, types[_type].burn, types[_type].tokenPrice);
    }

    function allTypes() external view returns (string[] memory) {
        return typesList;
    }

    function upgradeIdInfo(uint256 _id) external view returns (address burnedOwner, uint256[] memory sourceIds, string memory newIds, uint256 time, string memory upgradeType, uint256 tokenAmount) {
        require(upgrades[_id].sourceIds.length > 0, "Nonexistent upgrade ID.");
        return (upgrades[_id].owner, upgrades[_id].sourceIds, upgrades[_id].newIds, upgrades[_id].time, upgrades[_id].upgradeType, upgrades[_id].tokenAmount);
    }

    function upgradeIdMemo(uint256 _id) external view returns (string memory memo) {
        require(upgrades[_id].sourceIds.length > 0, "Nonexistent upgrade ID.");
        return (upgrades[_id].upgradeMemo);
    }

    function tokenIdInfo1(uint256 _id, uint256 _index) external view returns (uint256 tokenUpgrades, bool burned, uint256 _upgradeId, uint256 _upgradeAmount, address _owner) {
        return (token[_id].burned.length, token[_id].burned[_index], token[_id].upgradeId[_index], token[_id].upgradeAmount[_index], token[_id].owner[_index]);
    }

    function tokenIdInfo2(uint256 _id, uint256 _index) external view returns (string memory _tokenItem, string memory _tokenData, string memory _tokenRnd) {
        return (token[_id].tokenItem[_index], token[_id].tokenData[_index], token[_id].tokenRnd[_index]);
    }

    function requestIdInfo(uint256 _id) external view returns (address sender, string memory requestType, string memory memo, uint256 , uint256 amount, uint256 tokenAmount) {
        return (requests[_id].sender, requests[_id].requestType, requests[_id].requestMemo,  requests[_id].time, requests[_id].amount, requests[_id].tokenAmount);
    }

    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }
}
