      "content": "// SPDX-License-Identifier: UNLICENSED

//////////////////////////////////////////////////////////////////
//                   Welcome to Alpha ADDAMS Club               //
//////////////////////////////////////////////////////////////////

pragma solidity >=0.8.10;

import "solmate/tokens/ERC721.sol";
import "openzeppelin-contracts/contracts/utils/Strings.sol";
import "openzeppelin-contracts/contracts/access/Ownable.sol";
import "openzeppelin-contracts/contracts/token/ERC20/IERC20.sol";
import "chainlink-brownie-contracts/contracts/src/v0.8/interfaces/AutomationCompatibleInterface.sol";

error MintPriceNotPaid();
error NonExistentTokenURI();
error WithdrawTransfer();

contract AlphaADDAMS is ERC721, Ownable {
    event PriceUpdated(uint256 indexed newPrice);
    event ReferralCreated(uint256 indexed refNum, address indexed refOwner);

    IERC20 public immutable i_WED;
    using Strings for uint256;

    mapping(uint256 => uint256) public mintTimestamp;
    mapping(uint256 => uint256) public burnStamp;
    mapping(uint256 => uint256[]) private burnBatch;
    mapping(uint256 => bool) public batchBurned;
    mapping(uint256 => address) private refList;
    mapping(address => address[]) private refBook;

    uint256 public currentTokenId;
    uint256 private oneMonthPrice;
    uint256 private foreverPrice;
    uint256 private priceUnit;
    address public treasury;
    uint256 public currentTokenIdOneMonth = 1000;
    uint256 public currentTokenIdForever;
    uint256 public constant MAX_SUPPLY = 2000;
    uint256 public burnCount;
    uint256 public lastBatch;
    uint256 public burnBatchCount;
    uint256 public refNum;
    address public priceFeed;

    constructor(
        string memory _name,
        string memory _symbol,
        address _WED,
        address _treasury,
        address _priceFeed
    ) ERC721(_name, _symbol) Ownable(msg.sender) {
        i_WED = IERC20(_WED);
        treasury = _treasury;
        priceFeed = _priceFeed;
    }

    function mintOneMonthTo(address recipient) public returns (uint256) {
        if (
            i_WED.allowance(msg.sender, address(this)) < getMintOneMonthPrice()
        ) {
            revert MintPriceNotPaid();
        }
        require(currentTokenIdOneMonth <= MAX_SUPPLY, "Out of supply");
        require(
            i_WED.transferFrom(msg.sender, treasury, (getMintUnitPrice() * 80))
        );
        require(
            i_WED.transferFrom(
                msg.sender,
                address(0x000000000000000000000000000000000000dEaD),
                (getMintUnitPrice() * 120)
            )
        );
        uint256 newTokenId = ++currentTokenIdOneMonth;
        ++currentTokenId;
        mintTimestamp[newTokenId] = block.timestamp;
        burnStamp[newTokenId] = block.timestamp + 2628000; //2628000 = 30.42 days
        determineBurnBatch(newTokenId, burnStamp[1001], burnStamp[newTokenId]);
        _safeMint(recipient, newTokenId);
        return newTokenId;
    }

    function mintForeverTo(
        address recipient,
        uint256 refNumber
    ) public returns (uint256) {
        if (refNumber == 0) {
            if (
                i_WED.allowance(msg.sender, address(this)) <
                getMintForeverPrice()
            ) {
                revert MintPriceNotPaid();
            }
            require(currentTokenIdForever <= MAX_SUPPLY, "Out of supply");
            require(
                i_WED.transferFrom(
                    msg.sender,
                    treasury,
                    (getMintUnitPrice() * 600)
                )
            );
            require(
                i_WED.transferFrom(
                    msg.sender,
                    address(0x000000000000000000000000000000000000dEaD),
                    (getMintUnitPrice() * 900)
                )
            );
            ++refNum;
            refList[refNum] = recipient;
            uint256 newTokenId = ++currentTokenIdForever;
            ++currentTokenId;
            mintTimestamp[newTokenId] = block.timestamp;
            _safeMint(recipient, newTokenId);

            emit ReferralCreated(refNum, recipient);

            return newTokenId;
        } else {
            if (
                i_WED.allowance(msg.sender, address(this)) <
                getMintForeverRefPrice()
            ) {
                revert MintPriceNotPaid();
            }
            require(currentTokenIdForever <= MAX_SUPPLY, "Out of supply");
            require(
                refList[refNumber] != msg.sender,
                "Cannot use your own referral link"
            );

            require(
                i_WED.transferFrom(
                    msg.sender,
                    treasury,
                    (getMintUnitPrice() * 150)
                )
            );
            require(
                i_WED.transferFrom(
                    msg.sender,
                    refList[refNumber],
                    (getMintUnitPrice() * 450)
                )
            );
            require(
                i_WED.transferFrom(
                    msg.sender,
                    address(0x000000000000000000000000000000000000dEaD),
                    (getMintUnitPrice() * 750)
                )
            );
            refBook[refList[refNumber]].push(msg.sender);
            ++refNum;
            refList[refNum] = msg.sender;
            uint256 newTokenId = ++currentTokenIdForever;
            ++currentTokenId;
            mintTimestamp[newTokenId] = block.timestamp;
            _safeMint(recipient, newTokenId);

            emit ReferralCreated(refNum, msg.sender);

            return newTokenId;
        }
    }

    function transferFrom(
        address from,
        address to,
        uint256 id
    ) public override {
        if (id >= 1000) {
            revert("One Month NFTs are soulbound");
        }

        require(from == _ownerOf[id], "WRONG_FROM");

        require(to != address(0), "INVALID_RECIPIENT");

        require(
            msg.sender == from ||
                isApprovedForAll[from][msg.sender] ||
                msg.sender == getApproved[id],
            "NOT_AUTHORIZED"
        );

        // Underflow of the sender's balance is impossible because we check for
        // ownership above and the recipient's balance can't realistically overflow.
        unchecked {
            _balanceOf[from]--;

            _balanceOf[to]++;
        }

        _ownerOf[id] = to;

        delete getApproved[id];

        emit Transfer(from, to, id);
    }

    function tokenURI(
        uint256 tokenId
    ) public view virtual override returns (string memory) {
        if (ownerOf(tokenId) == address(0)) {
            revert NonExistentTokenURI();
        }
        if (tokenId <= 1000) {
            return
                "ipfs://bafyreihklcmlchd2jkw7upbuduxz7uqxbc2pbgh2vzsazi27jzg24pnnxq/metadata.json"; //blue - forever
        } else {
            return
                "ipfs://bafyreifis5gjzbyjbssoyb2lxnmfc5bijqy7seofvhtyfgs56w7f7uw5ku/metadata.json"; //green - one month
        }
    }

    //Emergency recovery of ETH
    function withdrawPayments(address payable payee) external onlyOwner {
        uint256 balance = address(this).balance;
        (bool transferTx, ) = payee.call{value: balance}("");
        if (!transferTx) {
            revert WithdrawTransfer();
        }
    }

    function checkUpkeep(
        bytes memory /* checkData */
    ) public view returns (bool upkeepNeeded, bytes memory /* performData */) {
        if (burnBatch[burnCount].length > 0) {
            bool timePassed = burnStamp[burnBatch[burnCount][0]] <
                block.timestamp;
            upkeepNeeded = timePassed && !batchBurned[burnCount];
        }
    }

    /**
     * @dev Once `checkUpkeep` is returning `true`, this function is called
     * and it kicks off the burn.
     */
    function performUpkeep(bytes calldata /* performData */) external {
        (bool upkeepNeeded, ) = checkUpkeep("");
        require(upkeepNeeded, "Upkeep not needed");
        uint256 batch = burnCount;
        for (uint256 i = 0; i < burnBatch[batch].length; i++) {
            _burn(burnBatch[batch][i]);
        }
        ++burnCount;
        batchBurned[batch] = true;
    }

    function determineBurnBatch(
        uint256 _tokenId,
        uint256 _timestampInitialBurn,
        uint256 _timestampBurn
    ) internal {
        uint256 newBatch = (_timestampBurn - _timestampInitialBurn) / 900; //every 15 mins
        if (newBatch > lastBatch) {
            lastBatch = newBatch;
            ++burnBatchCount;
            burnBatch[burnBatchCount].push(_tokenId);
        } else {
            burnBatch[burnBatchCount].push(_tokenId);
        }
    }

    function _burn(uint256 id) internal override {
        address owner = _ownerOf[id];

        require(owner != address(0), "NOT_MINTED");

        // Ownership check above ensures no underflow.
        unchecked {
            _balanceOf[owner]--;

            _balanceOf[0x000000000000000000000000000000000000dEaD]++;
        }

        _ownerOf[id] = 0x000000000000000000000000000000000000dEaD;

        delete getApproved[id];

        emit Transfer(owner, 0x000000000000000000000000000000000000dEaD, id);
    }

    function manualSafeBurn() external onlyOwner {
        uint256 count = burnCount;
        for (uint256 n = count; n < burnBatchCount; n++) {
            for (uint256 i = 0; i < burnBatch[n].length; i++) {
                require(
                    burnStamp[burnBatch[n][0]] < block.timestamp,
                    "Not the time to burn yet"
                );
                require(!batchBurned[n], "Already burned");
                _burn(burnBatch[n][i]);
            }
            ++burnCount;
            batchBurned[n] = true;
        }
    }

    function setMintUnit(uint256 _unit) external {
        require(
            msg.sender == priceFeed || msg.sender == owner(),
            "Not allowed"
        );
        priceUnit = _unit; //WED/USDT
        emit PriceUpdated(priceUnit);
    }

    function setPriceFeed(address _priceFeed) external onlyOwner {
        priceFeed = _priceFeed;
    }

    function setTreasury(address _treasury) external onlyOwner {
        treasury = _treasury;
    }

    function totalSupply() public view returns (uint256) {
        return currentTokenId;
    }

    function getDaysUntilBurn(uint256 _tokenId) public view returns (uint256) {
        return (burnStamp[_tokenId] - block.timestamp) / 86390;
    }

    function getSecondsUntilBurn(
        uint256 _tokenId
    ) public view returns (uint256) {
        return (burnStamp[_tokenId] - block.timestamp);
    }

    function getMintOneMonthPrice() public view returns (uint256) {
        return priceUnit * 200;
    }

    function getMintForeverPrice() public view returns (uint256) {
        return priceUnit * 1500;
    }

    function getMintForeverRefPrice() public view returns (uint256) {
        return priceUnit * 1350;
    }

    function getMintUnitPrice() public view returns (uint256) {
        return priceUnit;
    }

    function getBurnBatch(
        uint256 _batch
    ) public view returns (uint256[] memory) {
        return burnBatch[_batch];
    }

    function getReferralList() public view returns (address[] memory) {
        return refBook[msg.sender];
    }

    function getTreasury() public view returns (address) {
        return treasury;
    }
}
