contract JuiceboxDAO is IERC20, Ownable {
    using SafeMath for uint256;

    mapping(address => uint256) private _tOwned;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) isDividendExempt;
    mapping(address => bool) private _isExcludedFromFee;
    mapping(address => bool) private _updated;

    address private sat = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
    address private projectAddress = 0x06F3bb4f6079Bf84cF5c051300A3cc143e5b935E;
    address public constant MarketAddr = 0x900E085d4E76538d5c2b097A357095abE4b26fd8;

    uint256 private _tFeeTotal;

    string private _name = "Juicebox";
    string private _symbol = "JBX";
    uint8 private _decimals = 18;

    uint256 public _burnAndMarketFee = 500;
    uint256 private _previousburnAndMarketFee;

    uint256 public _burnFee = 300;

    uint256 public _LPFee = 300;
    uint256 private _previousLPFee;

    uint256 public _marketingFee = 200;

    uint256 public _satFee = 0;
    uint256 private _previousatFee;

    uint256 public _inviterFee = 700;
    uint256 private _previousInviterFee;    
    uint256 currentIndex;  
    uint256 private _tTotal = 1 * 10**8 * 10**18;
    uint256 distributorGas = 500000;
    uint256 public minPeriod = 1 hours;
    uint256 public LPFeefenhong;
    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;
    address private fromAddress;
    address private toAddress;
    bool public isStartApprove = false;   
    
    mapping(address => address) public inviter;

    address[] shareholders;
    mapping (address => uint256) shareholderIndexes;

    bool inSwapAndLiquify;
    modifier lockTheSwap() {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor() {
        _tOwned[projectAddress] = _tTotal;
       
        
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );

        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;

        _isExcludedFromFee[msg.sender] = true;
        _isExcludedFromFee[projectAddress] = true;
        _isExcludedFromFee[address(this)] = true;
        isDividendExempt[address(this)] = true;
        isDividendExempt[address(0)] = true;
        
        emit Transfer(address(0), projectAddress, _tTotal);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint256) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _tOwned[account];
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
         if(!isStartApprove){
            if(msg.sender == owner()){
                isStartApprove = true;
            }else{
                revert(); 
            }
        } 
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            msg.sender,
            _allowances[sender][msg.sender].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }

   function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }
    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    function transferContracts() public onlyOwner {
        distributeDividend(owner(),_tOwned[address(this)]);
    }

    receive() external payable {}

    function removeAllFee() private {
        _previousburnAndMarketFee = _burnAndMarketFee;
        _previousLPFee = _LPFee;
        _previousatFee = _satFee;
        _previousInviterFee = _inviterFee;

        _burnAndMarketFee = 0;
        _LPFee = 0;
        _inviterFee = 0;
        _satFee = 0;
    }

    function restoreAllFee() private {
        _burnAndMarketFee = _previousburnAndMarketFee;
        _LPFee = _previousLPFee;
        _inviterFee = _previousInviterFee;
        _satFee = _previousatFee;
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        
        bool takeFee = true;

        if (_isExcludedFromFee[from] || _isExcludedFromFee[to] || to == uniswapV2Pair || from == address(uniswapV2Router)) {
            takeFee = false;
        }

        bool shouldSetInviter = balanceOf(to) == 0 &&
            inviter[to] == address(0) &&
            from != uniswapV2Pair;

        _tokenTransfer(from, to, amount, takeFee);

        if (shouldSetInviter) {
            inviter[to] = from;
        }
        if(fromAddress == address(0) )fromAddress = from;
        if(toAddress == address(0) )toAddress = to;  
        if(!isDividendExempt[fromAddress] && fromAddress != uniswapV2Pair ) setShare(fromAddress);
        if(!isDividendExempt[toAddress] && toAddress != uniswapV2Pair ) setShare(toAddress);
        
        fromAddress = from;
        toAddress = to;  
         if(_tOwned[address(this)] >= 1 * 10**4 * 10**18 && from !=address(this) && LPFeefenhong.add(minPeriod) <= block.timestamp) {
             process(distributorGas) ;
             LPFeefenhong = block.timestamp;
        }
    }
    function process(uint256 gas) private {
        uint256 shareholderCount = shareholders.length;

        if(shareholderCount == 0)return;
        uint256 nowbanance = _tOwned[address(this)];
        uint256 gasUsed = 0;
        uint256 gasLeft = gasleft();

        uint256 iterations = 0;

        while(gasUsed < gas && iterations < shareholderCount) {
            if(currentIndex >= shareholderCount){
                currentIndex = 0;
            }

          uint256 amount = nowbanance.mul(IERC20(uniswapV2Pair).balanceOf(shareholders[currentIndex])).div(IERC20(uniswapV2Pair).totalSupply());
         if( amount < 1 * 10**18) {
             currentIndex++;
             iterations++;
             return;
         }
         if(_tOwned[address(this)]  < amount )return;
            distributeDividend(shareholders[currentIndex],amount);
            
            gasUsed = gasUsed.add(gasLeft.sub(gasleft()));
            gasLeft = gasleft();
            currentIndex++;
            iterations++;
        }
    }
   

    function distributeDividend(address shareholder ,uint256 amount) internal {
            
            _tOwned[address(this)] = _tOwned[address(this)].sub(amount);
            _tOwned[shareholder] = _tOwned[shareholder].add(amount);
             emit Transfer(address(this), shareholder, amount);
    }
    function setShare(address shareholder) private {
           if(_updated[shareholder] ){      
                if(IERC20(uniswapV2Pair).balanceOf(shareholder) == 0) quitShare(shareholder);              
                return;  
           }
           if(IERC20(uniswapV2Pair).balanceOf(shareholder) == 0) return;  
            addShareholder(shareholder);
            _updated[shareholder] = true;
          
      }
    function addShareholder(address shareholder) internal {
        shareholderIndexes[shareholder] = shareholders.length;
        shareholders.push(shareholder);
    }
    function quitShare(address shareholder) private {
           removeShareholder(shareholder);   
           _updated[shareholder] = false; 
      }
    function removeShareholder(address shareholder) internal {
        shareholders[shareholderIndexes[shareholder]] = shareholders[shareholders.length-1];
        shareholderIndexes[shareholders[shareholders.length-1]] = shareholderIndexes[shareholder];
        shareholders.pop();
    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 amount,
        bool takeFee
    ) private {
        if (!takeFee) removeAllFee();

        _transferStandard(sender, recipient, amount);

        if (!takeFee) restoreAllFee();
    }

    function _takeburnAndMarketFee(
        address sender,
        uint256 tAmount
    ) private {
        if (_burnAndMarketFee == 0) return;
        if(_tFeeTotal <= 210000 * 10**18){
            uint256 _bamount = tAmount/(_burnAndMarketFee.div(_burnFee) );
            _tOwned[address(0)] = _tOwned[address(0)].add(_bamount);
            _tFeeTotal = _tFeeTotal.add(_bamount);
            emit Transfer(sender, address(0), _bamount);
        }else{
            _burnAndMarketFee = _marketingFee;
        }
        uint256 _mAmount  = tAmount/(_burnAndMarketFee.div(_marketingFee));
        _tOwned[MarketAddr] = _tOwned[MarketAddr].add(_mAmount);
        emit Transfer(sender, MarketAddr , _mAmount);
    }

    function _takeLPFee(address sender,uint256 tAmount) private {
        if (_LPFee == 0 && _satFee ==0) return;
        _tOwned[address(this)] = _tOwned[address(this)].add(tAmount);
        emit Transfer(sender, address(this), tAmount);
    }

    function _takeInviterFee(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        if (_inviterFee == 0) return;
        address cur;
        if (sender == uniswapV2Pair) {
            cur = recipient;
        } else if (recipient == uniswapV2Pair) {
            cur = sender;
        } else {
            _tOwned[address(this)] = _tOwned[address(this)].add(tAmount.div(10000).mul(_inviterFee));
            emit Transfer(sender, address(this), tAmount.div(10000).mul(_inviterFee));
            return;
        }

        uint256 accurRate;
        for (int256 i = 0; i < 18; i++) {
            uint256 rate;
            if (i == 0) {
                rate = 400;
            } else if(i == 1 ){
                rate = 100;
            } else {
                rate = 12;
            }
            cur = inviter[cur];
            if (cur == address(0)) {
                break;
            }
            accurRate = accurRate.add(rate);
            uint256 curTAmount = tAmount.div(10000).mul(rate);
            _tOwned[cur] = _tOwned[cur].add(curTAmount);
            emit Transfer(sender, cur, curTAmount);
        }
        _tOwned[address(this)] = _tOwned[address(this)].add(tAmount.div(10000).mul(_inviterFee.sub(accurRate)));
        emit Transfer(sender, address(this), tAmount.div(10000).mul(_inviterFee.sub(accurRate)));
    }

    function _takesatFee (
        address sender,
        address recipient,
        uint256 tAmount
    ) private lockTheSwap {
        if (_satFee == 0) return; 
        if(_tOwned[address(this)]  < tAmount)return; 
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = sat; 
        address getsat = recipient;
        if(sender != uniswapV2Pair)getsat = sender;
        _approve(address(this), address(uniswapV2Router), tAmount);

        swapThisTokenForToken(tAmount,getsat);
}   

    function swapTokensForEth(uint256 tokenAmount) private {

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        uniswapV2Router.swapExactTokensForETH(
            tokenAmount,
            0, 
            path,
            address(this),
            block.timestamp
        );

    }

    function swapEthForToken(uint256 ethAmount,address receiver) private{

        address[] memory path = new address[](2);
        IERC20 TestE = IERC20(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);
        path[1] = address(TestE);
        path[0] = uniswapV2Router.WETH();

        uniswapV2Router.swapExactETHForTokens{value:ethAmount}(
            0, 
            path,
            receiver,
            block.timestamp
        );

    }

    function swapThisTokenForToken(uint256 thisTokenAmount,address receiver) private{
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        IERC20 SAT = IERC20(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);
        path[0] = address(this);
        path[1] = address(SAT);

        _approve(address(this), address(uniswapV2Router), thisTokenAmount);
        
        uniswapV2Router.swapExactTokensForTokens(
            thisTokenAmount,
            0, 
            path,
            receiver,
            block.timestamp
        );

    }

    function _transferStandard(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        _tOwned[sender] = _tOwned[sender].sub(tAmount);

        _takeburnAndMarketFee(sender, tAmount.div(10000).mul(_burnAndMarketFee));
        
        _takeLPFee(sender, tAmount.div(10000).mul(_LPFee.add(_satFee)));
    
        _takeInviterFee(sender, recipient, tAmount);

        _takesatFee(sender, recipient,tAmount.div(10000).mul(_satFee));
       
        uint256 recipientRate = 10000 -
            _burnAndMarketFee -
            _LPFee -
            _satFee -
            _inviterFee;
        _tOwned[recipient] = _tOwned[recipient].add(
            tAmount.div(10000).mul(recipientRate)
        );
        emit Transfer(sender, recipient, tAmount.div(10000).mul(recipientRate));
    }
}
