contract StakingB is Ownable {
  using SafeERC20 for IERC20;

  // Info of each user.
  struct UserInfo {
    uint256 start;
    uint256 amount; // How many LP tokens the user has provided.
    uint256 rewardDebt; // This number always increases to counterwaight always increasing "accTokenPerShare"
    bool isStaking;
  }

  // Info of each pool.
  struct PoolInfo {
    IERC20 token; // Address of LP token contract.
    uint256 rate;
    uint256 rateDenominator;
    uint256 lastRewardBlock; // Last block number that CAKEs distribution occurs.
    uint256 accTokenPerShare; // Accumulated CAKEs per share, times 1e12. See below.
    uint256 totalStaked;
  }

  PoolInfo public pool;
  address feeAddress;
  uint256 constant feeRate = 40;
  mapping(address => UserInfo) public userInfo;
  uint256 public amountOfUsers = 0;

  event Stake(address indexed user, uint256 amount);
  event Unstake(address indexed user, uint256 amount);
  event ForceUnstake(address indexed user, uint256 amount);

  constructor(
    IERC20 _token,
    uint256 rate,
    uint256 rateDenominator,
    address _feeAddress
  ) {
    pool.token = _token;
    pool.accTokenPerShare = 0;
    set(rate, rateDenominator);
    feeAddress = _feeAddress;
    pool.lastRewardBlock = block.number;
  }

  // USER ACTIONS

  /**
   * @notice Stake token to a certain staking pool
   */
  function stake(uint256 amount) external {
    UserInfo storage user = userInfo[msg.sender];
    require(user.amount == 0, "already staking");
    require(amount > 0, "Nothing to stake");
    updatePool();
    pool.token.safeTransferFrom(msg.sender, address(this), amount);
    pool.totalStaked += amount; // 2)

    userInfo[msg.sender] = UserInfo({
      start: block.timestamp,
      amount: amount,
      rewardDebt: (amount * pool.accTokenPerShare) / 1e12 / 1e32,
      isStaking: true
    });
    amountOfUsers++;
    emit Stake(msg.sender, amount);
  }

  /**
   * @notice Unstake with reward
   */
  function unstake() external {
    UserInfo storage user = userInfo[msg.sender];
    require(user.isStaking);
    updatePool();

    uint256 pending = ((user.amount * pool.accTokenPerShare) / 1e12 / 1e32) -
      user.rewardDebt;
    uint256 available = pool.token.balanceOf(address(this)) - pool.totalStaked;
    require(available >= pending, "reserves empty");
    uint256 total = pending + user.amount;
    uint256 fee = (total * feeRate) / 1000;
    total -= fee;

    pool.token.transfer(feeAddress, fee);
    pool.token.safeTransfer(msg.sender, total);
    pool.totalStaked -= user.amount; // 1)

    delete userInfo[msg.sender];
    amountOfUsers--;
    emit Unstake(msg.sender, total);
  }

  /**
   * @notice Unstake when 1 month passed but without reward and without fee
   * If user is getting "reserves ampty" error, user can still wait for admins to supply the reward to contract.
   * Or not wait and use this function
   */
  function forceUnstake() external {
    UserInfo storage user = userInfo[msg.sender];
    updatePool();

    uint256 pending = ((user.amount * pool.accTokenPerShare) / 1e12 / 1e32) -
      user.rewardDebt;
    uint256 available = pool.token.balanceOf(address(this)) - pool.totalStaked;
    require(available < pending, "use normal unstake");
    pool.token.safeTransfer(msg.sender, user.amount);
    pool.totalStaked -= user.amount; // 1)

    emit ForceUnstake(msg.sender, user.amount);
    amountOfUsers--;
    delete userInfo[msg.sender];
  }

  // PUBLIC ACTIONS

  // Update reward variables of the given pool to be up-to-date.
  function updatePool() public {
    if (block.number <= pool.lastRewardBlock) return;
    if (pool.totalStaked == 0) {
      pool.lastRewardBlock = block.number;
      return;
    }
    pool.accTokenPerShare +=
      (((block.number - pool.lastRewardBlock) *
        pool.rate *
        1e12 *
        1e18 *
        1e32) / pool.rateDenominator) /
      pool.totalStaked;
    pool.lastRewardBlock = block.number;
  }

  function increaseRewardPool(uint256 amount) external {
    pool.token.safeTransferFrom(msg.sender, address(this), amount);
  }

  // SETTERS

  // Update the given pool's rates
  function set(uint256 rate, uint256 rateDenominator) public onlyOwner {
    updatePool();
    require(rate < 1e6, "Overflow prevention");
    pool.rate = rate;
    pool.rateDenominator = rateDenominator;
  }

  function setNewFeeAddress(address newFeeAddress) external onlyOwner {
    require(newFeeAddress != address(0), "Zero address");
    feeAddress = newFeeAddress;
  }

  // GETTERS

  function pendingReward(address who) external view returns (uint256) {
    UserInfo storage user = userInfo[who];
    uint256 accTokenPerShare = pool.accTokenPerShare;
    if (block.number > pool.lastRewardBlock && pool.totalStaked != 0) {
      accTokenPerShare +=
        ((block.number - pool.lastRewardBlock) * pool.rate * 1e12) /
        pool.rateDenominator /
        pool.totalStaked;
    }
    return ((user.amount * accTokenPerShare) / 1e12 / 1e32) - user.rewardDebt;
  }
}
